INSERT INTO `books` (`id`, `name`, `giaban`, `soluong`, `category_id`, `tacgia`, `nxb`, `gioithieu`, `hinhanh`, `taiban`, `created_at`, `updated_at`) VALUES
(1, 'Bến Xe (Tái Bản 2020)', '61000.00', 40, 2, 'Thương Thái Vi', 'NXB Văn Học', '<p><br />\r\nBến Xe (T&aacute;i Bản 2020)</p>\r\n\r\n<p>Bến Xe</p>\r\n\r\n<p>Thứ t&ocirc;i c&oacute; thể cho em trong cuộc đời n&agrave;y</p>\r\n\r\n<p>chỉ l&agrave; danh dự trong sạch</p>\r\n\r\n<p>v&agrave; một tương lai tươi đẹp m&agrave; th&ocirc;i.</p>\r\n\r\n<p>Thế nhưng, nếu ch&uacute;ng ta c&oacute; kiếp sau,</p>\r\n\r\n<p>nếu kiếp sau t&ocirc;i c&oacute; đ&ocirc;i mắt s&aacute;ng,</p>\r\n\r\n<p>t&ocirc;i sẽ ở bến xe n&agrave;y&hellip; đợi em.</p>', '1654352787.jpg', 19, NULL, '2022-06-07 06:08:20'),
(2, 'Thiên Quan Tứ Phúc - Tập 4', '138000.00', 45, 2, 'Mặc Hương Đồng Khứu', 'NXB Hà Nội', '<p>Tạ Li&ecirc;n v&agrave; Sư Thanh Huyền tiếp tục truy t&igrave;m ch&acirc;n tướng của Bạch Thoại Ch&acirc;n Ti&ecirc;n, nhưng n&agrave;o ngờ lại bị cuốn về nơi biển khơi dậy s&oacute;ng, quỷ thần đối đầu, người ph&agrave;m cũng bị k&eacute;o v&agrave;o v&ograve;ng v&acirc;y. Cuối c&ugrave;ng sự thật h&eacute; lộ nơi Hắc Thủy Quỷ Vực, tội &aacute;c năm xưa bị phơi b&agrave;y.</p>\r\n\r\n<p>Kẻ vong mạng, người mất t&iacute;ch. C&ograve;n chưa r&otilde; liệu kết cục c&oacute; vẹn to&agrave;n, chuyện mới lại ập đến.</p>\r\n\r\n<p>N&uacute;i Đồng L&ocirc; mở rộng c&aacute;nh cửa, l&ocirc;i k&eacute;o ch&uacute;ng quỷ v&agrave;o một cuộc chiến t&agrave;n s&aacute;t h&ograve;ng tạo ra một Tuyệt Cảnh Quỷ Vương mới. Giữa l&uacute;c vạn quỷ xao động, Hoa Th&agrave;nh ph&aacute;p lực suy yếu, Th&iacute;ch Dung bỏ trốn, song song đ&oacute; lại xuất hiện th&ecirc;m một Cẩm Y Ti&ecirc;n v&agrave;o cuộc.</p>\r\n\r\n<p>Tạ Li&ecirc;n lần nữa bị cuốn v&agrave;o tranh đoạt kh&ocirc;ng hồi kết, lần n&agrave;y ai sẽ đồng h&agrave;nh c&ugrave;ng y&hellip;</p>\r\n\r\n<p><strong>Đ&ocirc;i n&eacute;t t&aacute;c giả</strong></p>\r\n\r\n<p>Nghề nghiệp: Tiểu thuyết gia của mạng văn học Tấn Giang.</p>\r\n\r\n<p><em>&quot;Gặp được người, chỉ ba chữ n&agrave;y th&ocirc;i đ&atilde; l&agrave; một trong những chuyện l&atilde;ng mạn nhất thế giới.&rdquo;</em></p>\r\n\r\n<p>C&aacute;c t&aacute;c phẩm đ&atilde; xuất bản tại Việt Nam:&nbsp;<em>Ma đạo tổ sư, Hệ thống tự cứu của nh&acirc;n vật phản diện.</em></p>', '1654352930.jpg', NULL, NULL, '2022-06-07 06:01:20'),
(3, 'All In Love - Ngập Tràn Yêu Thương', '95000.00', 46, 2, 'Cố Tây Tước', 'NXB Phụ nữ', '<p><br />\r\nTừ Vi Vũ hơi mắc bệnh sạch sẽ, c&oacute; ch&uacute;t bỉ ổi, c&oacute; ch&uacute;t mặt d&agrave;y, tuy nhi&ecirc;n trước mặt người ngo&agrave;i anh lu&ocirc;n h&agrave;o hoa phong nh&atilde;, sống t&aacute;ch biệt, độc lập, lạnh l&ugrave;ng m&agrave; ki&ecirc;u ngạo, lạnh l&ugrave;ng m&agrave; xa c&aacute;ch, trong sự xa c&aacute;ch ấy lại to&aacute;t l&ecirc;n sự cao qu&yacute;. Nhưng cứ về đến nh&agrave;, anh liền biến th&agrave;nh qu&yacute; &ocirc;ng &ldquo;th&iacute;ch cởi&rdquo;, lu&ocirc;n miệng k&ecirc;u: &ldquo;Tắm, tắm, tắm! Cố Thanh Kh&ecirc;, em c&oacute; muốn đến ch&agrave; đạp anh kh&ocirc;ng?&rdquo;</p>\r\n\r\n<p>Cố Thanh Kh&ecirc; lu&ocirc;n nghĩ, con người n&agrave;y c&ograve;n c&oacute; thể bỉ ổi hơn được nữa kh&ocirc;ng?</p>\r\n\r\n<p>Nếu kh&ocirc;ng sẽ l&agrave;:</p>\r\n\r\n<p>&ldquo;Vợ ơi, mau nấu cơm cho anh, y&ecirc;u cầu hợp ph&aacute;p đấy!&rdquo;</p>\r\n\r\n<p>&ldquo;Vợ ơi, h&ocirc;m nay đi xem phim nh&eacute;! Y&ecirc;u cầu hợp ph&aacute;p đấy!&rdquo;</p>\r\n\r\n<p>&ldquo;Thanh Kh&ecirc;, h&aacute;t tặng anh một b&agrave;i đi, y&ecirc;u cầu hợp ph&aacute;p đấy!&rdquo;</p>\r\n\r\n<p>Mỗi lần như thế, bạn Cố Thanh Kh&ecirc; lại phải cố kiềm chế kh&ocirc;ng xử l&yacute; anh một c&aacute;ch phi ph&aacute;p.</p>\r\n\r\n<p>Hạnh ph&uacute;c l&agrave; g&igrave;?</p>\r\n\r\n<p>Hạnh ph&uacute;c l&agrave; mười ba năm trước, cứ tan học về, c&oacute; một cậu b&eacute; lại đi h&igrave;nh chữ S đến trước mặt bạn.</p>\r\n\r\n<p>Mười ba năm sau, vẫn cậu b&eacute; đ&oacute; &ocirc;m bạn v&agrave;o l&ograve;ng, thủ thỉ: &ldquo;Cố Thanh Kh&ecirc;, cả tuổi thanh xu&acirc;n của anh đều d&agrave;nh hết cho em, thế n&ecirc;n em phải c&oacute; tr&aacute;ch nhiệm với anh đấy!&rdquo;</p>', '1654361560.jpg', NULL, NULL, '2022-06-07 05:45:15'),
(4, 'Nhiệt Độ Xã Giao', '87000.00', 48, 2, 'Carbeeq', 'NXB Hồng Đức', '<p><br />\r\n<strong>Nhiệt Độ X&atilde; Giao</strong></p>\r\n\r\n<p><em>&ldquo;N&agrave;y, &ocirc;ng c&oacute; biết người ta bảo &lsquo;kỳ thị đồng t&iacute;nh tức&nbsp; gay ngầm&rsquo; kh&ocirc;ng?&rdquo;</em></p>\r\n\r\n<p><em>&ldquo;T&ocirc;i thấy cũng c&oacute; l&yacute; phết đấy, &ocirc;ng nghĩ sao?&rdquo;</em></p>\r\n\r\n<p>Tống Viễn Tuần &ndash; một g&atilde; trai thẳng vừa lạnh l&ugrave;ng vừa đẹp trai nhưng lại kỳ thị đồng t&igrave;nh. Qua lời kể của một người bạn m&agrave; trở n&ecirc;n kh&ocirc;ng c&oacute; thiện cảm với du học sinh trao đổi Phương Chi&ecirc;u Mộ. Cảm x&uacute;c kh&ocirc;ng th&iacute;ch h&igrave;nh th&agrave;nh ngay từ khi chưa gặp mặt v&agrave; c&agrave;ng r&otilde; r&agrave;ng hơn đến khi gặp bởi m&ugrave;i hương qu&aacute; ngọt tr&ecirc;n cơ thể cậu.</p>\r\n\r\n<p>Phương Chi&ecirc;u Mộ - một cậu trai vốn dĩ rất hoạt b&aacute;t, vui vẻ trong cuộc sống nhưng v&igrave; sự gh&eacute;t bỏ ngầm của Tống Viễn Tuần m&agrave; qu&atilde;ng đời sinh vi&ecirc;n trao đổi tưởng chừng như đẹp đẽ của cậu ho&agrave;n to&agrave;n chấm dứt. Trở th&agrave;nh một người kh&eacute;p k&iacute;n hơn, lu&ocirc;n thui thủi một m&igrave;nh v&agrave; bị c&ocirc; lập.</p>\r\n\r\n<p>Hai con người tưởng chừng như chẳng c&oacute; nổi một điểm n&agrave;o c&oacute; thể chạm nhau tr&ecirc;n cuộc đời của họ th&igrave; &ocirc;ng mối &ndash; Triệu H&agrave;m vừa trực tiếp lại gi&aacute;n tiếp se duy&ecirc;n cho cả hai qua phần mềm kết bạn. Từ đ&oacute; Mu &ndash; Phương Chi&ecirc;u Mộ v&agrave; Andrew &ndash; Tống Viễn Tuần với vai diễn l&agrave; ch&agrave;ng kỹ sư 29 tuổi quen nhau. Ch&iacute;nh nhờ c&oacute; Andrew m&agrave; cuộc sống của Phương Chi&ecirc;u Mộ trở n&ecirc;n tươi s&aacute;ng, vui vẻ v&agrave; c&oacute; mục đ&iacute;ch hơn.</p>\r\n\r\n<p>Nhưng nếu Mu v&agrave; Andrew c&agrave;ng ng&agrave;y c&agrave;ng gần nhau hơn, th&acirc;n cận nhau hơn bao nhiều th&igrave; khoảng c&aacute;ch giữa Phương Chi&ecirc;u Mộ v&agrave; Tống Viễn Tuần lại c&aacute;ch xa nhau bấy nhi&ecirc;u&hellip; Sự xa l&aacute;nh của Phương Chi&ecirc;u Mộ d&agrave;nh cho Tống Viễn Tuần ch&iacute;nh l&agrave; điều khiến anh kh&ocirc;ng biết phải l&agrave;m sao để cho Andrew đối mặt với cậu ngo&agrave;i đời.</p>\r\n\r\n<p><em>&ldquo;Mộ Mộ,&rdquo; Tống Viễn Tuần cất tiếng, &ldquo;em cứ xem t&ocirc;i l&agrave; Andrew đi. Nếu c&oacute; chỗ n&agrave;o kh&aacute;c th&igrave; t&ocirc;i sẽ thay đổi cho giống.&rdquo;</em><br />\r\n<em>&ldquo;Những g&igrave; hồi trước l&agrave;m sai, t&ocirc;i sẽ sửa lại từng ch&uacute;t một. Em thấy kh&ocirc;ng vui th&igrave; mắng t&ocirc;i thế n&agrave;o cũng được.&rdquo;<br />\r\n&ldquo;T&igrave;nh cảm của t&ocirc;i kh&ocirc;ng đ&aacute;ng gi&aacute;, em cứ mang ra m&agrave; d&ugrave;ng, t&igrave;nh cảm của em rất đ&aacute;ng gi&aacute;, n&ecirc;n em đừng đ&ograve;i lấy lại.&rdquo;</em></p>\r\n\r\n<p>Nhiệt độ x&atilde; giao &ndash; Cuốn s&aacute;ch nhẹ nh&agrave;ng, dịu d&agrave;ng nhưng kh&ocirc;ng k&eacute;m phần thực tế m&agrave; ngo&agrave;i x&atilde; hội ch&uacute;ng ta ai cũng từng thấy, trải nghiệm về một cuộc sống nịnh nọt, vụ lợi, c&ocirc; lập,.. diễn ra hằng ng&agrave;y. Chỉ l&agrave; chẳng phải ai cũng đủ may mắn như Phương Chi&ecirc;u Mộ may mắn c&oacute; một Tống Viễn Tuần y&ecirc;u thương m&igrave;nh!</p>', '1654361572.jpg', NULL, NULL, '2022-06-07 06:02:11'),
(5, 'Thiên Quan Tứ Phúc - Tập 1', '120000.00', 48, 2, 'Mặc Hương Đồng Khứu', 'NXB Văn Học', '<p><br />\r\nChuyện bắt đầu từ t&aacute;m trăm năm trước...</p>\r\n\r\n<p>Một th&aacute;i tử điện hạ được vạn người ngưỡng vọng, phi thăng th&agrave;nh ti&ecirc;n. Những tưởng thế l&agrave; mỹ m&atilde;n, n&agrave;o ngờ y phi thăng hai lần th&igrave; hai lần bị đ&aacute;nh xuống trần, từ thần tướng tay hoa tay kiếm mu&ocirc;n d&acirc;n thờ phụng trở th&agrave;nh ti&ecirc;n nh&acirc;n đồng n&aacute;t lang thang khắp ng&otilde; hẻm hang c&ugrave;ng, kh&ocirc;ng c&oacute; lấy một ng&ocirc;i miếu thờ, từ giai thoại th&agrave;nh tr&ograve; cười của tam giới. Đến lần n&agrave;y, sau t&aacute;m trăm năm, y rốt cuộc cũng phi thăng lần thứ ba, một lần nữa trở lại Thi&ecirc;n đ&igrave;nh.</p>\r\n\r\n<p>Gặp lại bao người xưa, những chuyện cũ cũng lần lượt được v&eacute;n l&ecirc;n.</p>\r\n\r\n<p>T&aacute;m trăm năm qua lưu lạc hồng trần, y đ&atilde; trải qua những khổ nạn g&igrave;? Rốt cuộc l&agrave; ai đ&atilde; đẩy y đến thảm cảnh ấy?</p>\r\n\r\n<p><strong>Đ&ocirc;i n&eacute;t t&aacute;c giả</strong></p>\r\n\r\n<p>Mặc Hương Đồng Khứu, t&aacute;c giả của trang văn học mạng Tấn Giang, được rất nhiều độc giả biết tới qua cuốn tiểu thuyết Ma đạo tổ sư. Hai tiểu thuyết kh&aacute;c l&agrave; Hệ thống tự cứu của nh&acirc;n vật phản diện v&agrave; Thi&ecirc;n quan tứ ph&uacute;c cũng đ&aacute;nh gi&aacute; cao tr&ecirc;n c&aacute;c trang văn học.<br />\r\nC&aacute;c t&aacute;c phẩm của Mặc Hương Đồng Khứu được bạn đọc y&ecirc;u th&iacute;ch bởi t&igrave;nh tiết chặt chẽ, c&acirc;u chuyện mới lạ v&agrave; mang gi&aacute; trị nh&acirc;n văn s&acirc;u sắc, c&ugrave;ng phong c&aacute;ch h&agrave;i hước đặc trưng.</p>', '1654361588.jpg', NULL, NULL, '2022-06-06 06:29:45'),
(6, 'Không Có Kiếp Sau - Tập 1', '130000.00', 48, 2, 'Nguyệt Hạ Tang', 'NXB Hà Nội', '<p><br />\r\nKh&ocirc;ng C&oacute; Kiếp Sau - Tập 1</p>\r\n\r\n<p>Trong thế giới tương lai giả tưởng, để tr&aacute;nh khỏi nguy cơ bị ti&ecirc;u hủy, một nh&oacute;m người m&aacute;y c&ugrave;ng nhau bỏ trốn, chạy tới một cấm địa mang t&ecirc;n Lăng mộ Ho&agrave;ng đế biển Chết. V&agrave;o những ph&uacute;t cuối c&ugrave;ng của sinh mệnh người m&aacute;y, Mục Căn - người duy nhất tr&ecirc;n h&agrave;nh tinh ấy, đ&atilde; dang tay cứu gi&uacute;p họ.</p>\r\n\r\n<p>Tr&ecirc;n đường đi nhập học, &ldquo;gia đ&igrave;nh nhỏ&rdquo; của Mục Căn lại kết nạp th&ecirc;m hai th&agrave;nh vi&ecirc;n mới l&agrave; Olivia v&agrave; Manh Manh! V&agrave; thế l&agrave; c&acirc;u chuyện của gia đ&igrave;nh người-m&aacute;y bắt đầu!</p>\r\n\r\n<p><em>Con n&oacute;i rằng muốn c&oacute; &aacute;nh s&aacute;ng, vậy n&ecirc;n thế giới n&agrave;y liền c&oacute; &aacute;nh s&aacute;ng.</em></p>\r\n\r\n<p><em>&hellip; Từ nay về sau, thế giới của ta tồn tại v&igrave; con.</em></p>\r\n\r\n<p><strong>Đ&ocirc;i n&eacute;t t&aacute;c giả</strong></p>\r\n\r\n<p>Nguyệt Hạ Tang</p>\r\n\r\n<p>Th&iacute;ch ngắm đồ đẹp, th&iacute;ch đọc truyện kinh dị, xem phim kinh dị, tiểu thuyết trinh th&aacute;m.</p>\r\n\r\n<p>&ldquo;L&uacute;c nh&agrave;m ch&aacute;n th&iacute;ch viết l&aacute;ch một ch&uacute;t, thay thế cho thời gian viết nhật k&yacute; trước l&uacute;c đi ngủ, kể cho bản th&acirc;n một c&acirc;u chuyện, cũng kể cho mọi người một c&acirc;u chuyện m&agrave; th&ocirc;i. T&acirc;m trạng kh&aacute;c nhau kể được những c&acirc;u chuyện kh&aacute;c nhau.&rdquo;</p>\r\n\r\n<p><em>T&aacute;c phẩm ti&ecirc;u biểu:&nbsp;<em>Chờ hoa nguy&ecirc;n so&aacute;i nở, Ma vương (tạm dịch), Nguy&ecirc;n thủy t&aacute;i lai (tạm dịch),&hellip;</em></em></p>', '1654361615.jpg', NULL, NULL, '2022-06-07 05:58:36'),
(7, 'Quý Ngài Định Kiến', '122180.00', 48, 2, 'Khưu Trì', 'NXB Thanh Niên', '<p><br />\r\n<strong>Qu&yacute; Ng&agrave;i Định Kiến</strong></p>\r\n\r\n<p>Quan Lan - Gi&aacute;m đốc &acirc;m nhạc của tập đo&agrave;n giải tr&iacute; Thi&ecirc;n Long, Ph&oacute; tổng gi&aacute;m đốc thường vụ h&atilde;ng thu &acirc;m Thi&ecirc;n Long, trai g&aacute;i đều qua tay, quỷ đ&oacute;i h&aacute;o sắc, ma d&acirc;m cuồng quy tắc ngầm, ngủ với nửa giới &acirc;m nhạc Hoa ngữ, nh&agrave; sản xuất &acirc;m nhạc v&agrave;ng trong truyền thuyết.</p>\r\n\r\n<p>Như anh n&oacute;i: &ldquo;Người đi theo t&ocirc;i, từ cao xuống thấp, chia l&agrave;m một ho&agrave;ng hậu, hai qu&yacute; phi, bốn phi, t&aacute;m tần, mười s&aacute;u chi&ecirc;u nghi, ba mươi hai tiệp dư, mỹ nh&acirc;n v&agrave; t&agrave;i nh&acirc;n dưới nữa th&igrave; kh&ocirc;ng giới hạn số lượng. Cấp vị kh&aacute;c nhau th&igrave; lương th&aacute;ng v&agrave; đ&atilde;i ngộ cũng kh&aacute;c nhau, mỗi dịp cuối năm, t&ocirc;i sẽ căn cứ v&agrave;o l&yacute; lịch v&agrave; biểu hiện của mỗi người để tiến h&agrave;nh thăng cấp hoặc gi&aacute;ng cấp. Đến tiệp dư, mới c&oacute; thể k&yacute; hợp đồng với c&ocirc;ng ty của t&ocirc;i. Chi&ecirc;u nghi ra single2 , tần c&oacute; thể ra album, đến h&agrave;ng phi th&igrave; t&ocirc;i sẽ đ&iacute;ch th&acirc;n viết ca kh&uacute;c độc quyền cho cậu.&rdquo;</p>\r\n\r\n<p>Cũng ch&iacute;nh v&igrave; vậy cuộc đời sắp xếp cho anh gặp phải&nbsp; Trang L&acirc;n &ndash; cậu du học sinh v&ocirc; c&ugrave;ng điển trai với giọng h&aacute;t đặc biệt khiến cho &yacute; tưởng &acirc;m nhạc đ&atilde; biến mất từ l&acirc;u của Quan Lan len l&oacute;i trở lại. V&agrave; Trang L&acirc;n cũng l&agrave; một &nbsp;người tr&aacute;i ngược ho&agrave;n to&agrave;n với những g&igrave; anh đang c&oacute;. Gh&eacute;t quy tắc ngầm, muốn đi l&ecirc;n nhờ thực lực, chỉ muốn l&agrave;m &acirc;m nhạc kh&ocirc;ng quan t&acirc;m đến những g&igrave; người kh&aacute;c muốn. Người duy nhất khiến Quan Lan dốc hết sức để k&eacute;o về c&ocirc;ng ty cũng l&agrave; người gh&eacute;t Quan Lan v&agrave; khinh thường anh nhất.</p>\r\n\r\n<p>Đ&acirc;y thật sự l&agrave; một c&acirc;u chuyện bi h&agrave;i kịch của &ldquo;Qu&yacute; ng&agrave;i tự vả&rdquo; &ndash; Trang L&acirc;n với nội t&acirc;m v&ocirc; c&ugrave;ng sống động, suy nghĩ v&ocirc; c&ugrave;ng phi logic nhưng lại rất hợp l&yacute; d&ugrave; chưa bao giờ đi theo những g&igrave; anh muốn. L&agrave; cả một qu&aacute; tr&igrave;nh truy th&ecirc; của cậu ch&agrave;ng vừa ng&ocirc; ngh&ecirc;, ngốc nghếch nhưng cũng hết mực th&agrave;nh thật y như t&iacute;nh c&aacute;ch của cậu vậy. 30 năm chờ đợi của Quan Lan liệu c&oacute; đ&aacute;ng hay kh&ocirc;ng? H&atilde;y cũng đọc cuốn s&aacute;ch n&agrave;y nh&eacute;.</p>\r\n\r\n<p><strong><em>&ldquo;T&ocirc;i sợ chờ l&acirc;u qu&aacute;, anh sẽ l&agrave; của người kh&aacute;c.</em></strong></p>\r\n\r\n<p><strong><em>T&ocirc;i đ&atilde; đợi nhiều năm thế rồi, chỉ đợi một m&igrave;nh cậu th&ocirc;i.&rdquo;</em></strong></p>\r\n\r\n<p>&ldquo;Qu&yacute; ng&agrave;i định kiến&rdquo; &ndash; Bộ đam mỹ hiện đại, giải tr&iacute;, ni&ecirc;n hạ v&ocirc; c&ugrave;ng đ&aacute;ng đọc vừa khiến cho bạn cười đến chảy nước mắt v&agrave; cũng ẩn s&acirc;u trong đ&oacute; v&ocirc; c&ugrave;ng nhiều triết l&yacute; s&acirc;u sắc m&agrave; ch&uacute;ng ta vốn dĩ bỏ qu&ecirc;n giữa d&ograve;ng đời qu&aacute; x&ocirc; bồ, tấp nập v&agrave; trọng lợi &iacute;ch n&agrave;y!</p>', '1654361631.jpg', NULL, NULL, '2022-06-07 05:11:52'),
(8, 'Nhà Giả Kim (Tái Bản 2020)', '67150.00', 50, 1, 'Paulo Coelho', 'NXB Hội Nhà Văn', '<p><br />\r\n<em>Tất cả những trải nghiệm trong chuyến phi&ecirc;u du theo đuổi vận mệnh của m&igrave;nh đ&atilde; gi&uacute;p Santiago thấu hiểu được &yacute; nghĩa s&acirc;u xa nhất của hạnh ph&uacute;c, h&ograve;a hợp với vũ trụ v&agrave; con người</em>.&nbsp;</p>\r\n\r\n<p>Tiểu thuyết&nbsp;<em>Nh&agrave; giả kim&nbsp;</em>của Paulo Coelho như một c&acirc;u chuyện cổ t&iacute;ch giản dị, nh&acirc;n &aacute;i, gi&agrave;u chất thơ, thấm đẫm những minh triết huyền b&iacute; của phương Đ&ocirc;ng. Trong lần xuất bản đầu ti&ecirc;n tại Brazil v&agrave;o năm 1988, s&aacute;ch chỉ b&aacute;n được 900 bản. Nhưng, với số phận đặc biệt của cuốn s&aacute;ch d&agrave;nh cho to&agrave;n nh&acirc;n loại, vượt ra ngo&agrave;i bi&ecirc;n giới quốc gia,&nbsp;<em>Nh&agrave; giả kim&nbsp;</em>đ&atilde; l&agrave;m rung động h&agrave;ng triệu t&acirc;m hồn, trở th&agrave;nh một trong những cuốn s&aacute;ch b&aacute;n chạy nhất mọi thời đại, v&agrave; c&oacute; thể l&agrave;m thay đổi cuộc đời người đọc.</p>\r\n\r\n<p>&ldquo;Nhưng nh&agrave; luyện kim đan kh&ocirc;ng quan t&acirc;m mấy đến những điều ấy. &Ocirc;ng đ&atilde; từng thấy nhiều người đến rồi đi, trong khi ốc đảo v&agrave; sa mạc vẫn l&agrave; ốc đảo v&agrave; sa mạc. &Ocirc;ng đ&atilde; thấy vua ch&uacute;a v&agrave; kẻ ăn xin đi qua biển c&aacute;t n&agrave;y, c&aacute;i biển c&aacute;t thường xuy&ecirc;n thay h&igrave;nh đổi dạng v&igrave; gi&oacute; thổi nhưng vẫn m&atilde;i m&atilde;i l&agrave; biển c&aacute;t m&agrave; &ocirc;ng đ&atilde; biết từ thuở nhỏ. Tuy vậy, tự đ&aacute;y l&ograve;ng m&igrave;nh, &ocirc;ng kh&ocirc;ng thể kh&ocirc;ng cảm thấy vui trước hạnh ph&uacute;c của mỗi người lữ kh&aacute;ch, sau bao ng&agrave;y chỉ c&oacute; c&aacute;t v&agrave;ng với trời xanh nay được thấy ch&agrave; l&agrave; xanh tươi hiện ra trước mắt. &lsquo;C&oacute; thể Thượng đế tạo ra sa mạc chỉ để cho con người biết qu&yacute; trọng c&acirc;y ch&agrave; l&agrave;,&rsquo; &ocirc;ng nghĩ.&rdquo;</p>\r\n\r\n<p>- Tr&iacute;ch&nbsp;<em>Nh&agrave; giả kim</em></p>\r\n\r\n<p><strong>Nhận định</strong></p>\r\n\r\n<p>&ldquo;Sau Garcia M&aacute;rquez, đ&acirc;y l&agrave; nh&agrave; văn Mỹ Latinh được đọc nhiều nhất thế giới.&rdquo;&nbsp;<em>- The Economist</em>, London, Anh</p>\r\n\r\n<p>&ldquo;Santiago c&oacute; khả năng cảm nhận bằng tr&aacute;i tim như&nbsp;<em>Ho&agrave;ng tử b&eacute;</em>&nbsp;của Saint-Exup&eacute;ry.&rdquo;&nbsp;<em>- Frankfurter Allgemeine Zeitung, Đức</em></p>', '1654582488.jpg', 15, '2022-06-07 06:14:48', '2022-06-07 06:14:48'),
(9, 'Cây Cam Ngọt Của Tôi', '91800.00', 50, 1, 'José Mauro de Vasconcelos', 'NXB Hội Nhà Văn', '<p><br />\r\n&ldquo;Vị chua ch&aacute;t của c&aacute;i ngh&egrave;o h&ograve;a trộn với vị ngọt ng&agrave;o khi kh&aacute;m ph&aacute; ra những điều khiến cuộc đời n&agrave;y đ&aacute;ng sống... một t&aacute;c phẩm kinh điển của Brazil.&rdquo;&nbsp;<strong>- Booklist</strong></p>\r\n\r\n<p>&ldquo;Một c&aacute;ch nh&igrave;n cuộc sống gần như ho&agrave;n chỉnh từ con mắt trẻ thơ&hellip; c&oacute; sức mạnh sưởi ấm v&agrave; l&agrave;m tan n&aacute;t c&otilde;i l&ograve;ng, d&ugrave; người đọc ở lứa tuổi n&agrave;o.&rdquo;<strong>&nbsp;- The National</strong></p>\r\n\r\n<p>H&atilde;y l&agrave;m quen với Zez&eacute;, cậu b&eacute; tinh nghịch si&ecirc;u hạng đồng thời cũng đ&aacute;ng y&ecirc;u bậc nhất, với ước mơ lớn l&ecirc;n trở th&agrave;nh nh&agrave; thơ cổ thắt nơ bướm. Chẳng phải ai cũng c&ocirc;ng nhận khoản &ldquo;đ&aacute;ng y&ecirc;u&rdquo; kia đ&acirc;u nh&eacute;. Bởi v&igrave;, ở c&aacute;i x&oacute;m ngoại &ocirc; ngh&egrave;o ấy, nỗi khắc khổ bủa v&acirc;y đ&atilde; che mờ mắt người ta trước tr&aacute;i tim thiện lương c&ugrave;ng tr&iacute; tưởng tượng tuyệt vời của cậu b&eacute; con năm tuổi.</p>\r\n\r\n<p>C&oacute; hề g&igrave; đ&acirc;u bao nhi&ecirc;u l&agrave; hắt hủi, đ&aacute;nh mắng, v&igrave; Zez&eacute; đ&atilde; c&oacute; một người bạn đặc biệt để tr&uacute;t nỗi l&ograve;ng: c&acirc;y cam ngọt nơi vườn sau. V&agrave; cả một người bạn nữa, bằng xương bằng thịt, một ng&agrave;y kia xuất hiện, cho cậu b&eacute; nhạy cảm kh&ocirc;n sớm biết thế n&agrave;o l&agrave; tr&igrave;u mến, thế n&agrave;o l&agrave; nỗi đau, v&agrave; m&atilde;i m&atilde;i thay đổi cuộc đời cậu.</p>\r\n\r\n<p>Mở đầu bằng những thanh &acirc;m trong s&aacute;ng v&agrave; kết th&uacute;c lắng lại trong những nốt trầm ho&agrave;i niệm, C&acirc;y cam ngọt của t&ocirc;i khiến ta nhận ra vẻ đẹp thực sự của cuộc sống đến từ những điều giản dị như b&ocirc;ng hoa trắng của c&aacute;i c&acirc;y sau nh&agrave;, v&agrave; rằng cuộc đời thật khốn khổ nếu thiếu đi l&ograve;ng y&ecirc;u thương v&agrave; niềm trắc ẩn. Cuốn s&aacute;ch kinh điển n&agrave;y bởi thế kh&ocirc;ng ngừng khiến tr&aacute;i tim người đọc khắp thế giới thổn thức, kể từ khi ra mắt lần đầu năm 1968 tại Brazil.</p>\r\n\r\n<p><strong>T&Aacute;C GIẢ:</strong></p>\r\n\r\n<p>JOS&Eacute; MAURO DE VASCONCELOS (1920-1984) l&agrave; nh&agrave; văn người Brazil. Sinh ra trong một gia đ&igrave;nh ngh&egrave;o ở ngoại &ocirc; Rio de Janeiro, lớn l&ecirc;n &ocirc;ng phải l&agrave;m đủ nghề để kiếm sống. Nhưng với t&agrave;i kể chuyện thi&ecirc;n bẩm, tr&iacute; nhớ phi thường, tr&iacute; tưởng tượng tuyệt vời c&ugrave;ng vốn sống phong ph&uacute;, Jos&eacute; cảm thấy trong m&igrave;nh th&ocirc;i th&uacute;c phải trở th&agrave;nh nh&agrave; văn n&ecirc;n đ&atilde; bắt đầu s&aacute;ng t&aacute;c năm 22 tuổi. T&aacute;c phẩm nổi tiếng nhất của &ocirc;ng l&agrave; tiểu thuyết mang m&agrave;u sắc tự truyện C&acirc;y cam ngọt của t&ocirc;i. Cuốn s&aacute;ch được đưa v&agrave;o chương tr&igrave;nh tiểu học của Brazil, được b&aacute;n bản quyền cho hai mươi quốc gia v&agrave; chuyển thể th&agrave;nh phim điện ảnh. Ngo&agrave;i ra, Jos&eacute; c&ograve;n rất th&agrave;nh c&ocirc;ng trong vai tr&ograve; diễn vi&ecirc;n điện ảnh v&agrave; bi&ecirc;n kịch.</p>', '1654582620.jpg', NULL, '2022-06-07 06:17:00', '2022-06-07 06:17:00'),
(10, 'Bố Già (Đông A)', '95700.00', 50, 1, 'Mario Puzo', 'NXB Dân Trí', '<p><strong>Bố Gi&agrave; (Đ&ocirc;ng A)</strong></p>\r\n\r\n<p>Thế giới ngầm được phản &aacute;nh trong tiểu thuyết&nbsp;<em>Bố gi&agrave;</em>&nbsp;l&agrave; sự gặp gỡ giữa một b&ecirc;n l&agrave; &yacute; ch&iacute; cương cường v&agrave; nền tảng gia tộc chặt chẽ theo truyền thống mafia xứ Sicily với một b&ecirc;n l&agrave; x&atilde; hội Mỹ nhập nhằng đen trắng, mảnh đất m&agrave;u mỡ cho những cơ hội l&agrave;m ăn bất ch&iacute;nh hứa hẹn những m&oacute;n lợi kếch x&ugrave;. Trong thế giới ấy, h&igrave;nh tượng Bố gi&agrave; được t&aacute;c giả d&agrave;y c&ocirc;ng khắc họa đ&atilde; trở th&agrave;nh bức ch&acirc;n dung bất hủ trong l&ograve;ng người đọc. Từ một kẻ nhập cư tay trắng đến &ocirc;ng tr&ugrave;m tột đỉnh quyền uy, Don Vito Corleone l&agrave; con rắn hổ mang th&acirc;m trầm, nguy hiểm khiến kẻ th&ugrave; phải kiềng nể, e d&egrave;, nhưng cũng được bạn b&egrave;, th&acirc;n quyến xem như một đấng to&agrave;n năng đầy nghĩa kh&iacute;. Nh&acirc;n vật trung t&acirc;m ấy đồng thời cũng l&agrave; hiện th&acirc;n của một pho triết l&iacute; rất &ldquo;đời&rdquo; được nh&agrave;o nặn từ vốn sống của h&agrave;ng chục năm lăn lộn giữa chốn giang hồ bao phen v&agrave;o sinh ra tử, v&igrave; thế m&agrave; c&oacute; &yacute; kiến cho rằng&nbsp;<em>&ldquo;Bố gi&agrave;</em>&nbsp;l&agrave; sự tổng h&ograve;a của mọi hiểu biết.&nbsp;<em>Bố gi&agrave;</em>&nbsp;l&agrave; đ&aacute;p &aacute;n cho mọi c&acirc;u hỏi&rdquo;.</p>\r\n\r\n<p>Với cấu tứ ho&agrave;n hảo, cốt truyện kh&ocirc;ng thiếu những pha h&agrave;nh động gay cấn, t&igrave;nh tiết bất ngờ v&agrave; kh&ocirc;ng kh&iacute; k&igrave;nh địch đến nghẹt thở,&nbsp;<em>Bố gi&agrave;</em>&nbsp;xứng đ&aacute;ng l&agrave; đỉnh cao trong sự nghiệp văn chương của Mario Puzo. V&agrave; như một cơ duy&ecirc;n đặc biệt, ngay từ năm 1971-1972,&nbsp;<em>Bố gi&agrave;</em>&nbsp;đ&atilde; đến với bạn đọc trong nước qua phong c&aacute;ch chuyển ngữ h&agrave;o sảng, đậm chất giang hồ của dịch giả Ngọc Thứ Lang.</p>\r\n\r\n<p>Năm 2014, Đ&ocirc;ng A giới thiệu tới bạn đọc t&aacute;c phẩm&nbsp;<em>Bố gi&agrave;&nbsp;</em>c&oacute; bản quyền ch&iacute;nh thức tại Việt Nam qua bản dịch l&ocirc;i cuốn, hấp dẫn của Ngọc Thứ Lang.</p>\r\n\r\n<p><strong>V&agrave;i n&eacute;t về t&aacute;c giả:</strong></p>\r\n\r\n<p>Mario Puzo (1920&nbsp;-&nbsp;1999) l&agrave; nh&agrave; văn, nh&agrave; bi&ecirc;n kịch người Mỹ gốc Italy nổi tiếng với nhiều tiểu thuyết về đề t&agrave;i mafia v&agrave; tội phạm.&nbsp;<em>Bố gi&agrave; (The Godfather)&nbsp;</em>xuất bản năm&nbsp;1969 l&agrave; đỉnh cao của d&ograve;ng văn chương hư cấu n&agrave;y, đồng thời l&agrave; t&aacute;c phẩm đưa Puzo l&ecirc;n tột đỉnh vinh quang. Đ&acirc;y cũng l&agrave; một trong những tiểu thuyết b&aacute;n chạy nhất mọi thời đại.&nbsp;Ngo&agrave;i&nbsp;<em>Bố gi&agrave;</em>, Mario Puzo c&ograve;n nổi tiếng với c&aacute;c tiểu thuyết kh&aacute;c như&nbsp;<em>Sicilian kh&uacute;c ca bi tr&aacute;ng</em>,&nbsp;<em>Luật im lặng</em><em>,&nbsp;</em><em>&Ocirc;ng tr&ugrave;m quyền lực cuối c&ugrave;ng</em><em>,&nbsp;</em><em>Gia đ&igrave;nh Gi&aacute;o ho&agrave;ng</em><em>&hellip;</em></p>\r\n\r\n<p><strong>V&agrave;i n&eacute;t về dịch giả:</strong></p>\r\n\r\n<p>Ngọc Thứ Lang t&ecirc;n thật l&agrave; Nguyễn Ngọc T&uacute;, biệt danh l&agrave; c&ocirc;ng tử Bắc Kỳ, v&agrave;o S&agrave;i G&ograve;n lập nghiệp khoảng năm 1950.&nbsp;Ngọc Thứ Lang&nbsp;l&agrave; dịch giả của thời k&igrave; trước năm 1975,&nbsp;đ&atilde; chuyển ngữ nhiều t&aacute;c phẩm&nbsp;nhưng c&oacute; lẽ&nbsp;<em>Bố gi&agrave;</em>&nbsp;l&agrave; một dấu son trong sự nghiệp của &ocirc;ng.</p>\r\n\r\n<p>Năm 1972, bản dịch&nbsp;<em>Bố gi&agrave;&nbsp;</em>của Ngọc Thứ Lang chuyển ngữ từ nguy&ecirc;n bản tiếng Anh&nbsp;ra mắt v&agrave; đ&atilde;&nbsp;thu h&uacute;t được sự ch&uacute; &yacute; của&nbsp;rất nhiều&nbsp;độc giả. Nếu như&nbsp;<em>The Godfather</em>&nbsp;của Mario Puzo&nbsp;khi vừa xuất bản đ&atilde;&nbsp;nằm trong danh s&aacute;ch s&aacute;ch b&aacute;n chạy nhất&nbsp;suốt&nbsp;67&nbsp;tuần&nbsp;th&igrave;&nbsp;<em>Bố gi&agrave;</em>&nbsp;của Ngọc Thứ Lang cũng&nbsp;&ldquo;l&agrave;m mưa l&agrave;m gi&oacute;&rdquo;&nbsp;tr&ecirc;n&nbsp;thị trường&nbsp;văn học dịch của S&agrave;i G&ograve;n những năm&nbsp;70 của thế kỉ trước.</p>\r\n\r\n<p>C&aacute;i hay, c&aacute;i khiến người đọc say m&ecirc;&nbsp;<em>Bố gi&agrave;&nbsp;</em>c&oacute; lẽ nằm ở ch&iacute;nh giọng văn đậm chất giang hồ s&uacute;ng đạn của người dịch.&nbsp;V&agrave; bản th&acirc;n c&aacute;i t&ecirc;n&nbsp;<em>Bố gi&agrave;&nbsp;</em>cũng l&agrave; một s&aacute;ng tạo v&ocirc; tiền kho&aacute;ng hậu của Ngọc Thứ Lang. Nhiều độc giả Việt Nam n&oacute;i rằng nếu đọc&nbsp;<em>The Godfather</em>&nbsp;của Mario Puzo, h&atilde;y t&igrave;m đ&uacute;ng bản dịch của Ngọc Thứ Lang để thấy chất đ&agrave;n &ocirc;ng trong đ&oacute;&hellip;</p>\r\n\r\n<p><strong>Nhận x&eacute;t về t&aacute;c phẩm:</strong></p>\r\n\r\n<p><em>&ldquo;Bố gi&agrave;</em>&nbsp;l&agrave; sự tổng h&ograve;a của mọi hiểu biết.&nbsp;<em>Bố gi&agrave;</em>&nbsp;l&agrave; đ&aacute;p &aacute;n cho mọi c&acirc;u hỏi.&rdquo; -&nbsp;<strong><em>Diễn vi&ecirc;n Tom Hanks</em></strong></p>\r\n\r\n<p>&ldquo;Bạn kh&ocirc;ng thể dừng đọc n&oacute; v&agrave; kh&oacute; l&ograve;ng ngừng mơ về n&oacute;.&rdquo; -&nbsp;<strong><em>New York Times Magazine</em></strong></p>\r\n\r\n<p>&ldquo;Một t&aacute;c phẩm kinh điển về mafia&hellip; Tự bản th&acirc;n n&oacute; đ&atilde; tạo ra một thứ b&ugrave;a m&ecirc; hoặc độc giả.&rdquo;-&nbsp;<strong><em>The Times</em></strong></p>', '1654582670.jpg', NULL, '2022-06-07 06:17:50', '2022-06-08 01:05:21'),
(11, 'Cho Tôi Xin Một Vé Đi Tuổi Thơ', '64000.00', 50, 1, 'Nguyễn Nhật Ánh', 'NXB Trẻ', '<p>Trong Cho t&ocirc;i xin một v&eacute; đi tuổi thơ, nh&agrave; văn Nguyễn Nhật &Aacute;nh mời người đọc l&ecirc;n chuyến t&agrave;u quay ngược trở lại thăm tuổi thơ v&agrave; t&igrave;nh bạn dễ thương của 4 bạn nhỏ. Những tr&ograve; chơi dễ thương thời b&eacute;, t&iacute;nh c&aacute;ch thật th&agrave;, thẳng thắn một c&aacute;ch th&ocirc;ng minh v&agrave; dại dột, những ước mơ tự do trong l&ograve;ng&hellip; khiến cuốn s&aacute;ch c&oacute; thể l&agrave;m c&aacute;c bậc phụ huynh lo lắng rồi thở ph&agrave;o. Kh&ocirc;ng chỉ th&iacute;ch hợp với người đọc trẻ, cuốn s&aacute;ch c&ograve;n c&oacute; thể hấp dẫn v&agrave; thực sự c&oacute; &iacute;ch cho người lớn trong quan hệ với con m&igrave;nh.</p>', '1654582731.jpg', NULL, '2022-06-07 06:18:51', '2022-06-07 06:18:51'),
(12, 'Cảm Ơn Người Lớn', '88000.00', 50, 1, 'Nguyễn Nhật Ánh', 'NXB Trẻ', '<p><br />\r\n<strong>Cảm Ơn Người Lớn (B&igrave;a Mềm)</strong></p>\r\n\r\n<p>Cảm ơn người lớn - một &aacute;ng văn l&atilde;ng mạn trong giọng h&agrave;i hước đặc biệt &ldquo;d&agrave;nh cho trẻ em, v&agrave; những ai từng l&agrave; trẻ em&rdquo;..</p>\r\n\r\n<p>Bạn sẽ gặp lại M&ugrave;i, Tủn, T&iacute; s&uacute;n, Hải c&ograve;&hellip; của Cho t&ocirc;i xin một v&eacute; đi tuổi thơ, c&ugrave;ng chơi những tr&ograve; chơi quen thuộc, v&agrave; được đắm m&igrave;nh v&agrave;o những ước mơ đi&ecirc;n rồ, ngốc nghếch nhưng trong veo của tuổi mới lớn hồn nhi&ecirc;n v&agrave; đầy ắp dự định.</p>\r\n\r\n<p>V&agrave; cả khi họ đ&atilde; trưởng th&agrave;nh, bạo ch&uacute;a thời gian đ&atilde; v&ugrave;ng vẫy thế n&agrave;o trong cuộc đời của những nh&acirc;n vật m&agrave; bạn y&ecirc;u qu&yacute;&hellip;H&atilde;y bắt đầu đọc từ bất cứ trang n&agrave;o, c&oacute; thể đọc bất cứ l&uacute;c n&agrave;o, v&agrave; c&ugrave;ng với bất cứ ai. Bạn sẽ nhận được m&oacute;n qu&agrave; &ldquo;n trong 1&rdquo; của nh&agrave; văn Nguyễn Nhật &Aacute;nh: sẽ n lần thổn thức qua 1 cuốn s&aacute;ch 19 chương đầy ắp t&igrave;nh bạn ng&acirc;y thơ, t&igrave;nh x&oacute;m giềng tốt l&agrave;nh nh&acirc;n &aacute;i, t&igrave;nh y&ecirc;u đắm đuối ngọt ng&agrave;o&hellip;<br />\r\nCảm ơn người lớn được Nguyễn Nhật &Aacute;nh đặt b&uacute;t viết đ&uacute;ng sau 10 năm ra đời Cho t&ocirc;i xin một v&eacute; đi tuổi thơ &ndash; cuốn s&aacute;ch b&aacute;n chạy tới nay đ&atilde; 400.000 bản.</p>\r\n\r\n<p><strong>TR&Iacute;CH ĐOẠN HAY TRONG S&Aacute;CH</strong></p>\r\n\r\n<p>&ldquo;T&ocirc;i viết cuốn s&aacute;ch n&agrave;y kh&ocirc;ng d&agrave;nh cho trẻ em.&nbsp;T&ocirc;i viết cho những ai từng l&agrave; trẻ em.&rdquo;&nbsp;&nbsp;(CHO T&Ocirc;I XIN MỘT V&Eacute; ĐI TUỔI THỚ)</p>\r\n\r\n<p>---o0o---</p>\r\n\r\n<p>So với trẻ con, người lớn lu&ocirc;n cảm thấy thiếu thốn v&agrave; lu&ocirc;n cảm thấy đau khổ. &ldquo;&Ocirc;i, đời t&ocirc;i sao khổ thế n&agrave;y!&rdquo; - đ&oacute; l&agrave; c&acirc;u người lớn hay than, từ người kh&ocirc;ng đủ tiền mua một tấm &aacute;o đến người thiếu một khoản tiền nhỏ để c&oacute; thể mua một ng&ocirc;i nh&agrave; lớn.<br />\r\nTrẻ con kh&ocirc;ng bao giờ than như thế, v&igrave; bọn t&ocirc;i cả đời chỉ cần đủ tiền mua b&aacute;nh kẹo, c&agrave; rem, xi-r&ocirc;, ổi xo&agrave;i (tất nhi&ecirc;n cuộc đời trẻ con chỉ k&eacute;o d&agrave;i đến chừng n&agrave;o trở th&agrave;nh người lớn). Đang đ&oacute;i bụng m&agrave; c&oacute; tiền mua một ổ b&aacute;nh m&igrave; l&agrave; cuộc sống bọn t&ocirc;i lập tức biến th&agrave;nh m&agrave;u hồng d&ugrave; trước đ&oacute; n&oacute; được vẽ bằng gam m&agrave;u nhem nhuốc g&igrave; đi nữa.</p>\r\n\r\n<p>T&oacute;m lại, trẻ con chẳng c&oacute; mơ ước g&igrave; cao xa. Nhờ vậy bọn trẻ kh&ocirc;ng c&oacute; nhiều khổ n&atilde;o, thất vọng hay bất đắc ch&iacute; như người lớn. Người lớn cũng biết thế n&ecirc;n họ tự đặt ra c&aacute;c c&acirc;u danh ng&ocirc;n để tự răn m&igrave;nh. Lớn l&ecirc;n t&ocirc;i đọc được c&acirc;u &ldquo;tri t&uacute;c tiện t&uacute;c đ&atilde;i t&uacute;c h&agrave; thời t&uacute;c&rdquo; &ndash; &ldquo;biết đủ th&igrave; ắt thấy đủ, c&ograve;n đợi cho đủ sẽ chẳng bao giờ thấy đủ&rdquo;. C&acirc;u n&oacute;i rất hay (người lớn bao giờ cũng n&oacute;i hay) nhưng sau khi gật g&ugrave; khen hay, người lớn thường l&agrave;m ngược lại.</p>\r\n\r\n<p>(CẢM ỚN NGƯỜI LỚN)</p>', '1654582784.jpg', NULL, '2022-06-07 06:19:44', '2022-06-07 09:14:28'),
(13, 'Con Chim Xanh Biếc Bay Về', '112500.00', 49, 1, 'Nguyễn Nhật Ánh', 'NXB Trẻ', '<p><strong>Con chim xanh biếc bay về</strong></p>\r\n\r\n<p>Kh&ocirc;ng giống như những t&aacute;c phẩm trước đ&acirc;y lấy bối cảnh v&ugrave;ng qu&ecirc; miền Trung đầy ắp những ho&agrave;i niệm tuổi thơ dung dị, trong trẻo với c&aacute;c nh&acirc;n vật ở độ tuổi dậy th&igrave;, trong quyển s&aacute;ch mới lần n&agrave;y nh&agrave; văn Nguyễn Nhật &Aacute;nh lấy bối cảnh ch&iacute;nh l&agrave; S&agrave;i G&ograve;n &ndash; Th&agrave;nh phố Hồ Ch&iacute; Minh nơi t&aacute;c giả sinh sống (như l&agrave; một sự đền đ&aacute;p &acirc;n t&igrave;nh với mảnh đất miền Nam). C&aacute;c nh&acirc;n vật ch&iacute;nh trong truyện cũng &ldquo;lớn&rdquo; hơn, với những c&acirc;u chuyện mưu sinh lập nghiệp lắm gian nan thử th&aacute;ch của c&aacute;c sinh vi&ecirc;n trẻ đầy ho&agrave;i b&atilde;o. Tất nhi&ecirc;n kh&ocirc;ng thể thiếu những c&acirc;u chuyện t&igrave;nh cảm động, kịch t&iacute;nh v&agrave; bất ngờ khiến bạn đọc ngẩn ngơ, cười ra nước mắt. V&agrave; như trong mọi t&aacute;c phẩm Nguyễn Nhật &Aacute;nh, sự tử tế v&agrave; tinh thần hướng thượng vẫn l&agrave; điểm nhấn quan trọng trong quyển s&aacute;ch mới n&agrave;y.</p>\r\n\r\n<p>Như một cuốn phim &ldquo;trinh th&aacute;m t&igrave;nh y&ecirc;u&rdquo;,&nbsp;<strong>Con chim xanh biếc bay về</strong>&nbsp;dẫn bạn đi hết từ bất ngờ n&agrave;y đến t&ograve; m&ograve; suy đo&aacute;n kh&aacute;c, để kết th&uacute;c bằng một nỗi h&acirc;n hoan v&ocirc; bờ sau bao phen hồi hộp nghi kỵ đến kh&oacute; thở.</p>\r\n\r\n<p>Bạn sẽ theo phe sinh vi&ecirc;n-nh&acirc;n vi&ecirc;n với những c&acirc;u thơ dịu d&agrave;ng v&agrave; đ&aacute;o để, hay phe &ocirc;ng chủ với những kỹ năng kinh doanh khởi nghiệp? V&agrave; h&atilde;y đo&aacute;n thử, điều g&igrave; khiến bạn c&oacute; thể cảm động đến rưng rưng trong cuộc sống giữa S&agrave;i G&ograve;n bộn bề?</p>\r\n\r\n<p>L&acirc;u lắm mới c&oacute; h&igrave;nh ảnh th&agrave;nh phố rộn r&agrave;ng trong t&aacute;c phẩm của Nguyễn Nhật &Aacute;nh - điều hấp dẫn kh&aacute;c thường của&nbsp;<strong>Con chim xanh biếc bay về</strong>.</p>\r\n\r\n<p>Ch&iacute;nh v&igrave; thế m&agrave; cuốn s&aacute;ch chỉ c&oacute; một c&aacute;ch đọc th&ocirc;i: một mạch từ đầu đến cuối!</p>', '1654582862.jpg', NULL, '2022-06-07 06:21:02', '2022-06-08 08:55:21'),
(14, 'Tìm Em Nơi Anh - Find Me', '94300.00', 49, 1, 'André Aciman', 'NXB Trẻ', '<p><br />\r\nĐ&acirc;y l&agrave; cuốn tiếp theo sau &quot;Gọi em bằng t&ecirc;n anh&quot; (Call me by your name). Những nh&acirc;n vật gặp lại, v&agrave; c&acirc;u chuyện nhiều năm sau, khi họ đều đ&atilde; lớn tuổi hơn v&agrave; c&oacute; những ng&atilde; rẽ ri&ecirc;ng trong cuộc đời m&igrave;nh.</p>\r\n\r\n<p>T&aacute;c giả chia sẻ th&ecirc;m về quyết định viết tiếp c&acirc;u chuyện: &quot;T&ocirc;i vẫn lu&ocirc;n đau đ&aacute;u về c&acirc;u chuyện dang dở trong Gọi em bằng t&ecirc;n anh. D&ugrave; l&agrave; người tạo n&ecirc;n c&aacute;c nh&acirc;n vật v&agrave; cuộc đời họ, t&ocirc;i kh&ocirc;ng thể ngờ rằng ch&iacute;nh c&aacute;c nh&acirc;n vật lại khiến t&ocirc;i trăn trở về những xao động của t&igrave;nh y&ecirc;u - điều m&agrave; t&ocirc;i kh&ocirc;ng hay biết cho đến khi ho&agrave;n thiện cuốn s&aacute;ch. Bộ phim điện ảnh chuyển thể từ cuốn tiểu thuyết đ&atilde; gi&uacute;p t&ocirc;i nhận ra rằng m&igrave;nh muốn được trở lại với Elio v&agrave; Oliver v&agrave; tiếp tục đồng h&agrave;nh với 2 con người ấy&quot;.</p>\r\n\r\n<p>Find Me được chia l&agrave;m bốn phần: Tempo, Cadenza, Capriccio v&agrave; Da Capo, như một bản nhạc l&ocirc;i cuốn c&oacute; nhịp điệu v&agrave; hơi thở ri&ecirc;ng, c&ugrave;ng những đoạn ph&oacute;ng t&uacute;ng ngẫu hứng n&oacute;i l&ecirc;n tiếng l&ograve;ng người chơi.</p>\r\n\r\n<p>Tempo</p>\r\n\r\n<p>Phần đầu ti&ecirc;n được thuật lại bởi lời của Samuel &quot;Sami&quot; Perlman, cha của Elio Perlman. Mười năm sau c&aacute;c sự kiện của Call Me By Your Name, Sami Perlman gặp một người phụ nữ trẻ tuổi t&ecirc;n l&agrave; Miranda khi đi t&agrave;u đến Rome. Hai người nhanh ch&oacute;ng trở n&ecirc;n th&acirc;n thiết v&agrave; bắt đầu một mối quan hệ l&atilde;ng mạn. Trong khi ở Rome, cặp đ&ocirc;i đến thăm người cha ốm yếu của Miranda, tham dự một buổi thuyết tr&igrave;nh c&ocirc;ng khai do Sami tổ chức v&agrave; gặp được Elio.</p>\r\n\r\n<p>Cadenza<br />\r\nPhần thứ hai được thuật lại bởi lời của Elio. Năm năm sau, Elio l&agrave;m gi&aacute;o vi&ecirc;n dạy piano ở Paris. Tại một buổi h&ograve;a nhạc, anh gặp một người đ&agrave;n &ocirc;ng lớn tuổi t&ecirc;n l&agrave; Michel, v&agrave; họ bắt đầu một mối quan hệ l&atilde;ng mạn. Họ đến thăm ng&ocirc;i nh&agrave; thời thơ ấu của Michel ở v&ugrave;ng n&ocirc;ng th&ocirc;n nước Ph&aacute;p, v&agrave; kết th&uacute;c mối quan hệ v&agrave;i tuần sau đ&oacute; bởi cuối c&ugrave;ng, người cũ trong tr&aacute;i tim anh kh&ocirc;ng thể thay thế.<br />\r\nCapriccio<br />\r\nPhần thứ ba được thuật lại bởi lời của Oliver. V&agrave;i năm sau, Oliver l&agrave;m gi&aacute;o sư tại một trường cao đẳng ở New Hampshire. Anh ấy đ&atilde; lập gia đ&igrave;nh v&agrave; c&oacute; con, nhưng trong th&acirc;m t&acirc;m lại nu&ocirc;i dưỡng nỗi nhớ về khoảng thời gian anh ấy ở b&ecirc;n Elio khi c&ograve;n l&agrave; sinh vi&ecirc;n đại học. Anh ấy đo&agrave;n tụ với Elio ở &Yacute;, v&agrave; họ kết nối lại một c&aacute;ch l&atilde;ng mạn. Mặc d&ugrave; cha của Elio đ&atilde; chết, &ocirc;ng vẫn c&oacute; một đứa con với Miranda, đặt t&ecirc;n theo Oliver - Ollie Nhỏ.<br />\r\nDa Capo<br />\r\nPhần cuối c&ugrave;ng được thuật lại bởi lời của Elio. Elio v&agrave; Oliver lại ở c&ugrave;ng nhau, c&ugrave;ng nu&ocirc;i dưỡng Ollie Nhỏ, em trai c&ugrave;ng cha kh&aacute;c mẹ của Elio. Phần kết n&agrave;y diễn ra kh&ocirc;ng l&acirc;u sau c&aacute;c sự kiện trong Capriccio.</p>', '1654582936.jpg', NULL, '2022-06-07 06:22:16', '2022-06-08 06:42:55'),
(15, 'How Money Works - Hiểu Hết Về Tiền', '255000.00', 50, 3, 'DK', 'NXB Thế Giới', '<p><br />\r\nCẩm nang giới đơn giản, dễ h&igrave;nh dung nhất từ trước đến nay về tiền tệ v&agrave; hệ thống t&agrave;i ch&iacute;nh thế giới.</p>\r\n\r\n<p>Tiền đến từ đ&acirc;u? Sự kh&aacute;c biệt giữa gi&agrave;u c&oacute; v&agrave; thu nhập l&agrave; g&igrave;? C&aacute;c ch&iacute;nh phủ kiểm so&aacute;t tiền tệ như thế n&agrave;o? Tại sao một khoản nợ lại l&agrave; tốt?</p>\r\n\r\n<p><strong><em>How money works &ndash; Hiểu hết về tiền</em></strong>&nbsp;t&igrave;m hiểu c&aacute;ch thức c&aacute;c ch&iacute;nh phủ kiểm so&aacute;t tiền tệ, c&aacute;ch c&aacute;c c&ocirc;ng ty kiếm ra tiền, c&aacute;ch c&aacute;c thị trường t&agrave;i ch&iacute;nh vận h&agrave;nh, c&aacute;ch c&aacute;c c&aacute; nh&acirc;n tối đa h&oacute;a thu nhập th&ocirc;ng qua đầu tư&hellip;</p>\r\n\r\n<p>Đưa ra định nghĩa cho h&agrave;ng trăm kh&aacute;i niệm, c&ugrave;ng với những kiến thức nền tảng nhất về c&aacute;c hệ thống t&agrave;i ch&iacute;nh, như tr&aacute;i phiếu, cổ phiếu, tiền m&atilde; h&oacute;a, bitcoin, quản l&yacute; nợ, tr&aacute;nh vỡ nợ trực tuyến v&agrave; gọi vốn cộng đồng, cũng như c&aacute;ch tiền vận h&agrave;nh thế giới.</p>', '1654593561.jpg', NULL, '2022-06-07 09:19:21', '2022-06-07 09:19:21'),
(16, 'Không Bao Giờ Là Thất Bại! Tất Cả Là Thử Thách', '233160.00', 50, 3, 'Chung Ju Yung', 'NXB Dân Trí', '<p><strong>Kh&ocirc;ng Bao Giờ L&agrave; Thất Bại! Tất Cả L&agrave; Thử Th&aacute;ch - B&igrave;a Cứng</strong></p>\r\n\r\n<p><strong>Tự truyện nổi tiếng của g&atilde; khổng lồ trong nền kinh tế H&agrave;n Quốc - cố Chủ tịch tập đo&agrave;n Hyundai Chung Ju-yung</strong></p>\r\n\r\n<p>Thất bại xảy ra l&agrave; để con người nhận ra sức mạnh nội tại của bản th&acirc;n, bởi kh&ocirc;ng ai sống m&agrave; chỉ trải qua những th&agrave;nh c&ocirc;ng trong suốt cuộc đời. Tuy vậy, ta vẫn lu&ocirc;n băn khoăn tự hỏi bản th&acirc;n rằng bao nhi&ecirc;u lần thất bại mới đủ để th&agrave;nh c&ocirc;ng?</p>\r\n\r\n<p>Mỗi người đều c&oacute; định nghĩa kh&aacute;c nhau về th&agrave;nh c&ocirc;ng. N&oacute; c&oacute; thể được hiểu l&agrave; đạt được mục ti&ecirc;u đ&atilde; đề ra, nhưng ở một g&oacute;c nh&igrave;n kh&aacute;c th&igrave; th&agrave;nh c&ocirc;ng ch&iacute;nh l&agrave; r&uacute;t ra được kinh nghiệm v&agrave; hi&ecirc;n ngang bước tiếp sau những lần thất bại. Việc thấu hiểu bản chất của th&agrave;nh c&ocirc;ng lẫn thất bại đ&ograve;i hỏi &yacute; ch&iacute; v&agrave; tầm nh&igrave;n của ch&iacute;nh bản th&acirc;n người trải nghiệm. Chung Ju-yung - người khởi nghiệp từ hai b&agrave;n tay trắng đ&atilde; trở th&agrave;nh chủ tịch của Hyundai, một tập đo&agrave;n nổi tiếng tr&ecirc;n thế giới v&agrave; l&agrave; niềm tự h&agrave;o của người d&acirc;n H&agrave;n Quốc - c&oacute; lẽ hơn ai hết sẽ thấu hiểu những vất vả, thử th&aacute;ch v&agrave; b&agrave;i học xương m&aacute;u tr&ecirc;n con đường chinh phục đỉnh vinh quang của m&igrave;nh.</p>\r\n\r\n<p>&ldquo;Kh&ocirc;ng bao giờ l&agrave; thất bại! Tất cả l&agrave; thử th&aacute;ch&rdquo; được xem như cuốn tự truyện chi tiết về cuộc đời lẫn sự nghiệp của cố Chủ tịch Chung Ju-yung. Kh&ocirc;ng chỉ thuật lại h&agrave;nh tr&igrave;nh tạo lập n&ecirc;n một tập đo&agrave;n c&ocirc;ng nghiệp ph&aacute;t triển bậc nhất, cuốn s&aacute;ch c&ograve;n chứa đựng những b&agrave;i học s&acirc;u sắc về quản trị, kinh doanh cũng như đạo l&agrave;m người với tinh thần &ldquo;đắc nh&acirc;n t&acirc;m&rdquo;. Đ&acirc;y ch&iacute;nh l&agrave; cuốn s&aacute;ch gối đầu giường của rất nhiều doanh nh&acirc;n Việt Nam.</p>\r\n\r\n<p>&ldquo;Kh&ocirc;ng bao giờ l&agrave; thất bại! Tất cả l&agrave; thử th&aacute;ch&rdquo;, ấn phẩm đ&atilde; được t&aacute;i bản nhiều lần tại Việt Nam v&agrave; kh&ocirc;ng c&ograve;n đơn thuần l&agrave; tựa đề của một cuốn s&aacute;ch, t&ecirc;n của n&oacute; đ&atilde; trở th&agrave;nh phương ch&acirc;m sống, truyền cảm hứng cho rất nhiều bạn trẻ.</p>\r\n\r\n<p>Qua từng trang hồi k&yacute;, người đọc sẽ cảm nhận được tuổi thơ ngh&egrave;o kh&oacute; v&agrave; kh&aacute;t khao l&agrave;m gi&agrave;u của cố Chủ tịch Tập đo&agrave;n Hyundai: ba lần trốn chạy khỏi gia đ&igrave;nh v&agrave; qu&ecirc; nh&agrave; với kh&aacute;t khao tho&aacute;t khỏi cuộc sống vất vả của người l&agrave;m n&ocirc;ng. Đặc biệt, &ocirc;ng g&acirc;y dựng cơ nghiệp từ xuất ph&aacute;t điểm kh&ocirc;ng một đồng xu d&iacute;nh t&uacute;i. Con đường vươn đến th&agrave;nh c&ocirc;ng của &ocirc;ng Chung ho&agrave;n to&agrave;n kh&ocirc;ng &ecirc;m ả. Khi vừa lập nghiệp với đ&ocirc;i ch&uacute;t th&agrave;nh tựu, &ocirc;ng lại gặp biến cố v&agrave; trở về vạch xuất ph&aacute;t. Thế nhưng, điều quan trọng l&agrave; con người ấy kh&ocirc;ng bao giờ nghĩ m&igrave;nh thất bại.</p>\r\n\r\n<p>Trong mọi ho&agrave;n cảnh, kể cả khi đ&atilde; trở n&ecirc;n gi&agrave;u c&oacute;, &ocirc;ng vẫn lu&ocirc;n xem m&igrave;nh l&agrave; một người lao động l&agrave;m gi&agrave;u bằng ch&iacute;nh năng lực của m&igrave;nh, lu&ocirc;n cần kiệm, ch&acirc;n th&agrave;nh v&agrave; thẳng thắn. V&agrave; tr&ecirc;n hết, &ocirc;ng lu&ocirc;n c&oacute; tầm nh&igrave;n của một người y&ecirc;u nước, lu&ocirc;n nghĩ đến lợi &iacute;ch chung. Ch&iacute;nh v&igrave; điều đ&oacute;, &ocirc;ng đ&atilde; vượt qua rất nhiều chướng ngại tưởng chừng như kh&ocirc;ng thể vượt qua trong cuộc đời l&agrave;m doanh nh&acirc;n của m&igrave;nh.</p>\r\n\r\n<p>Với 9 chương s&aacute;ch, bạn đọc sẽ lần lượt đi qua từng chặng đường m&agrave; cố Chủ tịch Chung Ju-yung từng trải qua: từ thời thơ ấu thiếu thốn v&agrave; h&agrave;nh tr&igrave;nh thực hiện ước mơ - l&ecirc;n Seoul học để đổi đời, cho đến những va vấp khi bắt đầu dấn th&acirc;n v&agrave;o thương trường, v&agrave; cuối c&ugrave;ng l&agrave; chặng đường x&acirc;y dựng đế chế Hyundai đồ sộ, trở th&agrave;nh bộ mặt của đất nước H&agrave;n Quốc.</p>\r\n\r\n<ul>\r\n	<li>Chương 1: Cha mẹ v&agrave; Qu&ecirc; hương</li>\r\n	<li>Chương 2: Khởi đầu của Hyundai</li>\r\n	<li>Chương 3: T&ocirc;i l&agrave; một nh&agrave; x&acirc;y dựng</li>\r\n	<li>Chương 4: C&ocirc;ng ty &ocirc;-t&ocirc; Hyundai v&agrave; c&ocirc;ng ty đ&oacute;ng t&agrave;u Hyundai</li>\r\n	<li>Chương 5: Những thăng trầm ở Trung Đ&ocirc;ng v&agrave; năm 1980 đầy biến động</li>\r\n	<li>Chương 6: Đăng cai Thế vận hội Olympic 1988 v&agrave; Nh&agrave; nước Cộng ho&agrave; thứ năm</li>\r\n	<li>Chương 7: Dự &aacute;n n&uacute;i Kim Cương v&agrave; Siberia đầy tiềm năng</li>\r\n	<li>Chương 8: Con đường y&ecirc;u nước, thương d&acirc;n</li>\r\n	<li>Chương 9: Triết l&yacute; của t&ocirc;i, tinh thần của Hyundai</li>\r\n	<li>Lời cuối s&aacute;ch: Sinh ra tr&ecirc;n mảnh đất n&agrave;y</li>\r\n</ul>\r\n\r\n<p>Đặc biệt, trong ấn bản mới nhất, đầy đủ nhất do First News - Tr&iacute; Việt ph&aacute;t h&agrave;nh, độc giả sẽ đọc được bức t&acirc;m thư của người con trai Chung Mong-joon viết về cha m&igrave;nh - chỉ xuất hiện trong bản dịch tiếng Việt cho đến thời điểm hiện tại.</p>\r\n\r\n<p><strong><em>Về t&aacute;c giả</em></strong></p>\r\n\r\n<p>Chung Ju-yung (1915 - 2001) l&agrave; con cả trong một gia đ&igrave;nh l&agrave;m n&ocirc;ng c&oacute; ch&iacute;n người con, sinh sống tại một ng&ocirc;i l&agrave;ng nhỏ thuộc Bắc Triều Ti&ecirc;n. Do gia cảnh ngh&egrave;o kh&oacute; n&ecirc;n &ocirc;ng phải g&aacute;c lại việc học khi mới ở bậc tiểu học để phụ gi&uacute;p gia đ&igrave;nh. Sau n&agrave;y, &ocirc;ng được biết đến l&agrave; một nh&agrave; tư bản ng&agrave;nh c&ocirc;ng nghiệp, tỷ ph&uacute;, nh&agrave; từ thiện nổi tiếng tại H&agrave;n Quốc cũng như tr&ecirc;n thế giới. &Ocirc;ng nổi tiếng với vai tr&ograve; người s&aacute;ng lập Tập đo&agrave;n Hyundai - doanh nghiệp đi ti&ecirc;n phong trong ng&agrave;nh c&ocirc;ng nghiệp xe &ocirc;-t&ocirc; v&agrave; đ&oacute;ng t&agrave;u của H&agrave;n Quốc.</p>\r\n\r\n<p>L&agrave; một trong những nh&acirc;n vật c&oacute; tầm ảnh hưởng lớn nhất của nền kinh tế H&agrave;n Quốc, Chung Ju-yung l&agrave; người nước ngo&agrave;i đầu ti&ecirc;n nhận bằng Tiến sĩ danh dự của Đại học George Washington. Đồng thời, &ocirc;ng li&ecirc;n tiếp giữ ghế Chủ tịch Li&ecirc;n đo&agrave;n C&aacute;c ng&agrave;nh C&ocirc;ng nghiệp H&agrave;n Quốc trong gần một thập ni&ecirc;n v&agrave; l&agrave; một trong những người c&oacute; c&ocirc;ng lớn trong việc tham gia đ&agrave;m ph&aacute;n v&agrave; vận động để Seoul gi&agrave;nh được quyền đăng cai Thế vận hội Olympic 1988.</p>', '1654593627.jpg', NULL, '2022-06-07 09:20:27', '2022-06-07 09:21:48');
INSERT INTO `books` (`id`, `name`, `giaban`, `soluong`, `category_id`, `tacgia`, `nxb`, `gioithieu`, `hinhanh`, `taiban`, `created_at`, `updated_at`) VALUES
(17, 'Lạc Quan Tếu - Irrational Exuberance', '299000.00', 50, 3, 'Robert J Shiller', 'NXB Thế Giới', '<p><br />\r\nNếu t&igrave;m kiếm tr&ecirc;n Google những cuốn s&aacute;ch t&agrave;i ch&iacute;nh kinh điển m&agrave; mọi nh&agrave; đầu tư n&ecirc;n đọc, bạn sẽ thấy c&aacute;i t&ecirc;n Lạc Quan Tếu (Irrational Exuberance) của gi&aacute;o sư kinh tế học Robert Shiller xuất hiện ở nhiều danh s&aacute;ch uy t&iacute;n kh&aacute;c nhau.&nbsp;</p>\r\n\r\n<p>V&agrave; khi Robert Shiller cảnh b&aacute;o bong b&oacute;ng đang nổi l&ecirc;n khắp nơi tr&ecirc;n thị trường, mọi người đều phải thận trọng lắng nghe.</p>\r\n\r\n<p>Nếu như tương lai của bạn phụ thuộc v&agrave;o t&agrave;i khoản hưu tr&iacute;, nh&agrave; ở, chứng kho&aacute;n hoặc bất cứ khoản đầu tư n&agrave;o kh&aacute;c, cuốn s&aacute;ch Lạc Quan Tếu của Robert Shiller l&agrave; t&agrave;i liệu bạn bắt buộc phải nghi&ecirc;n cứu thật kỹ lưỡng.</p>\r\n\r\n<p><strong>NỘI DUNG CH&Iacute;NH</strong></p>\r\n\r\n<p>Lạc Quan Tếu đ&atilde; trải qua ba đợt cập nhật. Trong hai lần sửa đổi v&agrave; bổ sung đầu ti&ecirc;n, Gi&aacute;o sư Robert Shiller đ&atilde; li&ecirc;n tục cảnh b&aacute;o về bong b&oacute;ng c&ocirc;ng nghệ (ấn bản năm 2000), sau đ&oacute; l&agrave; dấu hiệu của sự b&ugrave;ng nổ gi&aacute; nh&agrave; ở (ấn bản năm 2005). Trong hai trường hợp, &ocirc;ng đều đ&uacute;ng.&nbsp;</p>\r\n\r\n<p>Nhưng thật sự những bong b&oacute;ng n&agrave;y chẳng c&oacute; g&igrave; mới, &iacute;t nhất l&agrave; về bản chất. Đ&oacute; l&agrave; sự lặp lại của những hiện tượng xưa cũ, một c&acirc;u chuyện của thời đại mới được đưa l&ecirc;n cao bởi những lời truyền miệng đầy m&ecirc; hoặc, một cảm gi&aacute;c rất r&otilde; rệt về cơn cuồng kh&aacute;t của đ&aacute;m đ&ocirc;ng về một loại h&igrave;nh đầu tư n&agrave;o đ&oacute;.&nbsp;</p>\r\n\r\n<p>N&oacute;i c&aacute;ch kh&aacute;c, đ&oacute; l&agrave; một sự &ldquo;lạc quan tếu&rdquo;. Nếu như hai phi&ecirc;n bản trước đ&oacute; bao qu&aacute;t thị trường chứng kho&aacute;n v&agrave; nh&agrave; ở. Phi&ecirc;n bản thứ ba n&agrave;y đ&atilde; mở rộng phạm vi để bao gồm cả thị trường tr&aacute;i phiếu, v&agrave; giải quyết tất cả c&aacute;c thị trường đầu tư lớn. S&aacute;ch cũng bao gồm dữ liệu được cập nhật xuy&ecirc;n suốt v&agrave; b&agrave;i giảng đạt Giải thưởng Nobel 2013 của Gi&aacute;o sư Shiller trong việc chẩn đo&aacute;n nguy&ecirc;n nh&acirc;n g&acirc;y ra bong b&oacute;ng t&agrave;i sản.&nbsp;</p>\r\n\r\n<p><strong>Cụ thể cuốn s&aacute;ch sẽ chỉ ra:</strong></p>\r\n\r\n<p>Những yếu tố cấu tr&uacute;c dẫn dắt bong b&oacute;ng thị trường</p>\r\n\r\n<p>Những yếu tố văn h&oacute;a vốn củng cố th&ecirc;m cấu tr&uacute;c của một bong b&oacute;ng đầu cơ</p>\r\n\r\n<p>Những yếu tố t&acirc;m l&yacute; đằng sau c&aacute;c h&agrave;nh vi thị trường</p>\r\n\r\n<p>Những l&yacute; giải c&aacute;c bong b&oacute;ng thị trường</p>\r\n\r\n<p>C&acirc;n nhắc hậu quả của bong b&oacute;ng đầu cơ đến nh&agrave; đầu tư c&aacute; nh&acirc;n, nh&agrave; đầu tư tổ chức, v&agrave; ch&iacute;nh phủ.</p>\r\n\r\n<p>Được lấy t&ecirc;n từ lời b&igrave;nh luận rất nổi tiếng năm 1996 của Cựu Chủ tịch Cục dự trữ li&ecirc;n bang Mỹ (FED) Alan Greenspan về sự bất hợp l&yacute; của thị trường chứng kho&aacute;n, cuốn s&aacute;ch của Shiller, xuất bản lần đầu ti&ecirc;n v&agrave;o th&aacute;ng 3/2001, đ&atilde; lạnh l&ugrave;ng đưa ra lời cảnh b&aacute;o về nguy cơ b&ugrave;ng nổ của quả bong b&oacute;ng dotcom.&nbsp;</p>\r\n\r\n<p>Cuốn s&aacute;ch đập tan mọi luận điểm cho rằng thị trường đang tăng trưởng rất hợp l&yacute; tr&ecirc;n cơ sở ph&acirc;n t&iacute;ch t&acirc;m l&yacute; cảm x&uacute;c của thị trường, sự kỳ vọng v&agrave; h&agrave;nh vi của đ&aacute;m đ&ocirc;ng. C&agrave;ng đọc nhiều biết nhiều, bạn c&agrave;ng c&oacute; nhiều cơ hội &aacute;p dụng c&aacute;c lời khuy&ecirc;n của c&aacute;c chuy&ecirc;n gia v&agrave;o chiến lược đầu tư của ri&ecirc;ng m&igrave;nh.&nbsp;</p>\r\n\r\n<p><strong>T&Aacute;C GIẢ ROBERT SHILLER L&Agrave; AI?</strong></p>\r\n\r\n<p>Robert J. Shiller (sinh năm 1946) l&agrave; Gi&aacute;o sư Kinh tế tại Đại học Yale. &Ocirc;ng nguy&ecirc;n l&agrave; Ph&oacute; Chủ tịch Hiệp hội Kinh tế Mỹ năm 2005. &Ocirc;ng đ&atilde; đoạt Giải Nobel Kinh tế năm 2013 với c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu về gi&aacute; cả của t&agrave;i sản đầu cơ. &Ocirc;ng cũng l&agrave; người đồng s&aacute;ng lập v&agrave; gi&aacute;m đốc kinh tế của quản l&yacute; c&ocirc;ng ty đầu tư MacroMarkets LLC. Robert J. Shiller được xếp hạng trong số 100 nh&agrave; kinh tế c&oacute; ảnh hưởng nhất tr&ecirc;n thế giới v&agrave; c&oacute; đ&oacute;ng g&oacute;p rất thường xuy&ecirc;n cho Thời b&aacute;o New Yorks chuy&ecirc;n mục G&oacute;c nh&igrave;n kinh tế.</p>\r\n\r\n<p><strong>AI N&Ecirc;N ĐỌC LẠC QUAN TẾU?</strong></p>\r\n\r\n<p>Bất cứ ai muốn c&oacute; một c&aacute;i nh&igrave;n rất KHOA HỌC về sự h&igrave;nh th&agrave;nh của bong b&oacute;ng</p>\r\n\r\n<p>Nh&agrave; đầu tư, nh&agrave; giao dịch t&igrave;m hiểu một c&aacute;ch NGHI&Ecirc;M T&Uacute;C về bong b&oacute;ng cũng như những kh&iacute;a cạnh t&acirc;m l&yacute;, h&agrave;nh vi của thị trường</p>\r\n\r\n<p>Nh&agrave; đầu tư, nh&agrave; giao dịch muốn c&oacute; được những lời khuy&ecirc;n H&Agrave;NH ĐỘNG CỤ THỂ khi ph&aacute;t hiện những dấu hiệu của bong b&oacute;ng</p>\r\n\r\n<p><strong>CHUY&Ecirc;N GIA N&Oacute;I G&Igrave; VỀ CUỐN S&Aacute;CH N&Agrave;Y?</strong></p>\r\n\r\n<p><em>&ldquo;Shiller đ&atilde; cung cấp một hướng dẫn dễ tiếp cận với c&aacute;c t&agrave;i liệu vốn kh&oacute; xuy&ecirc;n thủng tr&ecirc;n thị trường t&agrave;i ch&iacute;nh, đặc biệt l&agrave; thị trường chứng kho&aacute;n Mỹ&rdquo;. &ndash;&nbsp;</em>Tờ Foreign Affairs</p>\r\n\r\n<p><em>&ldquo;Robert J. Shiller&hellip; đ&atilde; hoạt động nghi&ecirc;n cứu nhiều hơn bất kỳ nh&agrave; kinh tế n&agrave;o kh&aacute;c trong thế hệ của m&igrave;nh trong việc ghi lại c&aacute;c kh&iacute;a cạnh &iacute;t duy l&yacute; hơn của thị trường t&agrave;i ch&iacute;nh&rdquo;. &ndash;&nbsp;</em>Nh&agrave; kinh tế học Paul Krugman, b&igrave;nh luận vi&ecirc;n của New York Times</p>\r\n\r\n<p><em>&ldquo;Shiller đ&atilde; viết n&ecirc;n một b&agrave;i ph&ecirc; b&igrave;nh r&otilde; r&agrave;ng v&agrave; rắn rỏi&rdquo;. &ndash;&nbsp;</em>David Warsh, bi&ecirc;n tập vi&ecirc;n của Boston Globe</p>', '1654593692.jpg', NULL, '2022-06-07 09:21:32', '2022-06-07 09:21:32'),
(18, 'Nguyên Lý Marketing', '869130.00', 50, 3, 'Philip Kotler, Gary Armstrong', 'NXB Đại Học Kinh Tế Quốc Dân', '<p><br />\r\n<strong>Nguy&ecirc;n L&yacute; Marketing</strong></p>\r\n\r\n<p>Cuốn s&aacute;ch c&oacute; thể được xem như l&agrave; gi&aacute;o tr&igrave;nh kinh điển d&agrave;nh cho bất cứ ai muốn nghi&ecirc;n cứu lĩnh vực marketing, bởi n&oacute; chứa b&ecirc;n trong gần như tất cả những g&igrave; bạn cần biết về marketing &ndash; từ định nghĩa, l&yacute; luận, c&aacute;c nguy&ecirc;n tắc, cho đến ứng dụng, v&iacute; dụ thực tế. S&aacute;ch cũng kh&ocirc;ng ngừng được chỉnh sửa, t&aacute;i bi&ecirc;n soạn, cập nhật th&ecirc;m th&ocirc;ng tin, trường hợp nghi&ecirc;n cứu mới cho ph&ugrave; hợp với t&igrave;nh h&igrave;nh kinh tế kh&ocirc;ng ngừng thay đổi.</p>\r\n\r\n<p><strong>ĐẶC ĐIỂM NỔI BẬT:</strong></p>\r\n\r\n<p>- Ngo&agrave;i c&aacute;c kh&aacute;i niệm cốt l&otilde;i của lĩnh vực marketing được tổng kết qua thời gian, hai t&aacute;c giả Philip Kotler v&agrave; Gary Armstrong tập trung v&agrave;o ph&acirc;n t&iacute;ch c&ocirc;ng việc marketing trong bối cảnh kinh tế đầy th&aacute;ch thức hiện nay.</p>\r\n\r\n<p>Cuốn s&aacute;ch tr&igrave;nh b&agrave;y th&ocirc;ng tin marketing nền tảng theo một h&igrave;nh thức s&aacute;ng tạo v&agrave; dễ hiểu, đầy đủ những dẫn chứng, minh họa được lấy từ thực tiễn sống động của thương trường. Sau mỗi chương s&aacute;ch đều c&oacute; phần t&oacute;m tắt rất c&ocirc; đọng, gi&uacute;p người đọc củng cố lại kiến thức tiếp thu được trong chương đ&oacute;. Đồng thời, s&aacute;ch cũng c&oacute; phần thảo luận v&agrave; ứng dụng nhằm mở rộng hiểu biết của độc giả về lĩnh vực n&agrave;y.</p>\r\n\r\n<p>- T&aacute;c giả của cuốn s&aacute;ch &ndash; gi&aacute;o sư marketing Philip Kotler được coi l&agrave; cha đẻ của ng&agrave;nh marketing hiện đại. &Ocirc;ng được xem l&agrave; huyền thoại duy nhất về marketing, đồng thời l&agrave; một trong bốn nh&agrave; quản trị vĩ đại nhất mọi thời đại, c&ugrave;ng với Peter Drucker, Jack Welch v&agrave; Bill Gates.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ trong lĩnh vực học thuật, những kiến thức của Kotler c&ograve;n được tiếp nhận v&agrave; &aacute;p dụng rộng r&atilde;i trong kinh doanh to&agrave;n cầu. Philip Kotler nổi tiếng khắp thế giới v&igrave; l&agrave; t&aacute;c giả của hơn 100 cuốn s&aacute;ch v&agrave; b&agrave;i b&aacute;o chuy&ecirc;n về marketing v&agrave; quản trị kinh doanh. Gi&aacute;o sư Kotler c&ograve;n c&oacute; t&aacute;c động t&iacute;ch cực tới nền kinh doanh tr&ecirc;n to&agrave;n thế giới bởi &ocirc;ng phổ biến kh&aacute;i niệm marketing x&atilde; hội, tr&aacute;ch nhiệm x&atilde; hội của marketing v&agrave; gi&uacute;p thế giới x&acirc;y dựng một nền kinh doanh nh&acirc;n bản hơn.</p>\r\n\r\n<p>- 20 chương của cuốn s&aacute;ch l&agrave; một bộ khung to&agrave;n diện với những kiến thức v&ocirc; c&ugrave;ng đầy đủ, chi tiết cho bất cứ ai muốn c&oacute; c&aacute;i nh&igrave;n tổng quan về ng&agrave;nh marketing. S&aacute;ch rất nhiều l&iacute; thuyết nhưng được tr&igrave;nh b&agrave;y dưới b&agrave;n tay v&agrave; khối &oacute;c của &ocirc;ng vua marketing n&ecirc;n cũng giống như một sản phẩm gi&aacute; trị. Đi k&egrave;m với l&iacute; thuyết lu&ocirc;n l&agrave; những v&iacute; dụ minh họa sinh động, dễ hiểu v&agrave; đa g&oacute;c cạnh n&ecirc;n người đọc kh&ocirc;ng hề thấy nh&agrave;m ch&aacute;n.</p>', '1654593772.jpg', NULL, '2022-06-07 09:22:52', '2022-06-07 09:22:52'),
(19, 'Đầu Tư Tài Chính', '659000.00', 50, 3, 'Nhiều Tác Giả', 'NXB Kinh tế TP.Hồ Chí Minh', '<p><strong>Đầu Tư T&agrave;i Ch&iacute;nh</strong></p>\r\n\r\n<p><em>Bạn muốn được tự do, bạn muốn thoải m&aacute;i trong cuộc sống thường nhật v&agrave; kh&ocirc;ng phải trả lời bất cứ ai!</em></p>\r\n\r\n<p><em>Bạn khao kh&aacute;t c&oacute; nhiều thời gian hơn ở b&ecirc;n cạnh những người th&acirc;n y&ecirc;u của m&igrave;nh m&agrave; vẫn c&oacute; thu nhập để nu&ocirc;i sống gia đ&igrave;nh!</em></p>\r\n\r\n<p><em>Đầu tư t&agrave;i ch&iacute;nh gi&uacute;p bạn ho&agrave;n th&agrave;nh giấc mơ tự do t&agrave;i ch&iacute;nh đ&oacute;, nghĩa l&agrave; bạn kh&ocirc;ng hề c&oacute; cấp tr&ecirc;n, tho&aacute;i m&aacute;i đi du lịch v&agrave; l&agrave;m việc khắp nơi tr&ecirc;n thế giới!</em></p>\r\n\r\n<p><em>Đ&oacute; l&agrave; cuộc sống của mộtnh&agrave; đầu tư t&agrave;i ch&iacute;nh th&agrave;nh c&ocirc;ng.</em></p>\r\n\r\n<p>Để trở th&agrave;nhnh&agrave; đầu tư t&agrave;i ch&iacute;nh th&agrave;nh c&ocirc;ng, bạn phải trả qua một qu&aacute; tr&igrave;nh học tập l&acirc;u d&agrave;i. Đầu tư cũng giống như học nghề b&aacute;c sĩ, kỹ sư, gi&aacute;o vi&ecirc;n,... lu&ocirc;n cần phải c&oacute; thời gian để nghi&ecirc;n cứu học hỏi. Nếu nhưquy luật 10,000 giờđ&uacute;ng với nhiều lĩnh vực, đ&acirc;y cũng cũng c&oacute; thể l&agrave; khoảng thời gian cần thiết để một nh&agrave; đầu tư phải tự học tập v&agrave; r&egrave;n luyện mới c&oacute; thể tự do t&agrave;i ch&iacute;nh. Quyển s&aacute;ch m&agrave; SG Trading sắp giới thiệu đến c&aacute;c bạn, rất đặc biệt ở chỗ n&oacute; gi&uacute;p bạntiết kiệm thời gian rất nhiều để đi đến th&agrave;nh c&ocirc;ngtrong lĩnh vực t&agrave;i ch&iacute;nh, kh&ocirc;ng chỉ đơn thuần l&agrave; 1 quyển s&aacute;ch, v&igrave; mục đ&iacute;ch cuối c&ugrave;ng l&agrave; gi&uacute;p bạn đọc c&oacute; thể kiếm sống được bằng nghề giao dịch v&agrave; đầu tư t&agrave;i ch&iacute;nh, tự khẳng định m&igrave;nh bằng ch&iacute;nh năng lực tư duy giao dịch t&agrave;i ch&iacute;nh.</p>\r\n\r\n<p>Quyển s&aacute;ch<em>Đầu Tư T&agrave;i Ch&iacute;nh</em>được bi&ecirc;n dịch từ t&aacute;c phẩm &quot;&quot;Investment&quot;&quot;, l&agrave; một t&aacute;c phẩm kinh điển về đầu tư t&agrave;i ch&iacute;nh, được bi&ecirc;n soạn dựa theo chương tr&igrave;nh học cho c&aacute;c nh&agrave; đầu tư t&agrave;i ch&iacute;nh chuy&ecirc;n nghiệp trong c&aacute;c c&ocirc;ng ty quỹ đầu tư, định chế t&agrave;i ch&iacute;nh lớn đ&oacute; l&agrave; chứng chỉ uy t&iacute;n<em>CFA (Chartered Financial Analysis)</em>, nhưng với c&aacute;ch tiếp cận gần gũi cho mọi nh&agrave; đầu tư v&agrave; mang t&iacute;nh thực chiến ngay từ đầu. S&aacute;ch l&agrave; một t&agrave;i liệu hữu &iacute;ch, hỗ trợ đắc lực trong qu&aacute; tr&igrave;nh t&igrave;m kiếm lợi nhuận từ thị trường t&agrave;i ch&iacute;nh của bạn. Nếu bạn đang t&igrave;m kiếm một quyển s&aacute;ch đầy đủ nhất về T&agrave;i Ch&iacute;nh, SG Trading nghĩ đ&acirc;y l&agrave; quyển s&aacute;ch bạn đang cần, b&ecirc;n cạnh những cuốn s&aacute;ch về T&agrave;i ch&iacute;nh m&agrave; SG Trading đ&atilde; giới thiệu trước đ&oacute;, v&igrave; n&oacute; bao gồm đầy đủ nhất c&aacute;c kiến thức thực chiến về T&agrave;i ch&iacute;nh như<em>Ph&acirc;n t&iacute;ch kĩ thuật, chiến lược giao dịch ph&aacute;i sinh, quyền chọn, tr&aacute;i phiếu, t&acirc;m l&yacute; giao dịch, t&agrave;i ch&iacute;nh h&agrave;nh vi, ph&acirc;n t&iacute;ch b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh, định gi&aacute; cổ phiếu,....</em></p>\r\n\r\n<p>Sẽ mất nhiều giờ để đọc quyển s&aacute;ch n&agrave;y, v&igrave; đ&ograve;i hỏi người đọc cần ki&ecirc;n tr&igrave; luyện tập, ch&uacute;ng t&ocirc;i khuyến nghị c&aacute;c bạn khi đọc được những hệ thống v&agrave; chiến lược đầu tư. Bạn đọc n&ecirc;n kiểm tra lại (backtest) dữ liệu tr&ecirc;n ch&iacute;nh thị trường m&agrave; bạn đang đầu tư cũng như hoạt động giao dịch của bạn. Bạn sẽ thu được kiến thức cho ri&ecirc;ng m&igrave;nh chỉ khi tự đặt ra c&aacute;c c&acirc;u hỏi v&agrave; kiểm nghiệm ch&uacute;ng. Luyện tập cho đến khi nhuần nhuyễn kĩ năng n&agrave;y sẽgi&uacute;p bạn kiếm sống suốt đời.</p>\r\n\r\n<p>Quyển s&aacute;ch<em>Đầu Tư T&agrave;i Ch&iacute;nh</em>sẽ cung cấp cho bạn đầy đủ kiến thức, để từ đ&oacute; bạn c&oacute; thể bắt đầu chọn cho m&igrave;nh một triết l&iacute; ri&ecirc;ng về đầu tư, b&agrave;i viết dưới đ&acirc;y<em>Warren Buffet</em>cho thấy tại sao &ocirc;ng l&agrave; một nh&agrave; đầu tư t&agrave;i ch&iacute;nh vĩ đại, v&igrave; &ocirc;ng nắm rất r&otilde; những kiến thức t&agrave;i ch&iacute;nh, r&otilde; đến từng kh&aacute;i niệm đơn giản v&agrave; từ đ&oacute; ph&aacute;t triển th&agrave;nh c&aacute;c chiến lược đầu tư bậc thầy, mời bạn đọc tham khảo:</p>\r\n\r\n<p>ĐẦU TƯ T&Agrave;I CH&Iacute;NH D&Agrave;I HẠN THEO PHONG C&Aacute;CH CỦA WARREN BUFFETT</p>\r\n\r\n<p>C&oacute; nhiều c&aacute;ch kh&aacute;c nhau để định nghĩa đượcđầu tư t&agrave;i ch&iacute;nh d&agrave;i hạn l&agrave; g&igrave;. Tuy nhi&ecirc;n c&oacute; một kh&aacute;i niệm chung đều được thừa nhận như sau:<em>&ldquo;Đầu tư t&agrave;i ch&iacute;nh d&agrave;i hạn c&oacute; thể được hiểu l&agrave; việc đầu tư v&agrave;o chứng kho&aacute;n trong thời gian tr&ecirc;n một năm v&agrave; c&oacute; thể k&eacute;o d&agrave;i đến h&agrave;ng chục năm với mục đ&iacute;ch mang lại lợi nhuận l&acirc;u d&agrave;i để phục vụ cho tương lai.&rdquo;</em></p>\r\n\r\n<p>Lợi &iacute;ch ở đ&acirc;y c&oacute; thể được hiểu l&agrave; phục vụ cho những nhu cầu của bản th&acirc;n nhưng về l&acirc;u d&agrave;i như việc nghỉ hưu hay tương lai con c&aacute;i. Việcđầu tư t&agrave;i ch&iacute;nh d&agrave;i hạnlu&ocirc;n được đ&aacute;nh gi&aacute; cao v&agrave; l&agrave; thước đo cho l&ograve;ng ki&ecirc;n nhẫn của mỗi người. Nếu bạn c&ograve;n trẻ, bạn muốn đầu tư cho tương lai con c&aacute;i sau n&agrave;y, th&igrave; việcđầu tư t&agrave;i ch&iacute;nh d&agrave;i hạnsẽ l&agrave; một quyết định s&aacute;ng suốt v&agrave; đ&uacute;ng đắn.</p>\r\n\r\n<p>Việc đầu tư t&agrave;i ch&iacute;nh d&agrave;i hạn bao gồm những g&igrave;? Đ&oacute; c&oacute; thể l&agrave; mua v&agrave; nắm giữ l&acirc;u d&agrave;i một cổ phiếu, hay c&oacute; thể th&acirc;u t&oacute;m cả một doanh nghiệp (M&amp;A). Warren Buffett l&agrave; một trong những nh&agrave; đầu tư theo trường ph&aacute;i đầu tư d&agrave;i hạn th&agrave;nh c&ocirc;ng nhất hiện nay. Một trong những kh&iacute;a cạnh hấp dẫn của việc đầu tư t&agrave;i ch&iacute;nh d&agrave;i hạn l&agrave; Buffett đ&atilde; c&oacute; thấy được t&iacute;nh linh hoạt cũng như những th&agrave;nh c&ocirc;ng phi thường m&agrave; n&oacute; mang lại v&agrave; &ocirc;ng ch&iacute;nh l&agrave; một minh chứng tốt nhất.</p>\r\n\r\n<p>C&aacute;c nh&agrave; kinh doanh sẽ c&oacute; thể đi theo những nguy&ecirc;n tắc cứng rắn v&agrave; theo những c&ocirc;ng thức c&oacute; sẵn nhưng điều l&agrave;m n&ecirc;n th&agrave;nh c&ocirc;ng của việc đầu tư t&agrave;i ch&iacute;nh d&agrave;i hạn l&agrave; việc th&iacute;ch nghi v&agrave; vượt qua được những con s&oacute;ng ngắn hạn của thị trường chứng kho&aacute;n.</p>\r\n\r\n<p>Để học theo c&aacute;ch đầu tư của&ldquo;nh&agrave; hiền triết xứ Ohama&rdquo;ch&uacute;ng ta cần phải lưu &yacute; đến những điều sau</p>\r\n\r\n<p><strong>Đ&aacute;nh gi&aacute; chất lượng quản l&yacute;</strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; điểm kh&oacute; nhất khi ph&acirc;n t&iacute;ch đầu tư v&agrave;o một cổ phiếu n&agrave;o đ&oacute;. Việc quản l&yacute; kh&ocirc;n ngoan li&ecirc;n quan đến việc trả cổ tức cho cổ đ&ocirc;ng v&agrave; t&aacute;i đầu tư. Đ&acirc;y l&agrave; một c&acirc;u hỏi s&acirc;u sắc, c&oacute; ảnh hưởng đến việc đầu tư l&acirc;u d&agrave;i hay kh&ocirc;ng. Tr&ecirc;n thực tế chứng minh rằng, đ&aacute;m đ&ocirc;ng lu&ocirc;n c&oacute; xu hướng tham lam v&agrave; mong muốn giữ được lợi nhuận c&aacute; nh&acirc;n c&agrave;ng nhiều c&agrave;ng tốt. Họ mong muốn x&acirc;y dựng đế chế hơn l&agrave; sử dụng d&ograve;ng vốn để tối đa h&oacute;a gi&aacute; trị của cổ đ&ocirc;ng. V&agrave; vấn đề của họ l&agrave; họ c&oacute; chấp nhận sai lầm của bản th&acirc;n hay kh&ocirc;ng?</p>\r\n\r\n<p>Việc quản l&yacute; kh&ocirc;ng chỉ thể hiện qua ban l&atilde;nh đạo m&agrave; c&ograve;n th&ocirc;ng qua cả c&aacute;c nh&acirc;n vi&ecirc;n, c&aacute;ch họ cống hiến cho doanh nghiệp. Ch&uacute;ng ta chỉ c&oacute; thể thấy một doanh nghiệp ph&aacute;t triển tốt kh&ocirc;ng chỉ th&ocirc;ng qua những b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh m&agrave; c&ograve;n phải qua c&aacute;ch quản trị doanh nghiệp. Đ&acirc;y c&oacute; thể l&agrave; một trong những bước chưa được ch&uacute; &yacute; nhiều khi nghi&ecirc;n cứu đểđầu tư t&agrave;i ch&iacute;nh d&agrave;i hạnv&agrave;o c&aacute;c doanh nghiệp. N&oacute; đ&ograve;i hỏi sự suy ngẫm cũng như nghi&ecirc;n cứu l&acirc;u d&agrave;i nhất c&oacute; thể.</p>\r\n\r\n<p><strong>Thước đo t&agrave;i ch&iacute;nh doanh nghiệp</strong></p>\r\n\r\n<p>Nếu bạn t&iacute;nh t&agrave;i ch&iacute;nh doanh nghiệp dựa tr&ecirc;n những th&ocirc;ng số cơ bản l&agrave; bạn đ&atilde; c&oacute; thể đạt được những th&agrave;nh quả đầu ti&ecirc;n của việc đầu tư t&agrave;i ch&iacute;nh d&agrave;i hạn. C&aacute;ch t&iacute;nh to&aacute;n n&agrave;y cũng kh&ocirc;ng phải qu&aacute; kh&oacute;, Buffett đưa ra những ph&eacute;p t&iacute;nh dựa tr&ecirc;n vốn chủ sở hữu (ROE) . ROE th&igrave; kh&aacute;c với ROC (tỷ số lợi nhuận tr&ecirc;n vốn đầu tư) hay ROA (lợi nhuận tr&ecirc;n tổng t&agrave;i sản) hay ROCE (lợi nhuận tr&ecirc;n vốn vay). Buffett sẽ loại bỏ c&aacute;c doanh nghiệp mang nợ hơn l&agrave; t&igrave;m kiếm mức lợi nhuận cao ở mức bất hợp l&yacute;.</p>\r\n\r\n<p>Buffett xem x&eacute;t t&agrave;i ch&iacute;nh doanh nghiệp dựa tr&ecirc;n c&aacute;i m&agrave; &ocirc;ng gọi l&agrave;<em>&ldquo;thu thập của chủ sở hữu&rdquo;</em>. Về thực chất, đ&acirc;y l&agrave; thu thập c&oacute; sẵn của c&aacute;c cổ đ&ocirc;ng v&agrave; n&oacute; th&igrave; kh&ocirc;ng c&oacute; li&ecirc;n quan g&igrave; tới thu nhập từ cổ phần. N&oacute; được định nghĩa từ l&atilde;i r&ograve;ng th&ecirc;m phần gi&aacute; trị khấu hao v&agrave; phần nợ trả dần trừ đi vốn đầu tư d&agrave;i hạn, vốn chi ph&iacute; ph&aacute;t sinh.</p>\r\n\r\n<p>Buffett chỉ ra rằng &ldquo;năng lực điều h&agrave;nh đồng vốn cổ đ&ocirc;ng ch&iacute;nh l&agrave; giới chủ&rdquo;.</p>\r\n\r\n<p><strong>Gi&aacute; trị doanh nghiệp</strong></p>\r\n\r\n<p>Thước đo cuối c&ugrave;ng được Buffett đặt ra l&agrave; t&iacute;nh to&aacute;n gi&aacute; trị thực của một c&ocirc;ng ty. Thu thập của chủ sở hữu c&ocirc;ng ty sẽ chiết khấu trở lại tới gi&aacute; trị hiện tại của ch&uacute;ng. Buffett cũng đưa ra kh&aacute;i niệm về &ldquo;r&agrave;o chắn kinh tế&rdquo;. Đ&acirc;y l&agrave; điều đem lại cho c&aacute;c c&ocirc;ng ty một lợi thế r&otilde; rệt hơn so với đối thủ kh&aacute;c v&agrave; bảo vệ n&oacute; trước những sự tấn c&ocirc;ng từ những đối thủ cạnh tranh. C&oacute; thể l&agrave; hơi ti&ecirc;u cực khi m&agrave; Buffett c&oacute; thể khấu trừ từ thu nhập được dự t&iacute;nh đến mức tỉ lệ rủi ro gần như bằng 0. &Ocirc;ng thực hiện bằng c&aacute;ch ki&ecirc;n định những &yacute; kiến của m&igrave;nh với nguy&ecirc;n tắc &ldquo;lợi nhuận an to&agrave;n&rdquo; trong khi &aacute;p dụng cả những nguy&ecirc;n tắc với h&agrave;m &yacute; giảm thiểu tối đa những rủi ro c&oacute; thể xảy ra.</p>\r\n\r\n<p>Những nguy&ecirc;n l&yacute; n&agrave;y của Buffett đ&atilde; tạo th&agrave;nh nền tảng vững chắc cho việcđầu tư t&agrave;i ch&iacute;nh d&agrave;i hạnvới t&ecirc;n gọi<em>&ldquo;thuyết đầu tư gi&aacute; trị&rdquo;.</em>Học thuyết n&agrave;y rộng đến mức n&oacute; đ&ograve;i hỏi sử bổ sung bằng trị thức trong tương lai v&agrave; trải nghiệm, ph&acirc;n t&iacute;ch s&acirc;u d&agrave;y. Việc tu&acirc;n theo những học thuyết n&agrave;y đ&ograve;i hỏi những nh&agrave; đầu tư kh&ocirc;ng chỉ đơn giản coiđầu tư t&agrave;i ch&iacute;nh d&agrave;i hạnl&agrave; một cuộc chơi, m&agrave; phải thật sự coi n&oacute; l&agrave; một c&ocirc;ng việc phải đổ to&agrave;n bộ t&acirc;m huyết của m&igrave;nh v&agrave;o ch&uacute;ng.</p>', '1654594069.jpg', NULL, '2022-06-07 09:27:49', '2022-06-07 09:27:49'),
(20, 'Hướng Dẫn Giao Dịch Theo Sóng Elliott', '369000.00', 50, 3, 'Wayne Gorman, Jeffrey Kennedy', 'NXB Thế Giới', '<p><br />\r\n<strong>HƯỚNG DẪN GIAO DỊCH THEO S&Oacute;NG ELLIOTT</strong></p>\r\n\r\n<p><em><strong>Hướng Dẫn Giao Dịch Theo S&oacute;ng Elliott</strong></em>&nbsp;l&agrave; cuốn s&aacute;ch phải đọc d&agrave;nh cho những ai muốn biết c&aacute;ch thức giao dịch theo Nguy&ecirc;n L&yacute; S&oacute;ng Elliott. Bạn sẽ biết l&agrave;m thế n&agrave;o để t&igrave;m ra c&aacute;c cơ hội giao dịch tốt, đ&aacute;nh gi&aacute; chất lượng từng t&iacute;n hiệu giao dịch, c&aacute;ch mở vị thế, quản trị giao dịch bằng c&aacute;ch n&acirc;ng v&agrave; &nbsp;hạ lệnh dừng lỗ, cũng như c&aacute;ch đ&oacute;ng lệnh hợp l&yacute; nhất.</p>\r\n\r\n<p>Cuốn s&aacute;ch<em><strong>&nbsp;Hướng Dẫn Giao Dịch Theo S&oacute;ng Elliott&nbsp;</strong></em>giả định bạn đọc l&agrave; những người đ&atilde; quen thuộc với Nguy&ecirc;n L&yacute; S&oacute;ng Elliott v&agrave; c&aacute;ch thức ứng dụng l&yacute; thuyết n&agrave;y. Giống như nhiều cuốn s&aacute;ch dạy chiến thuật đ&aacute;nh cờ tướng (lu&ocirc;n giả định bạn phải c&oacute; những kiến thức cơ bản về c&aacute;ch chơi cờ), cuốn s&aacute;ch n&agrave;y cũng giả định bạn đọc đ&atilde; nắm sơ lược c&aacute;c kiến thức cơ bản về một số mẫu h&igrave;nh S&oacute;ng Elliott v&agrave; l&agrave;m thế n&agrave;o để kết hợp ch&uacute;ng lại với nhau.</p>\r\n\r\n<p>Nếu bạn l&agrave; một nh&agrave; ph&acirc;n t&iacute;ch v&agrave; giao dịch s&oacute;ng Elliott đầy kinh nghiệm v&agrave; cần một bản t&oacute;m lược đơn giản, đầy đủ, x&uacute;c t&iacute;ch về Nguy&ecirc;n L&yacute; S&oacute;ng, th&igrave; Phụ Lục Nguy&ecirc;n L&yacute; S&oacute;ng Elliott đ&aacute;p ứng tất cả c&aacute;c y&ecirc;u c&acirc;u n&agrave;y.</p>\r\n\r\n<p>Nếu bạn l&agrave; một người chưa hề biết g&igrave; về Nguy&ecirc;n L&yacute; S&oacute;ng Elliott hoặc bạn muốn nghi&ecirc;n cứu chuy&ecirc;n s&acirc;u, ch&uacute;ng t&ocirc;i đề nghị bạn đọc n&ecirc;n tham khảo c&aacute;c t&agrave;i liệu đ&iacute;nh k&egrave;m trong cuốn s&aacute;ch n&agrave;y v&agrave; truy cập tới phi&ecirc;n bản miễn ph&iacute; cuốn s&aacute;ch kinh điển tr&ecirc;n phố Wall: &ldquo;Elliott Wave Principle (Nguy&ecirc;n L&yacute; S&oacute;ng Elliott)&rdquo; của Frost v&agrave; Prechter.&nbsp;</p>\r\n\r\n<p>Cả hai ch&uacute;ng t&ocirc;i đ&atilde; v&agrave; đang giao dịch kiếm sống bằng kỷ luật tu&acirc;n thủ Nguy&ecirc;n L&yacute; S&oacute;ng Elliott.&nbsp;<em><strong>Hướng Dẫn Giao Dịch Theo S&oacute;ng Elliott</strong></em>&nbsp;l&agrave; một h&agrave;nh tr&igrave;nh trải nghiệm của ch&uacute;ng t&ocirc;i khi &aacute;p dụng Nguy&ecirc;n L&yacute; S&oacute;ng Elliott v&agrave;o thực tiễn: Ch&uacute;ng t&ocirc;i nh&igrave;n thấy điều g&igrave;, ch&uacute;ng t&ocirc;i lờ đi điều g&igrave;, ch&uacute;ng t&ocirc;i đ&atilde; l&agrave;m đ&uacute;ng c&aacute;i g&igrave;, v&agrave; ch&uacute;ng t&ocirc;i đ&atilde; sai lầm như thế n&agrave;o.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i kh&ocirc;ng muốn tr&igrave;nh b&agrave;y những v&iacute; dụ ho&agrave;n hảo khiến bạn ngộ nhận rằng, bạn c&oacute; thể giao dịch l&agrave;m gi&agrave;u chỉ trong v&ograve;ng 30 ph&uacute;t v&agrave; d&agrave;nh phần lớn thời gian trong ng&agrave;y để chơi golf. Thay v&agrave;o đ&oacute;, ch&uacute;ng t&ocirc;i cố gắng tạo ra một cuốn s&aacute;ch hướng dẫn giao dịch giống với thực tế nhất. V&igrave; thế, ch&uacute;ng t&ocirc;i sẽ ph&ocirc; b&agrave;y tất cả những sai lầm, những điều kh&ocirc;ng ho&agrave;n hảo cho từng giao dịch, để cho bạn đọc hiểu rằng, c&oacute; nhiều c&aacute;ch giao dịch th&agrave;nh c&ocirc;ng khi tu&acirc;n thủ kỷ luật t&iacute;n hiệu giao dịch theo s&oacute;ng Elliott.</p>\r\n\r\n<p>Nếu bạn muốn giao dịch kiếm sống bằng ch&iacute;nh đồng vốn của m&igrave;nh, nếu bạn muốn trở th&agrave;nh một nh&agrave; giao dịch th&agrave;nh c&ocirc;ng, nếu bạn muốn chỉ giao dịch với những t&iacute;n hiệu tốt nhất, hoặc thậm ch&iacute; rất đơn giản, nếu mục ti&ecirc;u của bạn chỉ l&agrave; &quot;kh&ocirc;ng để mất tiền&quot; khi tham gia v&agrave;o thị trường t&agrave;i ch&iacute;nh.&nbsp;Giao dịch th&agrave;nh c&ocirc;ng đ&ograve;i hỏi phải l&agrave;m việc nghi&ecirc;m t&uacute;c, t&iacute;nh kỷ luật v&agrave; cả sự nhanh nhạy. Nhưng ngay cả khi c&oacute; được ba điều tr&ecirc;n, bạn cũng chỉ mới tiến gần đến th&agrave;nh c&ocirc;ng m&agrave; th&ocirc;i. Điều cuối c&ugrave;ng gi&uacute;p bạn c&oacute; được th&agrave;nh c&ocirc;ng bền vững ch&iacute;nh l&agrave; tri thức. V&agrave; đ&acirc;y l&agrave; điều cuốn s&aacute;ch mang lại.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i hy vọng bạn đọc sẽ y&ecirc;u th&iacute;ch cuốn s&aacute;ch&nbsp;<em><strong>Hướng Dẫn Giao Dịch Theo S&oacute;ng Elliott</strong></em>.&nbsp;</p>\r\n\r\n<p>Wayne Gorman v&agrave; Jeffrey Kennedy</p>', '1654594155.jpg', NULL, '2022-06-07 09:29:15', '2022-06-07 09:29:15'),
(21, '90 - 20 - 30 - 90 Bài Học Vỡ Lòng Về Ý Tưởng Và Câu Chữ', '159900.00', 50, 3, 'Huỳnh Vĩnh Sơn', 'NXB Lao Động', '<p><br />\r\n<strong>90 - 20 - 30 - 90 B&agrave;i Học Vỡ L&ograve;ng Về &Yacute; Tưởng V&agrave; C&acirc;u Chữ</strong></p>\r\n\r\n<p>NG&Agrave;NH QUẢNG C&Aacute;O QU&Aacute; MAY MẮN. L&uacute;c n&agrave;o cũng lung linh trong mắt c&aacute;c bạn trẻ y&ecirc;u s&aacute;ng tạo. Ai cũng muốn ra trường sẽ hiến bộ n&atilde;o đầy ắp &yacute; tưởng cho một c&ocirc;ng ty quảng c&aacute;o chất chơi n&agrave;o đ&oacute;.</p>\r\n\r\n<p>Nhưng dẫu c&oacute; được chuẩn bị kĩ c&agrave;ng, gi&aacute;o tr&igrave;nh quốc tế 5 sao đi nữa th&igrave; n&agrave;ng (Quảng) C&aacute;o l&uacute;c n&agrave;o cũng ki&ecirc;u k&igrave;. Ở trường th&igrave; học h&agrave;nh, v&agrave;o c&ocirc;ng ty th&igrave; vừa h&agrave;nh vừa học. Để c&oacute; &yacute; tưởng v&agrave; c&acirc;u chữ x&agrave;i được, c&aacute;c &ldquo;mầm non s&aacute;ng tạo&rdquo; phải va chạm li&ecirc;n tục v&agrave; tự m&igrave;nh vỡ lấy những b&agrave;i học, c&agrave;ng nhanh c&agrave;ng tốt, kh&ocirc;ng được để deadline qua mặt.</p>\r\n\r\n<p>V&agrave; người trực tiếp chăn dắt &ldquo;bọn trẻ&rdquo; đi qua những ng&agrave;y ban sơ ấy l&agrave; &ldquo;sinh vật&rdquo; mang t&ecirc;n &ldquo;mentor/ supervisor/ senior creative &ndash; &ldquo;Sếp&rdquo; trực tiếp, nh&acirc;n vi&ecirc;n s&aacute;ng tạo l&agrave;m kha kh&aacute; năm.</p>\r\n\r\n<p>Mời bạn đến với h&agrave;nh tr&igrave;nh 90 ng&agrave;y &ndash; tương đương 3 th&aacute;ng thực tập của c&ocirc; n&agrave;ng Copywriter vừa tr&ograve;n 20, dưới trướng một l&atilde;o Senior Copywriter vừa chấm 30.</p>\r\n\r\n<p>20 vừa bước ch&acirc;n ra khỏi giảng đường đại học, bắt đầu đối mặt với những thử th&aacute;ch thực sự.<br />\r\n30 vừa bước sang năm h&agrave;nh nghề thứ 9, bắt đầu đối mặt với &aacute;p lực dẫn dắt đ&agrave;n em.<br />\r\nHai con người xa ơi l&agrave; xa nhưng gặp nhau nơi niềm vui s&aacute;ng tạo, chiến đấu c&ugrave;ng nhau tại một Agency quảng c&aacute;o.<br />\r\nMỗi ng&agrave;y 20 v&agrave; 30 lại &ldquo;quần nhau&rdquo; v&agrave; mang về những b&agrave;i học của ri&ecirc;ng m&igrave;nh.</p>\r\n\r\n<p>20 th&igrave; cặm cụi g&otilde; l&ecirc;n tr&ecirc;n blog, 30 th&igrave; h&iacute; ho&aacute;y viết từng trang.</p>\r\n\r\n<p>C&ograve;n bạn th&igrave; ung dung cầm trọn những d&ograve;ng t&acirc;m tư ấy tr&ecirc;n tay.</p>\r\n\r\n<p>Đ&uacute;c kết những kinh nghiệm xương m&aacute;u trong suốt 9 năm h&agrave;nh nghề Copywriter,&nbsp;<strong>90 - 20 - 30 - 90 b&agrave;i học vỡ l&ograve;ng về &yacute; tưởng v&agrave; c&acirc;u chữ&nbsp;</strong>của&nbsp;<strong>t&aacute;c giả Huỳnh Vĩnh Sơn</strong>&nbsp;sẽ gi&uacute;p bạn:</p>\r\n\r\n<p>- Bật ra-đa s&aacute;ng tạo, nắm bắt cuộc sống m&agrave;u sắc v&agrave; đa chiều hơn</p>\r\n\r\n<p>- &Yacute; thức hơn về tổ chức c&acirc;u chữ, l&agrave;m tiền đề cho việc chơi chữ, viết th&ocirc;ng điệp, s&aacute;ng tạo slogan cho thương hiệu</p>\r\n\r\n<p>- Tư duy hệ thống, viết logic v&agrave; đ&uacute;ng đề b&agrave;i</p>\r\n\r\n<p>V&agrave; c&ograve;n rất nhiều những b&agrave;i học hữu &iacute;ch kh&aacute;c đang chờ bạn kh&aacute;m ph&aacute; trong h&agrave;nh tr&igrave;nh 90 - 20 - 30.</p>', '1654594221.jpg', NULL, '2022-06-07 09:30:21', '2022-06-07 09:30:21');
INSERT INTO `books` (`id`, `name`, `giaban`, `soluong`, `category_id`, `tacgia`, `nxb`, `gioithieu`, `hinhanh`, `taiban`, `created_at`, `updated_at`) VALUES
(22, 'Điệp Viên Hoàn Hảo X6', '134400.00', 50, 4, 'Larry Berman', 'NXB Hồng Đức', '<p><br />\r\nCuộc đời hai mặt phi thường của PHẠM XU&Acirc;N ẨN Ph&oacute;ng vi&ecirc;n Reuters, Time, New York Herald Tribune&hellip; &amp;Tướng T&igrave;nh B&aacute;o Chiến Lược Việt Nam V&Agrave; C&Ocirc;NG BỐ TRAO BẢN QUYỀN &ldquo;ĐIỆP VI&Ecirc;N HO&Agrave;N HẢO X6&rdquo; CHO FIRST NEWS - TRI VIỆT -Phạm Xu&acirc;n Ẩn, một c&aacute;i t&ecirc;n chứa biết bao điều b&iacute; ẩn, kh&ocirc;ng chỉ với người Việt Nam, m&agrave; đối với cả rất nhiều ch&iacute;nh kh&aacute;ch v&agrave; b&aacute;o ch&iacute; thế giới, đặc biệt l&agrave; b&aacute;o ch&iacute; Mỹ. Người ta đ&atilde; b&igrave;nh luận, tranh c&atilde;i, phỏng đo&aacute;n v&agrave; viết rất nhiều về &ocirc;ng, thậm ch&iacute; c&ograve;n l&agrave;m phim t&agrave;i liệu nhiều tập về &ocirc;ng, nhưng dường như c&ograve;n c&oacute; qu&aacute; nhiều điều vẫn chưa được l&agrave;m s&aacute;ng tỏ. Cho đến khi một người Mỹ bắt tay v&agrave;o cuộc&hellip; Đ&oacute; ch&iacute;nh l&agrave; Larry Berman, nh&agrave; sử học, gi&aacute;o sư ch&iacute;nh trị học thuộc đại học California, một chuy&ecirc;n gia xuất sắc chuy&ecirc;n nghi&ecirc;n cứu về cuộc chiến tranh x&acirc;m lược Việt Nam của Mỹ.</p>\r\n\r\n<p>Năm 2007, quyển s&aacute;ch Perfect Spy - The Incredible Double Life of PHAM XUAN AN Time Magazine Reporter &amp; Vietnamese Communist Agent (Điệp vi&ecirc;n ho&agrave;n hảo &ndash; Hai cuộc đời kh&oacute; tin (hay cuộc đời hai mặt theo c&aacute;ch gọi của Phương T&acirc;y về&nbsp; &nbsp;Phạm Xu&acirc;n Ẩn, ph&oacute;ng vi&ecirc;n tạp ch&iacute; Reuters, Time v&agrave; tướng t&igrave;nh b&aacute;o Cộng sản Việt Nam)) của Larry Berman được xuất bản lần đầu ti&ecirc;n tại Mỹ đ&atilde; g&acirc;y chấn động dư luận Mỹ, kiều b&agrave;o Việt Nam ở Mỹ v&agrave; cả ở Việt Nam. V&igrave; trước khi quyển s&aacute;ch n&agrave;y ra đời, rất &iacute;t người biết v&agrave; hiểu về tướng t&igrave;nh b&aacute;o Phạm Xu&acirc;n Ẩn, mặc d&ugrave; &ocirc;ng l&agrave; một nh&acirc;n vật huyền thoại của chiến tranh Việt Nam. C&oacute; lẽ bởi v&igrave; cuộc đời của Phạm Xu&acirc;n Ẩn, cũng giống như t&ecirc;n của &ocirc;ng, chứa đựng nhiều b&iacute; ẩn về con người v&agrave; sự nghiệp của một nh&agrave; t&igrave;nh b&aacute;o vĩ đại, t&agrave;i năng v&agrave; bản lĩnh nhưng lại v&ocirc; c&ugrave;ng khi&ecirc;m nhường, b&igrave;nh dị.</p>\r\n\r\n<p>Phạm Xu&acirc;n Ẩn (sinh ng&agrave;y 12/9/1927, mất ng&agrave;y 20/9/2006) tham gia hoạt động c&aacute;ch mạng từ đầu thập ni&ecirc;n 1950, năm 1953 được kết nạp Đảng v&agrave; được giao nhiệm vụ hoạt động điệp b&aacute;o. Nhằm tạo vỏ bọc tốt hơn để c&oacute; thể th&acirc;m nhập s&acirc;u hơn v&agrave;o giới chức ch&iacute;nh quyền v&agrave; qu&acirc;n đội S&agrave;i G&ograve;n, năm 1957 &ocirc;ng được cấp tr&ecirc;n bố tr&iacute; cho sang Mỹ học ng&agrave;nh b&aacute;o ch&iacute;. Năm 1959, Phạm Xu&acirc;n Ẩn về nước, l&agrave;m việc cho H&atilde;ng tin Reuters v&agrave; sau đ&oacute; l&agrave; tạp ch&iacute; h&agrave;ng đầu Time, New York Herald Tribune&nbsp;của Mỹ. Với kiến thức uy&ecirc;n b&aacute;c, hiểu biết rộng, cương trực v&agrave; t&agrave;i năng giao tiếp, ngoại giao kh&aacute;c biệt, độc đ&aacute;o theo kiểu l&atilde;ng tử, h&agrave;o hoa, ngang t&agrave;ng, &ldquo;chửi thề như bắp rang&rdquo;, xuất hiện với phong c&aacute;ch thượng lưu, thừa hưởng văn h&oacute;a được đ&agrave;o tạo ch&iacute;nh qui từ Mỹ, &ocirc;ng đ&atilde; th&acirc;m nhập v&agrave; l&agrave; bạn tri kỷ với c&aacute;c tướng lĩnh, tr&ugrave;m an ninh mật vụ cả Mỹ v&agrave; S&agrave;i G&ograve;n, giới b&aacute;o ch&iacute; Việt Nam Cộng H&ograve;a v&agrave; Mỹ cũng như c&aacute;c ch&iacute;nh kh&aacute;ch ch&oacute;p bu của ch&iacute;nh quyền S&agrave;i G&ograve;n để khai th&aacute;c những th&ocirc;ng tin tuyệt mật mang tầm chiến lược cho cuộc đối đầu của Bắc Việt Nam với Mỹ v&agrave; ch&iacute;nh quyền S&agrave;i G&ograve;n.</p>\r\n\r\n<p>Cuộc đời hoạt động t&igrave;nh b&aacute;o đầy ly kỳ hấp dẫn nhưng cũng v&ocirc; c&ugrave;ng hiểm nguy của Phạm Xu&acirc;n Ẩn với b&iacute; danh X6 thuộc cụm t&igrave;nh b&aacute;o H63 được thể hiện một c&aacute;ch trung thực, sinh động trong cuốn Điệp vi&ecirc;n ho&agrave;n hảo X6 &nbsp;được bổ sung cập nhật mới. Sau 6 năm, cuốn s&aacute;ch với những th&ocirc;ng tin c&oacute; gi&aacute; trị lịch sử giờ đ&acirc;y lại được ra mắt bạn đọc qua một bản dịch đầy đủ v&agrave; cập nhật nhiều th&ocirc;ng tin chưa từng được c&ocirc;ng bố do ch&iacute;nh Phạm Xu&acirc;n Ẩn tiết lộ với t&aacute;c giả Larry Berman c&oacute; ghi &acirc;m.</p>\r\n\r\n<p>Với lời hứa chỉ được c&ocirc;ng bố những th&ocirc;ng tin n&agrave;y từng giai đoạn sau khi Phạm Xu&acirc;n Ẩn mất, Larry Berman đ&atilde; mất 6 năm để c&acirc;n nhắc để từng bước c&ocirc;ng bố th&ocirc;ng tin của người đ&atilde; khuất. Trong ấn bản lần n&agrave;y, ở đầu s&aacute;ch c&oacute; lời cảm nhận của nguy&ecirc;n Ph&oacute; Thủ tướng Trương Vĩnh Trọng, với tựa đề &ldquo;Nhớ m&atilde;i một con người&rdquo;, trong đ&oacute; c&oacute; đoạn: &ldquo;Phạm Xu&acirc;n Ẩn l&agrave; nh&agrave; t&igrave;nh b&aacute;o chiến lược cực kỳ quan trọng. Anh đ&atilde; sống một cuộc đời đầy nhiệt huyết giữa hai chiến tuyến, hai l&agrave;n đạn. Một nh&agrave; b&aacute;o Reuters v&agrave; Time được cả ch&iacute;nh kh&aacute;ch Mỹ nể trọng. Một t&igrave;nh b&aacute;o, một người y&ecirc;u nước thật sự đ&atilde; đ&oacute;ng g&oacute;p rất nhiều cho sự nghiệp giải ph&oacute;ng d&acirc;n tộc với một nh&acirc;n c&aacute;ch đặc biệt, một tấm l&ograve;ng của một người Việt Nam ch&acirc;n ch&iacute;nh. Hiếm c&oacute; một nh&agrave; t&igrave;nh b&aacute;o n&agrave;o tr&ecirc;n thế giới để lại những b&iacute; ẩn v&agrave; t&igrave;nh cảm tốt đẹp cho mọi người từ cả hai ph&iacute;a như nh&agrave; t&igrave;nh b&aacute;o Phạm Xu&acirc;n Ẩn&rdquo;.</p>\r\n\r\n<p>Trong b&agrave;i Một phần của h&igrave;nh h&agrave;i Tổ quốc viết cho ấn bản mới cuốn Điệp vi&ecirc;n ho&agrave;n hảo, &ocirc;ng Đ&agrave;o Văn Lừng, Vụ trưởng, Trưởng cơ quan Thường trực Ban Tuy&ecirc;n gi&aacute;o Trung ương tại Th&agrave;nh phố Hồ Ch&iacute; Minh khẳng định: &ldquo;Thiếu tướng-Anh h&ugrave;ng LLVT - đ&oacute; l&agrave; sự vinh danh của Tổ quốc d&agrave;nh cho nh&agrave; t&igrave;nh b&aacute;o chiến lược Phạm Xu&acirc;n Ẩn. Nhưng t&ecirc;n tuổi của &Ocirc;ng c&ograve;n đi xa hơn thế, đặc biệt với những người b&ecirc;n kia chiến tuyến. Điều lạ l&ugrave;ng l&agrave; ngay cả khi nhiệm vụ thực sự của &Ocirc;ng được c&ocirc;ng bố, Phạm Xu&acirc;n Ẩn vẫn gi&agrave;nh được sự k&iacute;nh trọng v&agrave; ngưỡng mộ tuyệt đối từ những đối thủ của m&igrave;nh! H&agrave;ng loạt c&aacute;c ch&iacute;nh kh&aacute;ch, nh&agrave; ngoại giao, nh&agrave; nghi&ecirc;n cứu&hellip; từ Mỹ nva2 tr&ecirc;n thế giới khi đến Việt Nam đều mong muốn được diện kiến &ocirc;ng l&agrave; một minh chứng!</p>\r\n\r\n<p>Đặc biệt gi&aacute;o sư, nh&agrave; sử học Mỹ Larry Berman đ&atilde; d&agrave;nh nhiều năm với gần 30 lần bay từ Mỹ đến Việt Nam để gặp v&agrave; phỏng vấn &ldquo;đối thủ&rdquo;lớn của ng&agrave;nh t&igrave;nh b&aacute;o Mỹ sừng sỏ cho thấy sự hấp dẫn của Phạm Xu&acirc;n Ẩn &ndash; một người do ch&iacute;nh nước Mỹ g&oacute;p phần &ldquo;đ&agrave;o tạo&rdquo; n&ecirc;n!&rdquo; Ấn bản mới n&agrave;y c&oacute; bổ sung Lời giới thiệu của t&aacute;c giả cho lần t&aacute;i bản 2013 &ldquo;S&aacute;u năm sau: hồi tưởng về Điệp Vi&ecirc;n Ho&agrave;n Hảo&rdquo;. Larry Berman viết: &ldquo;&hellip;cuộc đời của Ẩn kh&ocirc;ng chỉ l&agrave; một c&acirc;u chuyện chiến tranh m&agrave; c&ograve;n l&agrave; c&acirc;u chuyện về h&agrave;n gắn, về l&ograve;ng trung th&agrave;nh với đất nước v&agrave; bạn b&egrave;. Trong ấn bản mới, t&ocirc;i đ&atilde; th&ecirc;m v&agrave;o những c&acirc;u chuyện v&agrave; t&igrave;nh tiết mới m&agrave; hồi năm 2007 chưa thể kể ra. T&ocirc;i cũng phản &aacute;nh lại việc một số độc giả d&acirc;n sự v&agrave; qu&acirc;n sự Mỹ đ&atilde; phản ứng về cuốn s&aacute;ch của t&ocirc;i cũng như về nh&acirc;n vật/con người Phạm Xu&acirc;n Ẩn như thế n&agrave;o. Tuy nhi&ecirc;n, điều l&agrave;m cho ấn bản mới n&agrave;y trở n&ecirc;n rất quan trọng đ&oacute; l&agrave; bản dịch mới.</p>\r\n\r\n<p>Độc giả Việt Nam sẽ lần đầu ti&ecirc;n được đọc c&acirc;u chuyện về cuộc đời phi thường của Phạm Xu&acirc;n Ẩn như ch&iacute;nh những g&igrave; m&agrave; &ocirc;ng đ&atilde; kể với t&ocirc;i, một người Mỹ viết hồi k&yacute; cho &ocirc;ng. Điều n&agrave;y l&agrave;m cho ấn bản của First News - Tr&iacute; Việt vừa rất đặc biệt, vừa l&agrave; một cuốn s&aacute;ch mới chứa đựng rất nhiều th&ocirc;ng tin lần đầu ti&ecirc;n c&ocirc;ng bố&hellip;&rdquo;. Đặc biệt trong lần in mới n&agrave;y c&ocirc;ng bố bức thư x&uacute;c động của b&agrave; Thu Nh&agrave;n, vợ thiếu tướng Phạm Xu&acirc;n Ẩn, gửi t&aacute;c giả Larry Berman: &ldquo;Do mắt k&eacute;m, t&ocirc;i phải đọc cuốn s&aacute;ch của &ocirc;ng ba lần trong suốt ba ng&agrave;y, d&ugrave; t&ocirc;i muốn đọc xong ngay lập tức: ba ng&agrave;y đầy cảm x&uacute;c, ba ng&agrave;y đầy nước mắt nhớ thương, đầy t&igrave;nh y&ecirc;u v&agrave; sự tiếc nuối&hellip; V&agrave; giờ đ&acirc;y, mỗi lần đọc lại l&agrave; t&ocirc;i kh&ocirc;ng cầm được nước mắt! Một &iacute;t người bạn của t&ocirc;i cũng c&oacute; cảm x&uacute;c như vậy&hellip; Nhưng t&ocirc;i muốn đọc lại nhiều lần để nhớ về chồng t&ocirc;i. Đ&ocirc;i l&uacute;c, t&ocirc;i cảm thấy như anh Ẩn c&ograve;n sống, nhưng thật đau đớn, t&ocirc;i m&atilde;i m&atilde;i kh&ocirc;ng thể gặp anh ấy ở bất cứ nơi n&agrave;o trong cuộc đời thực n&agrave;y nữa. Một nửa th&acirc;n thể t&ocirc;i đ&atilde; chết! C&agrave;ng th&ecirc;m tuổi, t&ocirc;i c&agrave;ng thấy c&ocirc; đơn khi kh&ocirc;ng c&ograve;n anh ấy. Đ&oacute; c&oacute; phải l&agrave; định mệnh khắc nghiệt m&agrave; tất cả ch&uacute;ng ta đều phải chịu đựng trong cuộc đời n&agrave;y?!&rdquo;.</p>\r\n\r\n<p>Ngo&agrave;i ra, ấn bản mới c&ograve;n c&oacute; lời giới thiệu &ldquo;H&atilde;y Viết Sự Thật&rdquo; của nh&agrave; sử học Dương Trung Quốc: &ldquo;&hellip;Chắc chắn, một người Mỹ viết về Phạm Xu&acirc;n Ẩn sẽ c&oacute; lợi thế m&agrave; c&aacute;c t&aacute;c giả Việt Nam kh&oacute; c&oacute; được. Kh&ocirc;ng phải chỉ l&agrave; vấn đề tư liệu. Gi&aacute;o sư Lary Berman c&oacute; thể khai th&aacute;c c&aacute;c kho lưu trữ ở Mỹ, tiếp cận c&aacute;c nh&acirc;n chứng người Mỹ, c&aacute;c cựu tướng lĩnh, ch&iacute;nh kh&aacute;ch Việt Nam Cộng ho&agrave; nay đang định cư ở Mỹ... l&agrave; những nh&acirc;n tố tạo n&ecirc;n m&ocirc;i trường sống v&agrave; hoạt động của Phạm Xu&acirc;n Ẩn với tư c&aacute;ch l&agrave; một ph&oacute;ng vi&ecirc;n của Tạp ch&iacute; Time v&agrave; một nh&agrave; t&igrave;nh b&aacute;o luồn s&acirc;u v&agrave;o nội bộ đối phương để chống lại cuộc chiến tranh m&agrave; nước Mỹ tiến h&agrave;nh tr&ecirc;n đất nước Việt Nam ở những thập kỷ 60,70 ở thế kỷ trước. Nhưng, c&aacute;i lợi thế ấy kh&ocirc;ng mấy quan trọng bằng c&aacute;ch suy nghĩ của một c&ocirc;ng d&acirc;n, một nh&agrave; sử học Mỹ về một nh&agrave; t&igrave;nh b&aacute;o đứng ở chiến tuyến b&ecirc;n kia của một cuộc chiến khốc liệt.</p>\r\n\r\n<p>N&oacute;i c&aacute;ch kh&aacute;c, chỉ một t&aacute;c giả người Mỹ như Larry Berman mới l&yacute; giải được v&igrave; sao Phạm Xu&acirc;n Ẩn kh&ocirc;ng chỉ được những người đồng b&agrave;o, đồng ch&iacute; Việt Nam của m&igrave;nh kh&acirc;m phục, vinh danh như một người anh h&ugrave;ng m&agrave; nhiều người Mỹ đ&atilde; từng quen biết vẫn giữ được sự k&iacute;nh trọng v&agrave; chia sẻ những việc Phạm Xu&acirc;n Ẩn đ&atilde; h&agrave;nh xử với tư c&aacute;ch một người Việt Nam y&ecirc;u nước...&rdquo;. Thay cho lời bạt l&agrave; những d&ograve;ng hồi tưởng v&ocirc; c&ugrave;ng x&uacute;c động của l&atilde;nh đạo v&agrave; c&aacute;c th&agrave;nh vi&ecirc;n cụm t&igrave;nh b&aacute;o H.63 về người đồng đội quả cảm, t&agrave;i năng v&agrave; qu&yacute; mến của m&igrave;nh &ndash; nh&agrave; t&igrave;nh b&aacute;o anh h&ugrave;ng Phạm Xu&acirc;n Ẩn. &Ocirc;ng Trần Quốc Hương (Mười Hương), nguy&ecirc;n B&iacute; thư BCH Trung ương Đảng, Chỉ huy Mạng lưới t&igrave;nh b&aacute;o chiến lược, Trưởng ban Nội ch&iacute;nh Trung ương: &ldquo;Phạm Xu&acirc;n Ẩn l&agrave; một điệp vi&ecirc;n c&oacute; tầm nh&igrave;n chiến lược, s&aacute;ng tạo, c&oacute; &oacute;c h&agrave;i hước v&agrave; t&agrave;i ngoại giao đặc biệt. Với t&agrave;i năng thu phục nh&acirc;n t&acirc;m v&agrave; khả năng khai th&aacute;c th&ocirc;ng tin xuất ch&uacute;ngđ&atilde; kh&aacute;m ph&aacute; ra những kế hoạch v&ocirc; c&ugrave;ng quan trọng ở cấp cao nhất của qu&acirc;n đội, t&igrave;nh b&aacute;o Mỹ v&agrave; ch&iacute;nh quyền Việt Nam Cộng H&ograve;a. T&ocirc;i từng được b&aacute;o c&aacute;o lại sau khi đọc c&aacute;c t&agrave;i liệu mật của Ẩn gửi về, B&aacute;c Hồ v&agrave; Đại tướng V&otilde; Nguy&ecirc;n Gi&aacute;p c&ugrave;ng c&aacute;c tướng lĩnh đ&atilde; x&uacute;c động: &ldquo;Đọc b&aacute;o c&aacute;o của Hai Trung m&agrave; c&oacute; cảm tưởng như đang ở ngay trong đầu n&atilde;o chỉ huy t&aacute;c chiến của Mỹ&rdquo;.</p>\r\n\r\n<p>Đại t&aacute; Nguyễn Văn T&agrave;u (Tư Cang), Cụm trưởng t&igrave;nh b&aacute;o H.63: &ldquo;Phạm Xu&acirc;n Ẩn l&agrave; một nh&agrave; t&igrave;nh b&aacute;o đặc biệt, c&oacute; một tr&aacute;i tim lớn v&agrave; l&ograve;ng trắc ẩn. Trưa 29/4/1975, khi Đại sứ qu&aacute;n Mỹ đ&atilde; chật k&iacute;n v&agrave; v&ocirc; c&ugrave;ng hỗn loạn, kh&ocirc;ng một ai c&oacute; thể v&agrave;o được, ch&iacute;nh Phạm Xu&acirc;n Ẩn l&agrave; người l&aacute;i xe đưa b&aacute;c sĩ Trần Kim Tuyến &ndash; tr&ugrave;m mật vụ S&agrave;i G&ograve;n từ kh&aacute;ch sạn Continental đến t&ograve;a nh&agrave; 22 đường Gia Long khi c&aacute;nh cửa sắt lớn đang từ từ hạ xuống v&agrave; chiếc trực thăng cuối c&ugrave;ng tr&ecirc;n s&acirc;n thượng chật n&iacute;ch người sắp cất c&aacute;nh. Anh thắng gấp xe nhảy xuống d&ugrave;ng một tay giữ cửa, tay kia k&eacute;o Tuyến chui v&agrave;o kịp khi c&aacute;nh cửa c&ograve;n c&aacute;ch mặt đất chỉ v&agrave;i tấc. Kh&ocirc;ng kịp từ biệt &ndash; &ldquo;Chạy mau đi!&rdquo; &ndash; anh qu&aacute;t lớn trong nước mắt. Trần Kim Tuyến bật kh&oacute;c v&agrave; chỉ kịp thốt l&ecirc;n: &ldquo;T&ocirc;i sẽ kh&ocirc;ng bao giờ qu&ecirc;n anh!&rdquo;.</p>\r\n\r\n<p>Đại t&aacute; Nguyễn Xu&acirc;n Mạnh (Mười Nho), Trưởng ph&ograve;ng điệp b&aacute;o 73, Cục T&igrave;nh b&aacute;o Bộ Tổng tham mưu: Sau 30/4/1975, nhận thấy t&agrave;i năng t&igrave;nh b&aacute;o bậc thầy thi&ecirc;n ph&uacute; v&agrave; xuất ch&uacute;ng của Ẩn, kh&ocirc;ng chỉ c&oacute; tầm nh&igrave;n chiến lược với Mỹ m&agrave; c&ograve;n với Trung Quốc v&agrave; c&aacute;c nước kh&aacute;c, t&ocirc;i đ&atilde; ch&iacute;nh thức đề xuất l&ecirc;n Tổng cục t&igrave;nh b&aacute;o Trung ương v&agrave; cấp tr&ecirc;n, đề nghị Phạm Xu&acirc;n Ẩn l&agrave; Viện trưởng Viện Đ&agrave;o tạo nghiệp vụ t&igrave;nh b&aacute;o cho QĐNDVN sẽ v&ocirc; c&ugrave;ng hiệu quả&rdquo;.</p>\r\n\r\n<p>B&agrave; Nguyễn Thị Mỹ Nhung, giao li&ecirc;n của điệp vi&ecirc;n X6 trong Cụm H.63: &ldquo;Kh&ocirc;ng chỉ l&agrave; bậc thầy trong nghề t&igrave;nh b&aacute;o, Phạm Xu&acirc;n Ẩn c&ograve;n l&agrave; một người anh qu&yacute; mến, với những đức t&iacute;nh m&agrave; t&ocirc;i đ&atilde; học hỏi v&agrave; trưởng th&agrave;nh. Anh lu&ocirc;n quan t&acirc;m, như t&igrave;nh cảm của một người anh d&agrave;nh cho người em g&aacute;i. Suốt đời t&ocirc;i kh&ocirc;ng bao giờ qu&ecirc;n được &aacute;nh mắt của anh, như nguồn kh&iacute;ch lệ, động vi&ecirc;n t&ocirc;i vượt qua những kh&oacute; khăn, thử th&aacute;ch&rdquo;.</p>\r\n\r\n<p>Đồng nghiệp b&aacute;o ch&iacute; v&agrave; những người bạn Mỹ cũng tỏ ra rất ngưỡng mộ v&agrave; cảm k&iacute;ch cuộc đời l&agrave;m b&aacute;o v&agrave; cuộc đời hoạt động t&igrave;nh b&aacute;o phi thường của Phạm Xu&acirc;n Ẩn. Trả lời phỏng vấn của dịch giả Đỗ H&ugrave;ng, b&agrave; Germaine Lộc Swanson, một đồng nghiệp cũ của Phạm Xu&acirc;n Ẩn ở h&atilde;ng tin Reuters ph&aacute;t biểu: &ldquo;Sự thực th&igrave; khi biết tin anh Ẩn l&agrave;m t&igrave;nh b&aacute;o, t&ocirc;i cũng kh&ocirc;ng c&oacute; suy nghĩ g&igrave; đặc biệt, kh&ocirc;ng hề sốc. Anh ấy vẫn l&agrave; một người anh, một người bạn th&acirc;n, một cựu đồng nghiệp của t&ocirc;i. Trước kia t&ocirc;i tr&acirc;n trọng, qu&yacute; trọng anh ấy thế n&agrave;o th&igrave; giờ đ&acirc;y vẫn vậy. Trong suy nghĩ của t&ocirc;i, anh Ẩn l&agrave; một người y&ecirc;u nước, một người c&oacute; tinh thần d&acirc;n tộc. Anh ấy l&agrave;m việc đ&oacute; v&igrave; anh ấy nghĩ sẽ gi&uacute;p &iacute;ch cho đất nước. T&ocirc;i y&ecirc;u nước, anh ấy cũng y&ecirc;u nước, nhưng ho&agrave;n cảnh xuất th&acirc;n mỗi người mỗi kh&aacute;c n&ecirc;n con đường sau đ&oacute; cũng kh&aacute;c nhau. Gia đ&igrave;nh lớn của t&ocirc;i c&oacute; người theo Việt Minh, c&oacute; người l&agrave;m cho Ph&aacute;p. Ho&agrave;n cảnh lịch sử đặc biệt của đất nước m&igrave;nh khiến kh&ocirc;ng &iacute;t những người bạn th&acirc;n, những th&agrave;nh vi&ecirc;n trong c&aacute;c gia đ&igrave;nh đứng về c&aacute;c ph&iacute;a chiến tuyến kh&aacute;c nhau. Đất nước khi chiến tranh l&agrave; vậy, đến thời b&igrave;nh th&igrave; c&aacute;c th&agrave;nh vi&ecirc;n gia đ&igrave;nh lại về với nhau. T&igrave;nh cảm giữa t&ocirc;i với Phạm Xu&acirc;n Ẩn v&agrave; gia đ&igrave;nh anh ấy cũng tương tự, kh&ocirc;ng g&igrave; thay đổi được&rdquo;.</p>\r\n\r\n<p>Sau khi đọc cuốn Điệp vi&ecirc;n ho&agrave;n hảo, John le Carre, nh&agrave; văn Anh chuy&ecirc;n viết tiểu thuyết t&igrave;nh b&aacute;o đ&atilde; thốt l&ecirc;n: &ldquo;C&uacute; sốc về điệp vi&ecirc;n hai mang kh&ocirc;ng sao l&agrave;m t&ocirc;i qu&ecirc;n được. V&agrave; sự nhẹ dạ của kẻ x&acirc;m lược ngạo mạn cũng thế&quot;. Robert Sam Anson, t&aacute;c giả cuốn War News: A Young Reporter in Indochina:&nbsp; &ldquo;Phạm Xu&acirc;n Ẩn l&agrave; đồng nghiệp, l&agrave; người bạn, người thầy của t&ocirc;i - v&agrave; l&agrave; người đ&atilde; cứu mạng sống của t&ocirc;i trong cuộc chiến Việt Nam. T&ocirc;i nghĩ t&ocirc;i đ&atilde; hiểu Phạm Xu&acirc;n Ẩn như bất kỳ người Mỹ n&agrave;o kh&aacute;c, cho đến khi t&ocirc;i đọc cuốn s&aacute;ch của Larry Berman. C&oacute; nhiều bất ngờ đến với t&ocirc;i tr&ecirc;n từng trang s&aacute;ch.&rdquo; David Lamb, t&aacute;c giả cuốn Việt Nam, B&acirc;y giờ: Một ph&oacute;ng vi&ecirc;n trở lại: &ldquo;Đọc tư liệu được bi&ecirc;n soạn kỹ lưỡng của Larry Berman, bạn sẽ hiểu tại sao chiến thắng Việt Nam chưa bao giờ nằm trong tầm với của nước Mỹ.&rdquo;</p>\r\n\r\n<p>Stanley Karnow, t&aacute;c giả cuốn Việt Nam: Một thi&ecirc;n lịch sử v&agrave; chủ nh&acirc;n giải Pulitzer 1991 về đề t&agrave;i lịch sử: &ldquo;Một trong những nh&acirc;n vật quyến rũ nhất của Việt Nam trong suốt cuộc chiến l&agrave; Phạm Xu&acirc;n Ẩn. Larry Berman đ&atilde; v&eacute;n bức m&agrave;n b&iacute; mật trong cuộc đời hai mặt lạ l&ugrave;ng của con người đặc biệt n&agrave;y th&ocirc;ng qua c&aacute;ch kể chuyện l&ocirc;i cuốn&rdquo;.</p>\r\n\r\n<p>Dan Southerland, Tổng thư k&yacute; Đ&agrave;i ph&aacute;t thanh Ch&acirc;u &Aacute; Tự do, cựu ph&oacute;ng vi&ecirc;n Christian Science Monitor : &ldquo;Kh&ocirc;ng ai c&oacute; thể biết được bao nhi&ecirc;u người l&iacute;nh đ&atilde; chết như l&agrave; hệ quả từ những hoạt động b&iacute; mật của Phạm Xu&acirc;n Ẩn . Nhưng Larry Berman đ&atilde; thực hiện một sứ mệnh ngoại hạng trong việc giải th&iacute;ch sự h&igrave;nh th&agrave;nh một điệp vi&ecirc;n bậc thầy ngay tại S&agrave;i G&ograve;n. Ở đ&acirc;y c&oacute; rất nhiều điều để cả những người ủng hộ lẫn chống đối phải suy ngẫm&rdquo;.</p>\r\n\r\n<p>Robert Dallek, t&aacute;c giả cuốn Nixon and Kissinger: Partners in Power: &ldquo;Perfect Spy l&agrave; một minh chứng sống động rằng sự thật c&ograve;n kỳ lạ hơn chuyện viễn tưởng. Larry Berman đ&atilde; l&agrave;m tỏa s&aacute;ng hai cuộc đời của Phạm Xu&acirc;n Ẩn. Cuốn s&aacute;ch đầy ắp th&ocirc;ng tin lịch sử với những đặc t&iacute;nh của c&acirc;u chuyện t&igrave;nh b&aacute;o thời Chiến tranh Lạnh đặc biệt ly kỳ v&agrave; l&ocirc;i cuốn&rdquo;. &nbsp;</p>\r\n\r\n<p>Zalin Granti, t&aacute;c giả cuốn Những người sống s&oacute;t: C&aacute;c t&ugrave; binh chiến tranh Việt Nam kể lại chuyện m&igrave;nh: &ldquo;Sau&nbsp; khi Perfect Spy xuất bản tại Mỹ v&agrave; tiết lộ Phạm Xu&acirc;n Ẩn l&agrave; điệp vi&ecirc;n Cộng sản, rất nhiều nh&agrave; b&aacute;o Mỹ đ&atilde; ra sức đứng ra bảo vệ Phạm Xu&acirc;n Ẩn. Họ tuy&ecirc;n bố v&agrave; đưa ra c&aacute;c bằng chứng rằng &ocirc;ng chưa bao giờ l&agrave;m sai lệch c&aacute;c b&agrave;i b&aacute;o, rằng &ocirc;ng lu&ocirc;n cung cấp cho họ những ph&acirc;n t&iacute;ch rất kh&aacute;ch quan. &Ocirc;ng ấy tranh luận với họ về c&aacute;c h&agrave;nh động ch&iacute;nh trị v&agrave; qu&acirc;n sự của Mỹ, S&agrave;i G&ograve;n v&agrave; H&agrave; Nội một c&aacute;ch c&ocirc;ng bằng v&agrave; ch&iacute;nh trực nhất&rdquo;.</p>\r\n\r\n<p>Jean - Clause Pomonti, t&aacute;c giả cuốn Một người Việt Nam thầm lặng: &ldquo;Tin Phạm Xu&acirc;n Ẩn l&agrave;m t&igrave;nh b&aacute;o cho Bắc Việt đ&atilde; khiến nhiều quan chức cao cấp CIA phải hổ thẹn v&igrave; họ đ&atilde; từng dựa tr&ecirc;n những ph&acirc;n t&iacute;ch của Ẩn để ra những quyết định quan trọng&quot;.&nbsp; Cuốn Điệp vi&ecirc;n ho&agrave;n hảo của Larry Berman được người đọc Việt Nam v&agrave; Mỹ cũng như giới sử học, giới b&aacute;o ch&iacute; v&agrave; c&aacute;c nh&agrave; t&igrave;nh b&aacute;o của cả hai nước đ&aacute;nh gi&aacute; cao.</p>\r\n\r\n<p>S&aacute;ch do First News thực hiện, nh&agrave; b&aacute;o Đỗ H&ugrave;ng bi&ecirc;n dịch từ New Fully Updated Edition X6 Perfect Spy - The Incredible Double Life of PHAM XUAN AN Time Magazine Reporter &amp; Vietnamese Communist Agent, ph&aacute;t h&agrave;nh tại nh&agrave; s&aacute;ch Tr&iacute; Việt, 11 H Nguyễn Thị Minh Khai Q.1 v&agrave; c&aacute;c nh&agrave; s&aacute;ch kh&aacute;c tr&ecirc;n to&agrave;n quốc.</p>\r\n\r\n<p>Lần xuất bản mới c&oacute; bổ sung n&agrave;y n&agrave;y của Larry Berman được xuất bản ở Việt Nam trước khi xuất bản bằng tiếng Anh ở Mỹ v&agrave; c&aacute;c nước tr&ecirc;n thế giới với tựa đề PERFECT SPY X6 Fully New Updated Edition</p>\r\n\r\n<p>Th&ocirc;ng tin về t&aacute;c giả&nbsp; Điệp vi&ecirc;n ho&agrave;n hảo: Larry Berman nh&agrave; sử học, gi&aacute;o sư khoa học ch&iacute;nh trị tại Đại học California đồng thời l&agrave; một người chuy&ecirc;n t&acirc;m nghi&ecirc;n cứu lịch sử cuộc chiến tranh của Mỹ ở Việt Nam. &Ocirc;ng cũng l&agrave; t&aacute;c giả của cuốn s&aacute;ch nổi tiếng Kh&ocirc;ng h&ograve;a b&igrave;nh, Kh&ocirc;ng danh dự: Nixon, Kissinger v&agrave; Sự phản bội ở Việt Nam. Đ&acirc;y l&agrave; cuốn s&aacute;ch rất c&oacute; gi&aacute; trị lịch sử về giai đoạn cuối cuộc chiến tranh do Mỹ g&acirc;y ra ở Việt Nam. Larry Berman đ&atilde; phải mất 5 năm v&agrave; sang Việt Nam 18 lần để thu thập tư liệu cho lần in cuốn s&aacute;ch đầu ti&ecirc;n Điệp Vi&ecirc;n Ho&agrave;n Hảo v&agrave; sau đ&oacute; h&agrave;ng chục lần cho lần in mới n&agrave;y.</p>', '1654607116.jpg', NULL, '2022-06-07 13:05:17', '2022-06-07 13:05:17'),
(23, 'Nước Mỹ Trong Mắt Trump', '168300.00', 50, 4, 'NXB Trẻ', 'NXB Trẻ', '<p>Nước Mỹ trong mắt Trump l&agrave; cuốn s&aacute;ch mới nhất của B.O&rsquo;Reilly, xuất bản cuối 2019, được viết dưới dạng kể chuyện từ những cuộc phỏng vấn trực tiếp của O&rsquo;Reilly với Tổng thống Mỹ D.Trump v&agrave; gia đ&igrave;nh theo mạch thời gian k&egrave;m theo một số b&igrave;nh luận của t&aacute;c giả; cũng như nghi&ecirc;n cứu của t&aacute;c giả về c&aacute;c chủ đề li&ecirc;n quan. O&rsquo;Reilly đ&atilde; t&igrave;m c&aacute;ch l&yacute; giải những động lực th&uacute;c đẩy Donald Trump trong cuộc sống v&agrave; cụ thể hơn l&agrave; trong quyết định tạm g&aacute;c lại những xa hoa ph&uacute; qu&yacute; của một &ocirc;ng tr&ugrave;m bất động sản để tuy&ecirc;n bố tranh cử Tổng thống; chỉ ra sức h&uacute;t của &ocirc;ng Trump đối với cử tri Mỹ trong năm 2016. Những lập luận v&agrave; khung ph&acirc;n t&iacute;ch của t&aacute;c giả chắc chắn rất c&oacute; &iacute;ch trong ph&acirc;n t&iacute;ch cuộc tổng tuyển cử Mỹ năm 2020. Ng&ocirc;n ngữ sắc sảo v&agrave; ngắn gọn, c&ugrave;ng sự cẩn trọng chi tiết trong nghi&ecirc;n cứu về chủ thể của t&aacute;c giả mang lại sự hấp dẫn đặc biệt kh&oacute; cưỡng đối với ai muốn biết về tổng thống Mỹ D. Trump- trong đời thường v&agrave; trong ch&iacute;nh trường.<br />\r\n- Về t&aacute;c giả: B. O&rsquo;Reilly c&oacute; hơn 20 năm l&agrave; người dẫn chương tr&igrave;nh v&agrave; b&igrave;nh luận vi&ecirc;n thời sự của Fox News, đ&atilde; trở n&ecirc;n quen thuộc với nhiều độc giả Việt Nam th&ocirc;ng qua loạt s&aacute;ch Killing, kể về những t&igrave;nh tiết đặc biệt xung quanh cuộc đời của những nh&acirc;n vật lịch sử nổi tiếng từng bị &aacute;m s&aacute;t. O&rsquo;Reilly đ&atilde; c&oacute; 4 cuốn s&aacute;ch nằm trong danh s&aacute;ch b&aacute;n chạy nhất của Thời b&aacute;o New York.</p>', '1654607196.jpg', NULL, '2022-06-07 13:06:36', '2022-06-07 13:08:49'),
(24, 'Putin - Logic Của Quyền Lực', '134400.00', 50, 4, 'Hubert Seipel', 'NXB Tổng Hợp TPHCM', '<p><br />\r\nT&ecirc;n gốc của t&aacute;c phẩm l&agrave; <strong>Putin: Innenansichten der Macht.</strong> S&aacute;ch gồm 21 chương, do Hubert Seipel thực hiện trong 5 năm (từ năm 2010 đến 2015). T&aacute;c giả đ&atilde; c&oacute; hơn 20 buổi phỏng vấn chuy&ecirc;n s&acirc;u với Putin, đồng thời th&aacute;p t&ugrave;ng &ocirc;ng tr&ecirc;n h&agrave;ng chục chuyến đi trong, ngo&agrave;i nước.</p>\r\n\r\n<p>S&aacute;ch mở ra g&oacute;c nh&igrave;n mới về nh&agrave; l&atilde;nh đạo Nga. Putin: Logic của quyền lực ph&aacute;t h&agrave;nh ở Việt Nam cuối th&aacute;ng 11, do dịch giả Phan Xu&acirc;n Loan chuyển ngữ, Nh&agrave; xuất bản Tổng hợp ph&aacute;t h&agrave;nh. Hubert Seipel t&aacute;i hiện những dấu mốc ch&iacute;nh trong cuộc đời Putin. Năm 1975, Putin tốt nghiệp khoa Luật Đại học Tổng hợp Quốc gia Saint Petersburg. Năm 1985, &ocirc;ng trở th&agrave;nh nh&acirc;n vi&ecirc;n t&igrave;nh b&aacute;o đối ngoại của Li&ecirc;n X&ocirc; ở Đức. Năm 1994, &ocirc;ng trở th&agrave;nh ph&oacute; chủ tịch thứ nhất của th&agrave;nh phố qu&ecirc; nh&agrave; Saint Petersburg. Năm 1996, &ocirc;ng chuyển đến Moskva v&agrave; được bổ nhiệm nhiều chức vụ quan trọng trong văn ph&ograve;ng Tổng thống Nga. Cuối năm 1999, &ocirc;ng trở th&agrave;nh Tổng thống Nga.</p>\r\n\r\n<p>Khi mới l&ecirc;n nắm quyền, &ocirc;ng phải đối mặt với nhiều kh&oacute; khăn trong giai đoạn &quot;tư nh&acirc;n h&oacute;a&quot; c&aacute;c t&agrave;i sản quốc gia. Putin c&ugrave;ng l&uacute;c phải vực dậy nền kinh tế sa s&uacute;t, giải quyết nạn tham nhũng, chia rẽ d&acirc;n tộc v&agrave; c&aacute;c &acirc;m mưu lật đổ ch&iacute;nh quyền. Cuốn s&aacute;ch đề cập đến cuộc chiến của Putin v&agrave; hai đối thủ lớn - ch&iacute;nh kh&aacute;ch Boris Berezovsky v&agrave; tỷ ph&uacute; v&agrave; Mikhai Khodorkovsky - chủ tập đo&agrave;n dầu kh&iacute; Yukos. Để đảo ngược t&igrave;nh thế kh&oacute; khăn, Putin đ&atilde; t&igrave;m c&aacute;ch tăng trưởng kinh tế, ph&aacute;t triển c&aacute;c dịch vụ y tế, gi&aacute;o dục. Tr&ecirc;n ch&iacute;nh trường quốc tế, &ocirc;ng đưa ra nhiều quyết định cứng rắn, đặc biệt l&agrave; việc phản đối sự mở rộng của Tổ chức Hiệp ước Bắc Đại T&acirc;y Dương (NATO) về ph&iacute;a Đ&ocirc;ng. S&aacute;ch kh&ocirc;ng chứa nhiều th&ocirc;ng tin về đời sống c&aacute; nh&acirc;n của tổng thống.</p>\r\n\r\n<p>Trước khi l&agrave;m việc c&ugrave;ng Hubert Seipel, Putin y&ecirc;u cầu đặt ra một ranh giới r&otilde; r&agrave;ng giữa c&ocirc;ng việc v&agrave; đời tư để bảo vệ bản th&acirc;n v&agrave; gia đ&igrave;nh. &Ocirc;ng n&oacute;i: &ldquo;T&ocirc;i hứng th&uacute; với c&aacute;c phương tiện truyền th&ocirc;ng bởi v&igrave; t&ocirc;i l&agrave; ch&iacute;nh kh&aacute;ch v&agrave; l&agrave; Tổng thống Nga, c&ograve;n c&aacute;c con g&aacute;i t&ocirc;i kh&ocirc;ng giữ chức vụ ch&iacute;nh trị, những quan hệ c&aacute; nh&acirc;n của t&ocirc;i kh&ocirc;ng thuộc về c&aacute;c vấn đề ch&iacute;nh trị - đ&oacute; l&agrave; việc ri&ecirc;ng của t&ocirc;i&quot;. V&igrave; thế, vợ con &ocirc;ng chỉ được nhắc đến v&agrave;i lần.</p>\r\n\r\n<p>Vladimir Vladimirovich Putin sinh năm 1952. &Ocirc;ng l&agrave; tổng thống Nga trong hai nhiệm kỳ từ năm 2000 tới năm 2008, trước khi phải rời điện Kremlin theo Hiến ph&aacute;p l&uacute;c bấy giờ. Putin đảm nhận vai tr&ograve; thủ tướng Nga sau khi &ocirc;ng Dmitry Medvedev tiếp quản vị tr&iacute; tổng thống. Với việc chiến thắng trong cuộc bầu cử năm 2012, Putin c&oacute; nhiệm kỳ tổng thống thứ ba. Năm 2007, Putin được tạp ch&iacute; Times b&igrave;nh chọn l&agrave; Nh&acirc;n vật của năm. Năm 2015, Putin đứng đầu trong danh s&aacute;ch 100 người c&oacute; sức ảnh hưởng nhất thế giới của tạo ch&iacute; n&agrave;y. Tạp ch&iacute; Forbes xếp Putin l&agrave; người quyền lực nhất thế giới trong bốn năm li&ecirc;n tiếp, từ năm 2013 tới năm 2016.</p>\r\n\r\n<p>T&aacute;c giả Hubert Seipel sinh năm 1950 ở Wasserlos, Đức. &Ocirc;ng l&agrave; nh&agrave; b&aacute;o, nh&agrave; l&agrave;m phim t&agrave;i liệu. &Ocirc;ng từng nghi&ecirc;n cứu ch&iacute;nh trị, lịch sử, khoa học ch&iacute;nh trị tại tại Đại học Marburg, Đức v&agrave; Trường Kinh tế v&agrave; Khoa học Ch&iacute;nh trị London, Anh. &Ocirc;ng c&oacute; cơ hội tiếp x&uacute;c với Putin từ năm 2010, khi l&agrave;m bộ phim t&agrave;i liệu I, Putin: A Portrait. Sau bộ phim đ&oacute;, &ocirc;ng trở th&agrave;nh nh&agrave; b&aacute;o phương T&acirc;y được tiếp cận gần Putin.</p>', '1654607294.jpg', NULL, '2022-06-07 13:08:14', '2022-06-07 13:08:14'),
(25, 'Gia Tộc Morgan', '399200.00', 50, 4, 'Ron Chernow', 'NXB Thế Giới', '<p><br />\r\nH&igrave;nh th&agrave;nh, ph&aacute;t triển, sụp đổ rồi lại hồi sinh, c&oacute; lẽ kh&ocirc;ng một tổ chức n&agrave;o ẩn chứa nhiều giai thoại, b&iacute; mật hay chủ đề g&acirc;y tranh c&atilde;i gay gắt như đế chế ng&acirc;n h&agrave;ng Mỹ &ndash; Gia tộc Morgan. Đạt Giải thưởng S&aacute;ch quốc gia v&agrave; hiện được coi l&agrave; một t&aacute;c phẩm kinh điển, Gia tộc Morgan l&agrave; cuốn tiểu sử tham vọng nhất từng được viết về một triều đại ng&acirc;n h&agrave;ng Mỹ. Cuốn s&aacute;ch vẽ n&ecirc;n bức tranh to&agrave;n diện về bốn thế hệ nh&agrave; Morgan v&agrave; c&aacute;c c&ocirc;ng ty b&iacute; mật, mạnh mẽ m&agrave; họ sở hữu. Với thế lực của m&igrave;nh, đế chế Morgan đ&atilde; biến nền kinh tế non trẻ của Mỹ th&agrave;nh một cường quốc c&ocirc;ng nghiệp mạnh nhất thế giới v&agrave; khiến trung t&acirc;m t&agrave;i ch&iacute;nh thế giới dịch chuyển từ London sang New York. Vượt xa cả lịch sử đơn thuần của ng&agrave;nh ng&acirc;n h&agrave;ng Mỹ, cuốn s&aacute;ch ch&iacute;nh l&agrave; c&acirc;u chuyện về sự tiến h&oacute;a của nền t&agrave;i ch&iacute;nh hiện đại. Dựa tr&ecirc;n c&aacute;c cuộc phỏng vấn rộng r&atilde;i c&ugrave;ng quyền truy cập đặc biệt v&agrave;o kho lưu trữ của gia tộc n&agrave;y, t&aacute;c đ&atilde; khắc họa n&ecirc;n bức ch&acirc;n dung hấp dẫn về c&acirc;u chuyện ri&ecirc;ng của nh&agrave; Morgan v&agrave; thế giới hiếm hoi của giới tinh hoa Mỹ v&agrave; Anh.</p>\r\n\r\n<p><strong>Tr&iacute;ch đoạn:</strong></p>\r\n\r\n<p><em>N&oacute;i về gia tộc Morgan thời kỳ đầu:</em></p>\r\n\r\n<p>&ldquo;Gia tộc Morgan trước những năm 1935 được coi l&agrave; sự kết hợp t&agrave;i ch&iacute;nh quyền lực nhất trong lịch sử. Được một chủ ng&acirc;n h&agrave;ng người Mỹ t&ecirc;n l&agrave; George Peabody s&aacute;ng lập tại London năm 1838, c&ocirc;ng ty được gia đ&igrave;nh Morgan kế thừa v&agrave; bắt nh&aacute;nh sang New York. Hai nh&agrave; điều h&agrave;nh được biết đến nhiều nhất của Morgan l&agrave; J. P. Morgan, Sr. (1837-1913) v&agrave; J. P. Morgan, Jr. (1867-1943), đ&atilde; kết hợp ho&agrave;n hảo</p>\r\n\r\n<p>th&agrave;nh &ldquo;qu&aacute;i th&uacute;&rdquo; J. P. Morgan tồn tại suốt hơn một thế kỷ. Vẻ bề ngo&agrave;i của họ giống nhau một c&aacute;ch kh&aacute;c thường: từ c&aacute;i đầu h&oacute;i, chiếc mũi to đến th&acirc;n h&igrave;nh quả l&ecirc; đều khiến người kh&aacute;c nhầm lẫn. Đối với những người ủng hộ, hai th&agrave;nh vi&ecirc;n nh&agrave; Morgan l&agrave; khu&ocirc;n mẫu của những &ocirc;ng chủ ng&acirc;n h&agrave;ng cổ điển lu&ocirc;n giữ chữ t&iacute;n trong từng lời n&oacute;i v&agrave; chốt giao dịch chỉ bằng một c&aacute;i bắt tay. Phe chỉ tr&iacute;ch th&igrave; cho rằng họ l&agrave; những t&ecirc;n bạo ch&uacute;a đạo đức giả chuy&ecirc;n đ&agrave;n &aacute;p c&aacute;c c&ocirc;ng ty, b&agrave;y mưu t&iacute;nh kế c&ugrave;ng với quyền lực ngoại bang v&agrave; khiến nước Mỹ rơi v&agrave;o cuộc chiến d&agrave;nh lợi nhuận. Kh&ocirc;ng một ai giữ được quan điểm trung lập khi n&oacute;i về nh&agrave; Morgan.&rdquo;</p>\r\n\r\n<p><em>N&oacute;i về con người thật sự của Peabody sau vẻ ngo&agrave;i vui t&iacute;nh:</em></p>\r\n\r\n<p>&ldquo;Đằng sau vẻ ngo&agrave;i vui t&iacute;nh của Peabody l&agrave; một g&atilde; h&agrave; tiện c&ocirc; độc. &Ocirc;ng sống trong những căn ph&ograve;ng tiện nghi ở một kh&aacute;ch sạn tr&ecirc;n phố Regent v&agrave; l&agrave;m việc kh&ocirc;ng ngừng nghỉ, ngoại trừ những l&uacute;c thỉnh thoảng đi c&acirc;u c&aacute;. Trong thời gian 12 năm, &ocirc;ng chưa bao giờ nghỉ l&agrave;m hai ng&agrave;y li&ecirc;n tiếp v&agrave; d&agrave;nh trung b&igrave;nh 10 giờ mỗi ng&agrave;y tại nơi l&agrave;m việc. Mặc d&ugrave; c&oacute; những b&agrave;i ph&aacute;t biểu g&acirc;y x&uacute;c động về vận mệnh nước Mỹ, &ocirc;ng kh&ocirc;ng trở về qu&ecirc; nh&agrave; trong 20 năm v&agrave; trong thời gian đ&oacute;, con người &ocirc;ng cũng &ldquo;sạm m&agrave;u&rdquo; c&ugrave;ng với giao dịch ảm đạm của tr&aacute;i phiếu c&aacute;c bang nước Mỹ. Trong đợt khủng hoảng trầm trọng v&agrave;o đầu những năm 1840&ndash;thập kỷ được g&aacute;n cho c&aacute;i t&ecirc;n Những năm 40 thiếu đ&oacute;i&ndash;nợ c&aacute;c bang ngập s&acirc;u tới mức cứ một đồng đ&ocirc;-la th&igrave; 50 xu phải d&agrave;nh để trả nợ. Điều tồi tệ nhất xảy ra khi năm bang của Mỹ&ndash;Pennsylvania, Mississippi, Indiana, Arkansas v&agrave; Michigan v&agrave; l&atilde;nh thổ Florida kh&ocirc;ng trả được nợ l&atilde;i suất tr&aacute;i phiếu của họ. Trong một li&ecirc;n minh con nợ sơ khai, một số thống đốc bang của Mỹ li&ecirc;n kết với nhau nhằm đ&aacute;nh quỵt. Cho đến nay, vụ Mississippi v&ocirc; lại vẫn l&agrave; vụ vỡ nợ đ&aacute;ng hổ thẹn.&rdquo;</p>\r\n\r\n<p><em>N&oacute;i về tầm ảnh hưởng của Pierpont đối với sự kịch t&iacute;nh của thị trường t&agrave;i ch&iacute;nh Mỹ:</em></p>\r\n\r\n<p>&ldquo;Trong cuộc hoảng loạn năm 1907, Pierpont đ&atilde; chứng minh rằng ng&agrave;nh t&agrave;i ch&iacute;nh Mỹ c&oacute; thể cực kỳ kịch t&iacute;nh. Trong một trận chung kết tối thứ Bảy, ng&agrave;y 2 th&aacute;ng 11, &ocirc;ng đ&atilde; nghĩ ra c&aacute;ch giải cứu cho C&ocirc;ng ty t&iacute;n th&aacute;c Mỹ vẫn đang lung lay, cho Quỹ t&iacute;n th&aacute;c Lincoln v&agrave; cho Moore &amp; Schley, một c&ocirc;ng ty m&ocirc;i giới đầu cơ c&oacute; khoảng 25 triệu đ&ocirc;-la. C&ocirc;ng ty cuối c&ugrave;ng n&agrave;y nắm trong tay phần lớn cổ phần khổng lồ của C&ocirc;ng ty than v&agrave; sắt Tennessee với tư c&aacute;ch t&agrave;i sản thế chấp cho c&aacute;c khoản vay. Nếu c&ocirc;ng ty n&agrave;y phải thanh l&yacute; chỗ cổ phần đ&oacute; th&igrave; thị trường chứng kho&aacute;n c&oacute; thể sẽ sụp đổ. Nếu đến lượt Moore &amp; Schley sụp đổ th&igrave; c&aacute;c ng&acirc;n h&agrave;ng kh&aacute;c c&oacute; thể cũng bị k&eacute;o theo.</p>\r\n\r\n<p>Giống như một &ocirc;ng bầu tạo n&ecirc;n kiệt t&aacute;c s&acirc;n khấu của m&igrave;nh, Pierpont tập hợp c&aacute;c chủ ng&acirc;n h&agrave;ng của th&agrave;nh phố tại thư viện ri&ecirc;ng. &Ocirc;ng xếp c&aacute;c chủ ng&acirc;n h&agrave;ng thương mại tại Ph&ograve;ng Đ&ocirc;ng, b&ecirc;n dưới c&aacute;c biểu tượng cung ho&agrave;ng đạo v&agrave; một tấm thảm c&oacute; h&igrave;nh thất đại tội, c&ograve;n ở Ph&ograve;ng T&acirc;y l&agrave; chủ tịch c&aacute;c c&ocirc;ng ty t&iacute;n th&aacute;c ngồi lọt thỏm trong những chiếc ghế salon m&agrave;u đỏ đậm v&agrave; ghế b&agrave;nh dưới c&aacute;i nh&igrave;n của Đức mẹ Madonna v&agrave; c&aacute;c vị th&aacute;nh. Trong văn ph&ograve;ng của Belle Greene ở giữa, Pierpont ngồi chơi b&agrave;i như thần Jupiter v&ocirc; can trước vụ việc nghi&ecirc;m trọng đang diễn ra.&rdquo;</p>\r\n\r\n<p>Đặc điểm nổi bật:</p>\r\n\r\n<p>- Với văn phong vừa gi&agrave;u sức gợi về &yacute; tưởng, vừa phong ph&uacute; về th&ocirc;ng tin, đ&ocirc;i l&uacute;c l&atilde;ng mạn v&agrave; h&oacute;m hỉnh một c&aacute;ch tinh tế, cuốn s&aacute;ch n&agrave;y c&oacute; thể s&aacute;nh với một bộ trường thi&ecirc;n tiểu thuyết về 3 kỷ nguy&ecirc;n thăng trầm của J.P. Morgan, cội rễ của ng&agrave;nh Ng&acirc;n h&agrave;ng Mỹ: Thời kỳ c&aacute;c &ocirc;ng tr&ugrave;m, thời kỳ ngoại giao v&agrave; thời kỳ canh bạc.</p>\r\n\r\n<p>- Luận điểm m&agrave; cuốn s&aacute;ch n&agrave;y đưa ra l&agrave; sẽ kh&ocirc;ng bao giờ c&oacute; một ng&acirc;n h&agrave;ng quyền lực, b&iacute; ẩn hay gi&agrave;u c&oacute; như Gia tộc Morgan nữa. Sẽ kh&ocirc;ng một doanh nghiệp n&agrave;o c&oacute; thể m&ocirc; phỏng những g&igrave; m&agrave; Rothschilds thể hiện trong thế kỷ XIX hay Morgan trong thế kỷ XX trong v&ograve;ng 100 năm tới. Ng&acirc;n h&agrave;ng đ&atilde; kh&ocirc;ng c&ograve;n chiếm giữ vị tr&iacute; độc t&ocirc;n trong ng&agrave;nh t&agrave;i ch&iacute;nh. Khi nền t&agrave;i ch&iacute;nh thế giới ch&iacute;n muồi, quyền lực chia đều cho c&aacute;c tổ chức v&agrave; trung t&acirc;m t&agrave;i ch&iacute;nh. Do đ&oacute;, c&acirc;u chuyện của ch&uacute;ng ta đưa ra c&aacute;i nh&igrave;n về một thế giới ng&acirc;n h&agrave;ng tan biến nhanh ch&oacute;ng trong qu&aacute; khứ, một thế giới với những bất động sản khổng lồ, bộ sưu tập nghệ thuật c&ugrave;ng du thuyền vượt biển của những &ocirc;ng chủ d&agrave;nh phần lớn thời gian với người đứng đầu nh&agrave; nước v&agrave; tự huyễn hoặc bản th&acirc;n thuộc tầng lớp ho&agrave;ng gia cao qu&yacute;. Tr&aacute;i với nguy&ecirc;n tắc chiếu phối cảnh th&ocirc;ng thường, Gia tộc Morgan c&agrave;ng l&ugrave;i xa về thời gian qu&aacute; khứ c&agrave;ng được ph&oacute;ng to hơn.</p>\r\n\r\n<p>- Chernow l&agrave;m một c&ocirc;ng việc mẫu mực trong việc nắm bắt t&iacute;nh c&aacute;ch của c&aacute;c đối tượng của m&igrave;nh: mang đến cuộc sống sống động của những c&aacute; nh&acirc;n như Junius Spencer Morgan, George Peabody, v&agrave; tất nhi&ecirc;n, ch&iacute;nh John Pierpont Morgan. Bản tường thuật chứa đầy những chi tiết nhỏ l&agrave;m nổi bật t&iacute;nh c&aacute;ch của từng nh&acirc;n vật: l&ograve;ng th&ugrave; hận v&agrave; sự keo kiệt của Peabody đối với những c&aacute; nh&acirc;n cụ thể trong khi theo đuổi những h&agrave;nh động từ thiện vĩ đại, c&aacute;ch m&agrave; Junius Spencer Morgan đ&atilde; c&oacute; tất cả những bức thư của con trai &ocirc;ng Pierpont về điều kiện kinh tế v&agrave; ch&iacute;nh trị ở Mỹ, sự ch&uacute; &yacute; của Pierpont đến trang phục th&iacute;ch hợp (một quả b&oacute;ng n&eacute;m v&agrave;o m&ugrave;a đ&ocirc;ng, một chiếc mũ Panama v&agrave;o m&ugrave;a h&egrave;), v&agrave; thuộc hạ của Jack chụp ảnh Franklin Delano Roosevelt từ tờ b&aacute;o buổi s&aacute;ng của Jack, v&igrave; bệnh huyết &aacute;p cao v&agrave; sự căm gh&eacute;t của m&igrave;nh đối với Roosevelt.</p>\r\n\r\n<p>- Những trải nghiệm, những diễn biến, những giải ph&aacute;p của một ng&acirc;n h&agrave;ng th&acirc;m s&acirc;u v&agrave; ph&aacute;t triển h&agrave;ng đầu nước Mỹ, của một nền t&agrave;i ch&iacute;nh đ&atilde; đi trước ch&uacute;ng ta cả thế kỷ cũng c&oacute; thể coi l&agrave; những b&agrave;i học bổ &iacute;ch cho c&aacute;c chặng đường ph&iacute;a trước của nền t&agrave;i ch&iacute;nh Việt Nam.</p>\r\n\r\n<p><em>- &ldquo;Gia tộc Morgan h&eacute; lộ g&oacute;c nh&igrave;n xuy&ecirc;n suốt về sự th&agrave;nh h&igrave;nh của bối cảnh t&agrave;i ch&iacute;nh l&uacute;c bấy giờ...&nbsp;</em><em>Một g&oacute;c nh&igrave;n to&agrave;n cảnh được nghi&ecirc;n cứu kỹ lưỡng về gia tộc quyền lực nhất trong giới ng&acirc;n h&agrave;ng Mỹ suốt thế kỷ qua... Phong c&aacute;ch của Chernow quả thực tinh tế v&agrave; mềm mại&rdquo;.&nbsp;</em><strong><em>&mdash; Nhật b&aacute;o Washington Post</em></strong></p>\r\n\r\n<p><em>- &ldquo;Chernow đ&atilde; khắc họa bức ch&acirc;n dung mới mẻ về gia tộc Morgan v&agrave; c&aacute;c cộng sự cấp cao t&agrave;i giỏi, những người g&oacute;p phần đưa ng&acirc;n h&agrave;ng trở th&agrave;nh tập đo&agrave;n quyền lực xuy&ecirc;n quốc gia... Những tai tiếng, bi kịch, &acirc;m mưu được h&eacute; lộ đủ chất liệu để l&agrave;m nguy&ecirc;n một bộ phim truyền h&igrave;nh&rdquo;.&nbsp;</em><strong><em>&mdash; USA Today</em></strong></p>\r\n\r\n<p><strong>Về t&aacute;c giả:</strong></p>\r\n\r\n<p><strong>Ron Chernow&nbsp;</strong>l&agrave; một nh&agrave; văn, nh&agrave; b&aacute;o, nh&agrave; sử học v&agrave; người viết tiểu sử người Mỹ. &Ocirc;ng l&agrave; t&aacute;c giả của h&agrave;ng loạt tiểu sử b&aacute;n chạy nhất về c&aacute;c nh&acirc;n vật lịch sử trong thế giới kinh doanh, t&agrave;i ch&iacute;nh v&agrave; ch&iacute;nh trị Mỹ.</p>\r\n\r\n<p>&Ocirc;ng gi&agrave;nh giải thưởng Pulitzer Prize for Biography v&agrave; giải American History Book Prizen ăm 2011 cho cuốn Washington: A Life. &Ocirc;ng cũng l&agrave; người nhận giải National Book Award for Nonfiction cho cuốn The House of Morgan. Tiểu sử của &ocirc;ng về Alexander Hamilton (2004) v&agrave; John D. Rockefeller (1998) đều được đề cử cho giải National Book Critics Circle Awards. L&agrave; một nh&agrave; b&aacute;o tự do, &ocirc;ng đ&atilde; viết hơn 60 b&agrave;i b&aacute;o trong c&aacute;c ấn phẩm quốc gia.</p>', '1654607624.jpg', NULL, '2022-06-07 13:13:44', '2022-06-07 13:13:44'),
(26, 'Nhật Ký Đặng Thùy Trâm', '60800.00', 50, 4, 'Đặng Thuỳ Trâm', 'NXB Hội Nhà Văn', '<p><strong>Nhật K&yacute; Đặng Th&ugrave;y Tr&acirc;m (T&aacute;i Bản 2018)</strong></p>\r\n\r\n<p>Một cuốn nhật k&iacute; nhặt được b&ecirc;n x&aacute;c của một nữ Việt Cộng đ&atilde; su&yacute;t bị người l&iacute;nh Mỹ n&eacute;m v&agrave;o lửa, nhưng người phi&ecirc;n dịch đ&atilde; khuy&ecirc;n anh ta n&ecirc;n giữ lại v&igrave; &quot;trong đ&oacute; c&oacute; lửa&quot;.&nbsp;Nhật k&iacute; Đặng Th&ugrave;y Tr&acirc;m&nbsp;l&agrave; những ghi ch&eacute;p h&agrave;ng ng&agrave;y của một người nữ b&aacute;c sĩ về cuộc sống của chị nơi chiến tuyến. Cuốn nhật k&iacute; l&agrave; thế giới ri&ecirc;ng của người tr&iacute; thức nhạy cảm m&agrave; kh&ocirc;ng yếu đuối, tha thiết với cuộc sống m&agrave; kh&ocirc;ng hề sợ h&atilde;i trước những gian nan. Ở đ&oacute; ta vẫn gặp những băn khoăn trăn trở trước&nbsp;t&igrave;nh y&ecirc;u, trước cuộc sống phức tạp h&agrave;ng ng&agrave;y, những nỗi buồn, nỗi nhớ nhung, sự c&ocirc; đơn của một người con g&aacute;i, nhưng đồng thời ch&uacute;ng ta cũng thấy được một &yacute; ch&iacute; m&atilde;nh liệt, những lời n&oacute;i tự động vi&ecirc;n cảnh tỉnh, một l&ograve;ng can đảm phi thường - những điều đ&atilde; l&agrave;m n&ecirc;n một thế hệ anh h&ugrave;ng.</p>', '1654607932.jpg', NULL, '2022-06-07 13:18:52', '2022-06-07 13:18:52'),
(27, 'Napoleon Đại Đế', '399200.00', 50, 4, 'Andrew Roberts', 'NXB Thế Giới', '<p><br />\r\nNapoleon l&agrave; một nh&acirc;n vật đặc biệt vĩ đại v&agrave; hấp dẫn trong lịch sử Ph&aacute;p cũng như lịch sử thế giới. Cuộc đời, sự nghiệp, quan điểm, t&agrave;i năng của &ocirc;ng đ&atilde; l&agrave; chủ đề của h&agrave;ng ngh&igrave;n cuốn s&aacute;ch trong suốt hai thế kỉ qua, v&agrave; c&oacute; lẽ sẽ c&ograve;n được nghi&ecirc;n cứu tiếp trong nhiều thế kỉ sau nữa.</p>\r\n\r\n<p>Trong bối cảnh những cuốn s&aacute;ch dịch ra tiếng Việt về &ocirc;ng cũng như về thời hậu C&aacute;ch mạng Ph&aacute;p ở Việt Nam vẫn chỉ đếm tr&ecirc;n đầu ng&oacute;n tay, &iacute;t cả về số lượng lẫn tầm v&oacute;c, th&igrave; cuốn s&aacute;ch Napoleon Đại đế c&oacute; thể xem l&agrave; một hiện tượng bởi tầm v&oacute;c, quy m&ocirc;, t&iacute;nh chất gi&agrave;u sử liệu đ&atilde; l&agrave;m n&ecirc;n gi&aacute; trị đặc biệt của t&aacute;c phẩm.</p>\r\n\r\n<p><strong>+VỀ T&Aacute;C GIẢ:</strong></p>\r\n\r\n<p>T&aacute;c giả&nbsp; Andrew Roberts, trong cuốn tiểu sử rất đồ sộ n&agrave;y đ&atilde; đi s&acirc;u m&ocirc; tả một c&aacute;ch chi tiết c&aacute;c ho&agrave;n cảnh của thời C&aacute;ch mạng Ph&aacute;p đ&atilde; đưa Napoleon l&ecirc;n đỉnh quyền lực, c&aacute;ch m&agrave; &ocirc;ng đ&atilde; gi&agrave;nh lấy n&oacute; bằng đại b&aacute;c v&agrave; lưỡi l&ecirc;, v&agrave; củng cố quyền lực bằng chiến tranh, đầu ti&ecirc;n với những người Jacobin, sau đ&oacute; l&agrave; dẹp loạn Vand&eacute;e, v&agrave; cuối c&ugrave;ng đem chiến tranh ra ngo&agrave;i bi&ecirc;n giới nước Ph&aacute;p để trở th&agrave;nh ch&uacute;a tể của Ch&acirc;u &Acirc;u lục địa trong một cuộc chiến m&ugrave; qu&aacute;ng chống lại nước Anh nhằm thiết lập một trật tự thế giới do Paris điều khiển.</p>\r\n\r\n<p>Nội dung cuộc tọa đ&agrave;m, th&ocirc;ng qua c&aacute;c tư liệu qu&yacute; được n&ecirc;u ra trong cuốn s&aacute;ch, sẽ thảo luận về nh&acirc;n vật vĩ đại v&agrave; hấp dẫn n&agrave;y: Về t&iacute;nh c&aacute;ch, về c&aacute;ch tư duy v&agrave; h&agrave;nh xử của một nh&agrave; l&atilde;nh đạo, một thi&ecirc;n t&agrave;i qu&acirc;n sự, về cuộc chiến đấu của &ocirc;ng v&igrave; l&yacute; tưởng của ch&iacute;nh &ocirc;ng, v&agrave; về những c&aacute;ch nh&igrave;n nhận, đ&aacute;nh gi&aacute; con người n&agrave;y.</p>', '1654608467.jpg', NULL, '2022-06-07 13:27:48', '2022-06-07 13:27:48'),
(28, 'Tư Duy Và Chia Sẻ', '113400.00', 50, 4, 'Tôn Nữ Thị Ninh', 'NXB Trẻ', '<p>Cuốn s&aacute;ch l&agrave; những suy nghĩ sắc sảo của một một nh&agrave; ngoại giao, nh&agrave; hoạt động văn h&oacute;a gi&aacute;o dục, từng l&agrave; đại biểu quốc hội, về những vấn đề thời cuộc. Nhưng tr&ecirc;n hết, b&agrave; mong muốn đối thoại, chia sẻ với thế hệ trẻ, những người mang trọng tr&aacute;ch sẽ l&agrave;m chủ vận mệnh đất nước, trước ti&ecirc;n cần l&agrave;m chủ đời m&igrave;nh.</p>\r\n\r\n<p>Trong s&aacute;ch c&oacute; k&egrave;m DVD ghi lại những cuộc tr&ograve; chuyện, ph&aacute;t biểu, phỏng vấn của b&agrave; trong nước v&agrave; quốc tế, l&agrave; một tặng phẩm gửi bạn đọc.</p>', '1654608585.jpg', NULL, '2022-06-07 13:29:46', '2022-06-07 13:29:46'),
(29, 'Thiên Tài Bên Trái, Kẻ Điên Bên Phải', '179000.00', 50, 5, 'Cao Minh', 'NXB Thế Giới', '<p><strong>Thi&ecirc;n T&agrave;i B&ecirc;n Tr&aacute;i, Kẻ Đi&ecirc;n B&ecirc;n Phải</strong></p>\r\n\r\n<p><strong>NẾU MỘT NG&Agrave;Y ANH THẤY T&Ocirc;I ĐI&Ecirc;N, THỰC RA CH&Iacute;NH L&Agrave; ANH ĐI&Ecirc;N ĐẤY!</strong></p>\r\n\r\n<p>Hỡi những con người đang oằn m&igrave;nh trong cuộc sống, bạn biết g&igrave; về thế giới của m&igrave;nh? L&agrave; v&ocirc; v&agrave;n thứ l&yacute; thuyết được c&aacute;c bậc vĩ nh&acirc;n kiểm chứng, l&agrave; luật lệ, l&agrave; cả ngh&igrave;n thứ sự thật bọc trong c&aacute;i lốt hiển nhi&ecirc;n, hay những triết l&yacute; cứng nhắc của cuộc đời?</p>\r\n\r\n<p>Lại đ&acirc;y, vượt qua thứ nhận thức tẻ nhạt bị đ&oacute;ng k&iacute;n bằng con mắt trần gian, khai mở to&agrave;n bộ suy nghĩ, để d&ograve;ng m&aacute;u trong bạn sục s&ocirc;i trước những điều kỳ vĩ, ph&aacute; vỡ mọi quy tắc. Thế giới sẽ gọi bạn l&agrave; kẻ đi&ecirc;n, nhưng vậy th&igrave; c&oacute; sao? Ranh giới duy nhất giữa kẻ đi&ecirc;n v&agrave; thi&ecirc;n t&agrave;i chẳng qua l&agrave; một sợi chỉ mỏng manh: Thi&ecirc;n t&agrave;i chứng minh được thế giới của m&igrave;nh, c&ograve;n kẻ đi&ecirc;n chưa kịp l&agrave;m điều đ&oacute;. Chọn trở th&agrave;nh một kẻ đi&ecirc;n để vẫy v&ugrave;ng giữa nh&acirc;n gian loạn thế hay kh&oacute;a hết ch&uacute;ng lại, sống m&atilde;i một cuộc đời b&igrave;nh thường khiến bạn cảm thấy hạnh ph&uacute;c hơn?</p>\r\n\r\n<p><em>Thi&ecirc;n t&agrave;i b&ecirc;n tr&aacute;i, kẻ đi&ecirc;n b&ecirc;n phải&nbsp;</em>l&agrave; cuốn s&aacute;ch d&agrave;nh cho những người đi&ecirc;n rồ, những kẻ g&acirc;y rối, những người chống đối, những mảnh gh&eacute;p h&igrave;nh tr&ograve;n trong những &ocirc; vu&ocirc;ng kh&ocirc;ng vừa vặn&hellip; những người nh&igrave;n mọi thứ kh&aacute;c biệt, kh&ocirc;ng quan t&acirc;m đến quy tắc. Bạn c&oacute; thể đồng &yacute;, c&oacute; thể phản đối, c&oacute; thể vinh danh hay lăng mạ họ, nhưng điều duy nhất bạn kh&ocirc;ng thể l&agrave;m l&agrave; phủ nhận sự tồn tại của họ. Đ&oacute; l&agrave; những người lu&ocirc;n tạo ra sự thay đổi trong khi hầu hết con người chỉ sống rập khu&ocirc;n như một c&aacute;i m&aacute;y. Đa số đều nghĩ họ thật đi&ecirc;n rồ nhưng nếu nh&igrave;n ở g&oacute;c kh&aacute;c, ta lại thấy họ thi&ecirc;n t&agrave;i. Bởi chỉ những người đủ đi&ecirc;n nghĩ rằng họ c&oacute; thể thay đổi thế giới mới l&agrave; những người l&agrave;m được điều đ&oacute;.&nbsp;</p>\r\n\r\n<p>Ch&agrave;o mừng đến với thế giới của những kẻ đi&ecirc;n.</p>', '1654650295.jpg', NULL, '2022-06-08 01:04:56', '2022-06-08 01:04:56');
INSERT INTO `books` (`id`, `name`, `giaban`, `soluong`, `category_id`, `tacgia`, `nxb`, `gioithieu`, `hinhanh`, `taiban`, `created_at`, `updated_at`) VALUES
(30, 'Tâm Lý Học - Phác Họa Chân Dung Kẻ Phạm Tội', '145000.00', 50, 5, 'Diệp Hồng Vũ', 'NXB Thanh Niên', '<p><br />\r\n<strong>T&acirc;m L&yacute; Học - Ph&aacute;c Họa Ch&acirc;n Dung Kẻ Phạm Tội</strong></p>\r\n\r\n<p><strong>T&Acirc;M L&Yacute; HỌC TỘI PHẠM - PH&Aacute;C HỌA CH&Acirc;N DUNG KẺ PHẠM TỘI</strong></p>\r\n\r\n<p>Tội phạm, nhất l&agrave; những vụ &aacute;n mạng, lu&ocirc;n l&agrave; một chủ đề thu h&uacute;t sự quan t&acirc;m của c&ocirc;ng ch&uacute;ng, khơi gợi sự hiếu kỳ của bất cứ ai. Một khi đ&atilde; bắt đầu theo d&otilde;i vụ &aacute;n, hẳn bạn kh&ocirc;ng thể kh&ocirc;ng quan t&acirc;m tới kết cục, đặc biệt l&agrave; c&aacute;ch thức v&agrave; động cơ của kẻ s&aacute;t nh&acirc;n, từ những vụ &aacute;n phạm vi hẹp cho đến những vụ &aacute;n l&agrave;m r&uacute;ng động cả thế giới.</p>\r\n\r\n<p>Lấy 36 vụ &aacute;n C&Oacute; THẬT kinh điển nhất trong hồ sơ tội phạm của FBI, &ldquo;T&acirc;m l&yacute; học tội phạm - ph&aacute;c họa ch&acirc;n dung kẻ phạm tội&rdquo; mang đến c&aacute;i nh&igrave;n to&agrave;n cảnh của c&aacute;c chuy&ecirc;n gia về ch&acirc;n dung t&acirc;m l&yacute; tội phạm. Trả lời cho c&acirc;u hỏi: L&agrave;m thế n&agrave;o ph&acirc;n t&iacute;ch được t&acirc;m l&yacute; v&agrave; h&agrave;nh vi tội phạm, từ đ&oacute; kh&ocirc;i phục sự thật th&ocirc;ng qua c&aacute;c manh mối, từ hiện trường vụ &aacute;n, thời gian, dấu t&iacute;ch,&hellip; để t&igrave;m ra kẻ s&aacute;t nh&acirc;n thực sự.&nbsp;</p>\r\n\r\n<p>Đằng sau m&aacute;u v&agrave; nước mắt l&agrave; c&aacute;c c&acirc;u chuyện rợn t&oacute;c g&aacute;y về tội &aacute;c, g&oacute;c khuất x&atilde; hội v&agrave; những m&agrave;n đấu tr&iacute; đầy gay cấn giữa điều tra vi&ecirc;n v&agrave; kẻ phạm tội. Trong số đ&oacute; c&oacute; những con quỷ ăn thịt người; những c&ocirc; g&aacute;i xinh đẹp nhưng xảo quyệt; v&agrave; cả c&aacute;ch trả th&ugrave; đầy man rợ của c&aacute;c nh&agrave; khoa học,&hellip; Một số đ&atilde; sa v&agrave;o lưới ph&aacute;p luật ngay khi ch&uacute;ng vừa ra tay, nhưng cũng c&oacute; những kẻ cứ vậy ngủ y&ecirc;n hơn hai mươi năm.&nbsp;</p>\r\n\r\n<p>Bằng giọng văn sắc b&eacute;n, &ldquo;T&acirc;m l&yacute; học tội phạm - ph&aacute;c họa ch&acirc;n dung kẻ phạm tội&rdquo; hứa hẹn dẫn dắt người đọc đi qua c&aacute;c cung bậc cảm x&uacute;c từ t&ograve; m&ograve;, ngạc nhi&ecirc;n đến sợ h&atilde;i, hoang mang tận c&ugrave;ng. Ch&uacute;ng ta sẽ lần t&igrave;m về qu&aacute; khứ để từng bước gỡ những n&uacute;t thắt chưa được giải, khiến ta &quot;ngạt thở&quot; đọc tới tận trang cuối c&ugrave;ng.&nbsp;</p>\r\n\r\n<p>Hy vọng cuốn s&aacute;ch sẽ gi&uacute;p bạn c&oacute; c&aacute;i nh&igrave;n s&acirc;u sắc, r&otilde; r&agrave;ng hơn về bộ m&ocirc;n t&acirc;m l&yacute; học tội phạm v&agrave; c&oacute; thể r&egrave;n luyện th&ecirc;m sự tư duy, nhạy b&eacute;n.</p>', '1654650378.jpg', NULL, '2022-06-08 01:06:19', '2022-06-08 01:06:19'),
(31, 'How Psychology Works - Hiểu Hết Về Tâm Lý Học', '300000.00', 50, 5, 'Jo Hemmings', 'NXB Thế Giới', '<p><strong>MỘT TRONG NHỮNG CUỐN S&Aacute;CH MỞ KH&Oacute;A HỮU &Iacute;CH NHẤT VỀ TƯ DUY, K&Yacute; ỨC V&Agrave; CẢM X&Uacute;C CỦA CON NGƯỜI!</strong></p>\r\n\r\n<p>&Aacute;m sợ l&agrave; g&igrave;, &aacute;m sợ c&oacute; thực sự đ&aacute;ng sợ kh&ocirc;ng? Rối loạn t&acirc;m l&yacute; l&agrave; g&igrave;, l&agrave;m thế n&agrave;o để tho&aacute;t khỏi t&igrave;nh trạng suy nhược v&agrave; x&aacute;o trộn đ&oacute;? Trầm cảm l&agrave; g&igrave;, v&igrave; sao con người hiện đại thường xuy&ecirc;n gặp v&agrave; chống chọi với t&igrave;nh trạng u uất, mệt mỏi v&agrave; tuyệt vọng n&agrave;y?</p>\r\n\r\n<p>T&igrave;m hiểu về c&aacute;c vấn đề t&acirc;m tr&iacute; của con người lu&ocirc;n đầy sức hấp dẫn v&agrave; l&ocirc;i cuốn, v&igrave; vậy m&agrave; t&acirc;m l&yacute; học ra đời, h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển rất nhiều c&aacute;c học thuyết v&agrave; trường ph&aacute;i. Cuốn s&aacute;ch n&agrave;y dẫn dắt bạn đọc qua h&agrave;nh tr&igrave;nh t&igrave;m hiểu c&aacute;c học thuyết v&agrave; trường ph&aacute;i đ&oacute;, về c&aacute;ch c&aacute;c nh&agrave; t&acirc;m l&yacute; diễn giải h&agrave;nh xử v&agrave; t&acirc;m tr&iacute; con người. Tại sao ch&uacute;ng ta c&oacute; những h&agrave;nh vi, suy nghĩ v&agrave; cảm x&uacute;c như vậy, ch&uacute;ng diễn ra v&agrave; kết th&uacute;c như thế n&agrave;o, ch&uacute;ng ảnh hưởng l&acirc;u d&agrave;i, gi&aacute;n đoạn hay ngắn ngủỉ đến đời sống của ch&uacute;ng ta ra sao, l&agrave;m thế n&agrave;o để ch&uacute;ng ta tho&aacute;t khỏi những t&aacute;c động ti&ecirc;u cực của ch&uacute;ng?</p>\r\n\r\n<p>Cuốn s&aacute;ch c&oacute; cấu tr&uacute;c khoa học, tr&igrave;nh b&agrave;y dễ hiểu, s&uacute;c t&iacute;ch, thiết kế v&agrave; minh họa đẹp mắt n&agrave;y sẽ mang đến cho bạn những hiểu biết về c&aacute;c học thuyết t&acirc;m l&yacute; v&agrave; c&aacute;c phương ph&aacute;p trị liệu, từ c&aacute;c vấn đề c&aacute; nh&acirc;n đến những ứng dụng thực tế.</p>', '1654650439.jpg', NULL, '2022-06-08 01:07:19', '2022-06-08 01:07:19'),
(32, 'Tử Huyệt Cảm Xúc', '148000.00', 50, 1, 'Roy Garn', 'NXB Hà Nội', '<p>Đ&acirc;y l&agrave; một cuốn s&aacute;ch viết về Tử Huyệt Cảm X&uacute;c - những &ldquo;điểm yếu chết người&rdquo; trong t&iacute;nh c&aacute;ch của mỗi ch&uacute;ng ta. Tử Huyệt Cảm X&uacute;c c&oacute; thể gi&uacute;p ch&uacute;ng ta đạt được hạnh ph&uacute;c v&agrave; th&agrave;nh c&ocirc;ng tột đỉnh, nhưng cũng c&oacute; thể khiến ch&uacute;ng rơi v&agrave;o bi kịch như Achilles!</p>', '1654650480.jpg', NULL, '2022-06-08 01:08:00', '2022-06-08 01:08:00'),
(33, 'Bộ Sách Tâm Lý Học Tội Phạm', '288000.00', 50, 5, 'Stanton E Samenow', 'NXB Đại học Kinh Tế Quốc Dân', '<p><br />\r\n<strong>GỒM 2 CUỐN:</strong></p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; T&acirc;m l&yacute; học tội phạm tập 1</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; T&acirc;m l&yacute; học tội phạm tập 2</p>\r\n\r\n<p><strong><em>&quot;T&acirc;m l&yacute; học tội phạm&quot;</em></strong>&nbsp;l&agrave; bộ s&aacute;ch gồm 2 tập đề cập đến quyền lựa chọn, &yacute; ch&iacute; tự do, c&aacute;i thiện v&agrave; c&aacute;i &aacute;c, phản ứng trước c&aacute;m dỗ v&agrave; sự thể hiện l&ograve;ng dũng cảm hay h&egrave;n nh&aacute;t khi đối mặt với nghịch cảnh của con người. Những cuốn s&aacute;ch thi&ecirc;ng li&ecirc;ng của c&aacute;c t&ocirc;n gi&aacute;o đều khuy&ecirc;n lo&agrave;i người kh&ocirc;ng n&ecirc;n lừa dối, giận dữ v&agrave; ki&ecirc;u ngạo. Ch&uacute;ng ta nghĩ bản th&acirc;n thế n&agrave;o th&igrave; sẽ l&agrave; như thế. Ch&uacute;ng ta kh&ocirc;ng thể gi&uacute;p một người từ bỏ tội &aacute;c v&agrave; sống c&oacute; tr&aacute;ch nhiệm nếu kh&ocirc;ng thể khiến anh ta thay đổi nh&acirc;n t&iacute;nh, đ&oacute; ch&iacute;nh l&agrave; &quot;suy nghĩ&quot;.</p>\r\n\r\n<p>Ấn bản đầu ti&ecirc;n của cuốn s&aacute;ch&nbsp;<strong><em>&ldquo;T&acirc;m l&yacute; học tội phạm&rdquo;</em></strong>&nbsp;được xuất bản năm 1984, v&agrave; ấn bản thứ hai xuất bản năm 2004. Sau một thập kỷ, v&agrave; đ&atilde; đến l&uacute;c bổ sung th&ecirc;m c&aacute;c th&ocirc;ng tin cho cuốn s&aacute;ch n&agrave;y. Trong ấn bản n&agrave;y, bạn sẽ hiểu chi tiết về c&aacute;c qu&aacute; tr&igrave;nh tư duy v&agrave; chiến thuật phổ biến đối với những kẻ phạm tội, bất kể l&yacute; lịch hay tội &aacute;c của ch&uacute;ng.</p>\r\n\r\n<p>Giờ đ&acirc;y, với c&aacute;i nh&igrave;n s&acirc;u sắc hơn t&aacute;c giả Stanton E.Samenow đ&atilde; cung cấp cho độc giả một ấn bản cập nhật ho&agrave;n to&agrave;n về t&aacute;c phẩm kinh điển của m&igrave;nh, bao gồm những sự nhận thức mới mẻ về tội &aacute;c đang được ch&uacute; &yacute; ng&agrave;y nay, từ sự r&igrave;nh rập v&agrave; bạo lực gia đ&igrave;nh đến tội phạm cổ cồn v&agrave; ch&iacute;nh trị khủng bố. &Ocirc;ng đ&atilde; từng c&oacute; ba thập kỷ l&agrave;m việc với tội phạm khẳng định lại lập luận của m&igrave;nh rằng c&aacute;c yếu tố như ngh&egrave;o đ&oacute;i, ly h&ocirc;n v&agrave; bạo lực tr&ecirc;n phương tiện truyền th&ocirc;ng kh&ocirc;ng g&acirc;y ra tội phạm. Đ&uacute;ng hơn, như c&aacute;c t&agrave;i liệu của Samenow ở đ&acirc;y, tất cả tội phạm đều c&oacute; chung một suy nghĩ đặc biệt - thường thấy r&otilde; trong thời thơ ấu - kh&aacute;c hẳn với suy nghĩ của một c&ocirc;ng d&acirc;n c&oacute; tr&aacute;ch nhiệm.</p>\r\n\r\n<p>Trong khi c&aacute;c loại tội phạm mới ng&agrave;y c&agrave;ng phổ biến hơn, hoặc &iacute;t nhất l&agrave; dễ nh&igrave;n thấy hơn với c&ocirc;ng ch&uacute;ng - từ lạm dụng vợ chồng đến c&aacute;c vụ xả s&uacute;ng ở trường học - c&oacute; rất &iacute;t thay đổi về c&aacute;ch tiếp cận của ch&uacute;ng ta đối với tội phạm. C&aacute;c chương tr&igrave;nh phục hồi dựa tr&ecirc;n giả định rằng x&atilde; hội đổ lỗi cho tội phạm nhiều hơn l&agrave; tội phạm, một giả định m&agrave; mối li&ecirc;n hệ nh&acirc;n quả vẫn chưa được thiết lập, đ&atilde; được chứng minh l&agrave; kh&ocirc;ng đầy đủ. Tội phạm tiếp tục x&acirc;m chiếm mọi kh&iacute;a cạnh của cuộc sống ch&uacute;ng ta, c&aacute;c t&ograve;a &aacute;n h&igrave;nh sự v&agrave; nh&agrave; t&ugrave; lu&ocirc;n qu&aacute; tải, v&agrave; tỷ lệ t&aacute;i phạm tiếp tục leo thang.</p>\r\n\r\n<p>Tiến sĩ Samenow, một nh&agrave; t&acirc;m l&yacute; học l&acirc;m s&agrave;ng đ&atilde; b&aacute;c bỏ một c&aacute;ch hợp ph&aacute;p những lời giải th&iacute;ch về h&agrave;nh vi tội phạm đổ lỗi cho ho&agrave;n cảnh, m&ocirc;i trường (x&atilde; hội, gia đ&igrave;nh, truyền h&igrave;nh bạo lực,...). &Ocirc;ng thường xuy&ecirc;n đưa ra những lời kh&aacute;i qu&aacute;t s&acirc;u rộng v&agrave; kh&ocirc;ng cung cấp g&igrave; kh&aacute;c ngo&agrave;i những trường hợp c&oacute; thật ủng hộ quan điểm của &ocirc;ng rằng tất cả tội phạm đều vi phạm ph&aacute;p luật một c&aacute;ch c&oacute; &yacute; thức v&agrave; cố &yacute;.</p>\r\n\r\n<p>Hiểu biết th&ocirc;ng thường về nguy&ecirc;n nh&acirc;n phạm tội hiện vẫn l&agrave; kim chỉ nam cho nhiều người vạch ra c&aacute;c ch&iacute;nh s&aacute;ch ph&ograve;ng chống tội phạm. C&aacute;c nh&agrave; hoạch định ch&iacute;nh s&aacute;ch l&atilde;ng ph&iacute; h&agrave;ng tỷ đ&ocirc; la khi họ ng&acirc;y thơ t&igrave;m c&aacute;ch chống lại h&agrave;nh vi tội phạm bằng c&aacute;ch loại bỏ c&aacute;i gọi l&agrave;&nbsp;<strong><em>&ldquo;nguy&ecirc;n nh&acirc;n gốc rễ&rdquo;</em></strong>&nbsp;về m&ocirc;i trường xung quanh.</p>\r\n\r\n<p>Điều quan trọng l&agrave; ch&uacute;ng ta phải biết kẻ phạm tội l&agrave; ai v&agrave; l&agrave;m thế n&agrave;o v&agrave; tại sao hắn lại h&agrave;nh động kh&aacute;c với những c&ocirc;ng d&acirc;n c&oacute; tr&aacute;ch nhiệm. Từ sự hiểu biết đ&oacute; c&oacute; thể đưa ra những giải ph&aacute;p hợp l&yacute;, nh&acirc;n &aacute;i v&agrave; hiệu quả.</p>\r\n\r\n<p>-Th&ocirc;ng tin t&aacute;c giả:<br />\r\nTiến sĩ Stanton E. Samenow l&agrave; một nh&agrave; t&acirc;m l&yacute; học, đ&atilde; d&agrave;nh 40 năm với tư c&aacute;ch l&agrave; một nh&agrave; nghi&ecirc;n cứu, b&aacute;c sĩ l&acirc;m s&agrave;ng, nh&agrave; tư vấn v&agrave; nh&acirc;n chứng chuy&ecirc;n m&ocirc;n chuy&ecirc;n về h&agrave;nh vi tội phạm. &Ocirc;ng cũng l&agrave; người đ&aacute;nh gi&aacute; độc lập về c&aacute;c tranh chấp quyền nu&ocirc;i con trong hơn 20 năm qua v&agrave; được bổ nhiệm v&agrave;o ba đội đặc nhiệm của tổng thống về thực thi ph&aacute;p luật, quyền của nạn nh&acirc;n v&agrave; một nước Mỹ kh&ocirc;ng c&oacute; ma t&uacute;y.</p>', '1654650534.jpg', NULL, '2022-06-08 01:08:54', '2022-06-08 01:08:54'),
(34, 'Giá Như Có Ai Đó Dạy Tôi Trưởng Thành - Trưởng Thành Theo Hệ 4.0', '96000.00', 50, 5, 'Bloom', 'NXB Văn Học', '<p><br />\r\n<strong>Gi&aacute; Như C&oacute; Ai Đ&oacute; Dạy T&ocirc;i Trưởng Th&agrave;nh - Trưởng Th&aacute;nh Theo Hệ 4.0</strong></p>\r\n\r\n<p>Cuốn s&aacute;ch n&agrave;y d&agrave;nh cho bạn - những đứa trẻ đang tập lớn mang trong m&igrave;nh tr&aacute;i tim tổn thương v&agrave; rạn vỡ, những người đang phải đối mặt với những nỗi đau hay những cảm x&uacute;c ti&ecirc;u cực b&ecirc;n trong m&igrave;nh. Cuốn s&aacute;ch xinh xắn của t&aacute;c giả Bloom sẽ như một lời nhắc nhở, một c&aacute;i nắm tay gi&uacute;p bạn hiểu rằng m&igrave;nh kh&ocirc;ng hề đơn độc, rằng bạn ho&agrave;n to&agrave;n c&oacute; đủ sức mạnh để đối diện với những kh&oacute; khăn, để y&ecirc;u thương, để tự chữa l&agrave;nh cho bản th&acirc;n v&agrave; dần dần lớn l&ecirc;n.</p>\r\n\r\n<p>Bởi trong qu&aacute; tr&igrave;nh trưởng th&agrave;nh, đ&ocirc;i khi ch&uacute;ng ta sẽ tự hỏi, rằng liệu m&igrave;nh đang &ldquo;sống&rdquo; hay &ldquo;tồn tại&rdquo;, m&igrave;nh l&agrave; ai trong cuộc đời, m&igrave;nh thực sự cần g&igrave; v&agrave; mong muốn g&igrave;. Nhưng c&oacute; lẽ, kh&ocirc;ng phải ai cũng dễ d&agrave;ng t&igrave;m được cho bản th&acirc;n c&acirc;u trả lời, kh&ocirc;ng phải ai ngay từ ban đầu cũng đ&atilde; đủ bản lĩnh để th&iacute;ch nghi với những chặng đường ng&agrave;y c&agrave;ng th&ecirc;m kh&oacute;. M&agrave; ch&uacute;ng ta &ndash; những c&ocirc;ng tr&igrave;nh dang dở của Vũ trụ, bằng c&aacute;ch lắng nghe tr&aacute;i tim v&agrave; tin tưởng v&agrave;o bản th&acirc;n m&igrave;nh, cứ thế can đảm bước đi, lần lượt trải nghiệm v&agrave; vượt qua những thử th&aacute;ch to nhỏ, cho ph&eacute;p m&igrave;nh được sai, được học, được sửa, được l&agrave;m lại. Để rồi đến một h&ocirc;m, tất cả những g&igrave; ch&uacute;ng ta t&igrave;m kiếm, một c&aacute;ch tự nhi&ecirc;n đều v&agrave;o đ&uacute;ng chỗ của ch&uacute;ng.</p>\r\n\r\n<p>Với c&aacute;ch viết v&ocirc; c&ugrave;ng dễ hiểu, giọng văn dịu d&agrave;ng khẽ chạm v&agrave;o những cảm x&uacute;c s&acirc;u thẳm trong t&acirc;m hồn, t&aacute;c giả Bloom mang đến cho bạn những th&ocirc;ng điệp s&acirc;u sắc th&ocirc;ng qua việc đặt ra những c&acirc;u hỏi m&agrave; c&oacute; lẽ kh&ocirc;ng phải của ri&ecirc;ng ai khi đi tr&ecirc;n h&agrave;nh tr&igrave;nh trưởng th&agrave;nh, để mỗi người từ đ&oacute; c&oacute; cơ hội nh&igrave;n nhận, phản &aacute;nh, v&agrave; kh&aacute;m ph&aacute; bản th&acirc;n nhiều hơn. Đồng thời, đi c&ugrave;ng với đ&oacute; cũng l&agrave; những lời gợi mở gi&uacute;p bạn c&oacute; thể tự t&igrave;m ra đ&aacute;p &aacute;n ph&ugrave; hợp cho bản th&acirc;n m&igrave;nh: Trưởng th&agrave;nh c&oacute; nghĩa l&agrave; bạn cần&hellip; Trưởng th&agrave;nh l&agrave; khi bạn biết&hellip; &amp; Trưởng th&agrave;nh l&agrave; khi bạn tin&hellip;</p>\r\n\r\n<p>V&agrave; điều quan trọng nhất l&agrave; ghi nhớ rằng tất cả ch&uacute;ng ta đều xứng đ&aacute;ng được y&ecirc;u thương, xứng đ&aacute;ng với những điều tốt đẹp như Bloom đ&atilde; viết một c&aacute;ch đầy tin tưởng rằng &ldquo;Sẽ đến một ng&agrave;y bạn thực sự c&oacute; thể cảm nhận được bản th&acirc;n m&igrave;nh đặc biệt như thế n&agrave;o&rdquo;. V&agrave; kh&ocirc;ng sao cả nếu như hiện tại cuộc sống của bạn hỗn độn v&agrave; bất ổn. Bởi v&igrave; &ldquo;Những ng&agrave;y th&aacute;ng kh&oacute; khăn, nhiều rắc rối v&agrave; nhiều &acirc;u lo n&agrave;y sẽ kh&ocirc;ng k&eacute;o d&agrave;i m&atilde;i. H&atilde;y xem n&oacute; l&agrave; một m&ugrave;a trong năm, đến rồi lại đi, v&agrave; việc bạn cần l&agrave;m l&agrave; tiếp tục bước đi tr&ecirc;n h&agrave;nh tr&igrave;nh ấy. Hạnh ph&uacute;c chờ bạn ở m&ugrave;a tiếp theo. Chỉ l&agrave; mọi thứ đều cần thời gian&rdquo;</p>', '1654650590.jpg', NULL, '2022-06-08 01:09:51', '2022-06-08 01:09:51'),
(35, 'Tâm Lý Học Về Tiền', '189000.00', 48, 5, 'Morgan Housel', 'NXB Dân Trí', '<p><br />\r\nTiền bạc c&oacute; ở khắp mọi nơi, n&oacute; ảnh hưởng đến tất cả ch&uacute;ng ta, v&agrave; khiến phần lớn ch&uacute;ng ta bối rối. Mọi người nghĩ về n&oacute; theo những c&aacute;ch hơi kh&aacute;c nhau một ch&uacute;t. N&oacute; mang lại những b&agrave;i học c&oacute; thể được &aacute;p dụng tới rất nhiều lĩnh vực trong cuộc sống, như rủi ro, sự tự tin, v&agrave; hạnh ph&uacute;c. Rất &iacute;t chủ đề cung cấp một lăng k&iacute;nh ph&oacute;ng to đầy quyền lực gi&uacute;p giải th&iacute;ch v&igrave; sao mọi người lại h&agrave;nh xử theo c&aacute;ch họ l&agrave;m hơn l&agrave; về tiền bạc. Đ&oacute; mới l&agrave; một trong những chương tr&igrave;nh ho&agrave;nh tr&aacute;ng nhất tr&ecirc;n thế giới.</p>\r\n\r\n<p>Ch&uacute;ng ta hiếm khi l&acirc;m v&agrave;o ho&agrave;n cảnh nợ ngập đầu ư? Biết tiết kiệm để d&agrave;nh cho l&uacute;c khốn kh&oacute; hơn ư? Chuẩn bị sẵn s&agrave;ng cho việc nghỉ hưu? C&oacute; những c&aacute;i nh&igrave;n thiết thực về mối quan hệ giữa tiền v&agrave; hạnh ph&uacute;c của ch&uacute;ng ta hơn phải kh&ocirc;ng?</p>\r\n\r\n<p>Ch&uacute;ng ta đều l&agrave;m những điều đi&ecirc;n rồ với tiền bạc, bởi v&igrave; ch&uacute;ng ta đều c&ograve;n kh&aacute; mới mẻ với tr&ograve; chơi n&agrave;y v&agrave; điều c&oacute; vẻ đi&ecirc;n rồ với bạn lại c&oacute; khi hợp l&yacute; với t&ocirc;i. Nhưng kh&ocirc;ng ai l&agrave; đi&ecirc;n rồ cả &ndash; ch&uacute;ng ta đều đưa ra c&aacute;c quyết định dựa tr&ecirc;n những trải nghiệm độc đ&aacute;o ri&ecirc;ng c&oacute; mang vẻ hợp l&yacute; với m&igrave;nh ở bất cứ thời điểm n&agrave;o.</p>\r\n\r\n<p>Mục đ&iacute;ch của cuốn s&aacute;ch n&agrave;y l&agrave; sử dụng những c&acirc;u chuyện ngắn để thuyết phục bạn rằng những kỹ năng mềm c&ograve;n quan trọng hơn kh&iacute;a cạnh l&yacute; thuyết của đồng tiền. Th&ocirc;ng qua một tập hợp những thử nghiệm v&agrave; sai lầm của nhiều năm ch&uacute;ng ta đ&atilde; học được c&aacute;ch trở th&agrave;nh những n&ocirc;ng d&acirc;n giỏi giang hơn, những thợ sửa ống nước nhiều kỹ năng hơn, v&agrave; những nh&agrave; h&oacute;a học ti&ecirc;n tiến hơn. Nhưng liệu việc thử nghiệm v&agrave; sai lầm c&oacute; dạy ch&uacute;ng ta trở n&ecirc;n giỏi hơn trong c&aacute;ch quản l&yacute; t&agrave;i ch&iacute;nh c&aacute; nh&acirc;n của ch&iacute;nh m&igrave;nh kh&ocirc;ng?</p>\r\n\r\n<p>Nhiều tiền kh&ocirc;ng li&ecirc;n quan nhiều đến việc bạn th&ocirc;ng minh như thế n&agrave;o m&agrave; lại li&ecirc;n quan lớn đến c&aacute;ch bạn h&agrave;nh xử. V&agrave; c&aacute;ch h&agrave;nh xử th&igrave; rất kh&oacute; để uốn nắn, ngay cả đối với những người thực sự th&ocirc;ng minh.</p>\r\n\r\n<p>Một thi&ecirc;n t&agrave;i kh&ocirc;ng kiểm so&aacute;t được cảm x&uacute;c của anh ta c&oacute; thể dẫn tới một thảm họa t&agrave;i ch&iacute;nh. Điều ngược lại cũng đ&uacute;ng. Những người b&igrave;nh thường kh&ocirc;ng c&oacute; kiến thức về t&agrave;i ch&iacute;nh c&oacute; thể trở n&ecirc;n gi&agrave;u c&oacute; nếu họ nắm trong tay những kỹ năng h&agrave;nh xử kh&ocirc;ng li&ecirc;n quan đến những thước đo ch&iacute;nh thống về tr&iacute; th&ocirc;ng minh.</p>\r\n\r\n<p>Sự th&agrave;nh c&ocirc;ng trong t&agrave;i ch&iacute;nh kh&ocirc;ng phải l&agrave; một lĩnh vực khoa học kh&oacute; nhằn. N&oacute; l&agrave; một kỹ năng mềm, nơi m&agrave; c&aacute;ch bạn h&agrave;nh xử quan trọng hơn điều m&agrave; bạn biết. Trong &ldquo;<strong><em>T&acirc;m l&yacute; học về tiền&rdquo;</em></strong>, t&aacute;c giả từng đoạt giải thưởng Morgan Housel chia sẻ 19 c&acirc;u chuyện ngắn kh&aacute;m ph&aacute; những c&aacute;ch kỳ lạ m&agrave; mọi người nghĩ về tiền bạc v&agrave; dạy bạn c&aacute;ch hiểu r&otilde; hơn về một trong những chủ đề quan trọng nhất của cuộc sống.</p>', '1654650638.jpg', NULL, '2022-06-08 01:10:39', '2022-06-08 01:28:19');

